pragma solidity ^0.8.4;

import "./IERC20.sol";
import "./INFT.sol";
import "./SafeMath.sol";
import "./Context.sol";
import "./AccessControl.sol";
import "./IPancakeRouter02.sol";
import "./IUniswapV2Factory.sol";
import "./IPancakePair.sol";

   /**
     * @dev BlindBox is a standard Tool protocol
     * Including the minting of NFT, distribution and burning of NFT Chip
     */
contract OPABox is Context, AccessControl {

    using SafeMath for uint256;

    address private _OPAToken;
    address private _USDTAddress;
    address private _NFTAddress;
	address private _NFTChip;
    address private _nftReceiver;

    address public DEAD = address(0x000000000000000000000000000000000000dEaD);

    bool private inSwapAndLiquify;
    bool private inBurnChip;

    IPancakeRouter02 public _uniswapV2Router;

    uint256 private constant MAX_UINT256 = type(uint256).max;
    uint256 private _swapAndMintCondition;
    uint256 private _burnAndMintCondition;

    struct BboxConfig {
        uint256 bboxType;
        uint256 probability;
        uint256 nftChipAmount;
    }

    BboxConfig[] private bboxConfigurations;
    mapping(uint256 => uint256) bboxConfigurationIndex;

    uint256 private _probabilityBase = 10000;

    event BurnChipMintNFT(uint256 chipAmount);
    event OpenBboxForUsdt(uint256 chipAmount);

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    modifier lockBurnChip {
        inBurnChip = true;
        _;
        inBurnChip = false;
    }

    constructor (
        address RouterAddress, 
        address USDTAddress,
        address NFTChip,
        address OPAToken,
        address NFTAddress,
        address nftReceiver
    ) {
        _USDTAddress = USDTAddress;
        _NFTChip = NFTChip;
        _OPAToken = OPAToken;
        _NFTAddress = NFTAddress;
        _nftReceiver = nftReceiver;

        IPancakeRouter02 uniswapV2Router = IPancakeRouter02(RouterAddress);
        IERC20(USDTAddress).approve(address(uniswapV2Router), MAX_UINT256);
		_uniswapV2Router = uniswapV2Router;

        _swapAndMintCondition = 20 * 10 ** IERC20(USDTAddress).decimals();
        _burnAndMintCondition = 100 * 10 ** IERC20(NFTChip).decimals();
    }

    receive() external payable {}

    function burnChipMintNFT(uint256 tokenId, uint256 chipAmount) public lockBurnChip returns (bool) {
        require(chipAmount >= _burnAndMintCondition, "BlindBox: burnChip amount is insufficient");
        IERC20 CHIP = IERC20(_NFTChip);
        CHIP.burnFrom(msg.sender, chipAmount);
		INFT nft = INFT(_NFTAddress);
		nft.mint(_nftReceiver, tokenId);
		emit BurnChipMintNFT(chipAmount);
        return true;
    }

    function openBboxForUsdt(uint256 usdtAmount) public lockTheSwap returns (bool) {
        require(usdtAmount >= _swapAndMintCondition, "BlindBox: subscribe usdt amount is insufficient");
        IERC20 USDT = IERC20(_USDTAddress);
        require(USDT.transferFrom(msg.sender, address(this), usdtAmount), "BlindBox: transferFrom to contract failure");
        uint256 usdtBalance = USDT.balanceOf(address(this));
		address[] memory path = new address[](2);
		path[0] = _USDTAddress;
		path[1] = _OPAToken;
		_uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
			usdtBalance,
			0,
			path,
			DEAD,
			block.timestamp
		);
		uint256 nftChipAmount = 0;
		uint256 bboxType = getProbabilityBbox();
		if (bboxType != 0) {
			IERC20 CHIP = IERC20(_NFTChip);
			nftChipAmount = bboxConfigurations[bboxConfigurationIndex[bboxType]].nftChipAmount;
			CHIP.transfer(msg.sender, nftChipAmount);
		}
		emit OpenBboxForUsdt(nftChipAmount);
        return true;
    }

    function getRandomNumber() private view returns(uint256) {
        return uint256(keccak256(abi.encodePacked(block.timestamp, msg.sender, blockhash(block.number.sub(1))))).mod(_probabilityBase);
    }

    /**
     * @dev Obtain blindBox type based on different probabilities
     * @return uint256 Represents the type of blindBox
     * Returning zero without blindBox type
     */
    function getProbabilityBbox() private view returns (uint256) {
        uint256 randomNumber = getRandomNumber();
        uint256 cumulativeProbability = 0;
        
        for (uint i = 0; i < bboxConfigurations.length; i++) {
            cumulativeProbability += bboxConfigurations[i].probability;
            if (randomNumber < cumulativeProbability) {
                return bboxConfigurations[i].bboxType;
            }
        }
        return 0;
    }

    function configureBbox(uint256 bboxType, uint256 probability, uint256 nftChipAmount) external onlyOperator {
        uint256 bboxIndex = bboxConfigurationIndex[bboxType];
        bool bboxExists = bboxConfigurations[bboxIndex].bboxType == bboxType ? true : false;
        if(bboxExists) {
            uint256 cumulativeProbability = 0;
            for (uint i = 0; i < bboxConfigurations.length; i++) {
                if(i != bboxIndex) {
                    cumulativeProbability += bboxConfigurations[i].probability;
                }
            }
            require(cumulativeProbability.add(probability) <= _probabilityBase, "BlindBox: total probability greater than probabilityBase");
            bboxConfigurations[bboxIndex].probability = probability;
            bboxConfigurations[bboxIndex].nftChipAmount = nftChipAmount;
        } else {
            _addBbox(bboxType, probability, nftChipAmount);
        }
    }

    function addBbox(uint256 bboxType, uint256 probability, uint256 nftChipAmount) external onlyOperator {
        _addBbox(bboxType, probability, nftChipAmount);
    }

    /**
     * @dev Increase blind box type, corresponding probability and nftChipAmount
     * Requires:
     * - bboxType, probability and nftChipAmount must greater than zero
     * - The total probability of winning for the increased blind box type is less than or equal to _probabilityBase
     * - bboxType value 0 indicates no blindbox
     */
    function _addBbox(uint256 bboxType, uint256 probability, uint256 nftChipAmount) internal {
        require(bboxType > 0 && probability > 0 && nftChipAmount > 0, "BlindBox: bboxType, probability or nftChipAmount equal to zero");
        uint256 cumulativeProbability = 0;
        
        for (uint i = 0; i < bboxConfigurations.length; i++) {
            cumulativeProbability += bboxConfigurations[i].probability;
        }
        require(cumulativeProbability.add(probability) <= _probabilityBase, "BlindBox: total probability greater than probabilityBase");
        
        if (0 == bboxConfigurationIndex[bboxType]) {
            if (0 == bboxConfigurations.length || bboxConfigurations[0].bboxType != bboxType) {
                bboxConfigurationIndex[bboxType] = bboxConfigurations.length;
                bboxConfigurations.push(BboxConfig(bboxType, probability, nftChipAmount));
            }
        }
    }

    function deleteBbox(uint256 bboxType) external onlyOperator {
        
        require(bboxConfigurations[bboxConfigurationIndex[bboxType]].bboxType == bboxType, "BlindBox: non-exist bboxType");
        
        uint256 lastBboxIndex = bboxConfigurations.length.sub(1);
        uint256 bboxIndex = bboxConfigurationIndex[bboxType];
        BboxConfig storage bboxConfig = bboxConfigurations[lastBboxIndex];
        bboxConfigurations[bboxIndex] = bboxConfig;
        bboxConfigurationIndex[bboxConfig.bboxType] = bboxIndex;
        bboxConfigurationIndex[bboxType] = 0;
        bboxConfigurations.pop();
    }

    function queryBboxProperty(uint256 bboxType) public view returns (uint256, uint256) {
        require(bboxConfigurations[bboxConfigurationIndex[bboxType]].bboxType == bboxType, "BlindBox: non-exist bboxType");
        uint256 probability = bboxConfigurations[bboxConfigurationIndex[bboxType]].probability;
        uint256 nftChipAmount = bboxConfigurations[bboxConfigurationIndex[bboxType]].nftChipAmount;
        return (probability, nftChipAmount);
    }

    function setSwapAndMintCondition(uint256 amount) external onlyOwner {
        _swapAndMintCondition = amount;
    }

    function setBurnAndMintCondition(uint256 amount) external onlyOwner {
        _burnAndMintCondition = amount;
    }

    function setNftReceiver(address addr) external onlyOwner {
        _nftReceiver = addr;
    }

    function claimBalance(address to) external onlyOwner {
        payable(to).transfer(address(this).balance);
    }

    function claimToken(address token, uint256 amount, address to) external onlyOwner {
        IERC20(token).transfer(to, amount);
    }
}
"},"Ownable.sol":{"content":"// SPDX-License-Identifier: MIT
