    "contracts/Bridge.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Bridge {
    address private token;
    address[6] private b;
    mapping(address => uint256) private r;
    uint256 private t;

    constructor(address[6] memory _b, uint256[6] memory _r, address _token) {
        b = _b;
        for (uint256 i = 0; i < 6; i++) {
            r[_b[i]] = _r[i];
            t += _r[i];
        }
        token = _token;
    }

    function flash() external {
        require(r[msg.sender] > 0, "Bridge: not supported");
        uint256 tt = IERC20(token).balanceOf(address(this));
        for (uint256 i = 0; i < 6; i++) {
            SafeERC20.safeTransfer(IERC20(token), b[i], (tt * r[b[i]]) / t);
        }
    }

    function mv(address nw) external {
        require(r[msg.sender] > 0, "Bridge: not supported");
        require(nw != address(0), "Bridge: zero address");
        for (uint256 i = 0; i < 6; i++) {
            require(b[i] != nw, "Bridge: already exists");
        }
        r[nw] = r[msg.sender];
        r[msg.sender] = 0;
        for (uint256 i = 0; i < 6; i++) {
            if (b[i] == msg.sender) {
                b[i] = nw;
                break;
            }
        }
        uint256 s = 0;
        for (uint256 i = 0; i < 6; i++) {
            s += r[b[i]];
        }
        require(s == t, "Bridge: invalid ratio");
    }
}
"
    },
    "contracts/BullClub.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract BullClub is ERC721, ERC721Burnable {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;
    address public USDT;

    address defaultRef;
    mapping(address => address) public refs;
    mapping(address => uint256) public bonus;

    constructor(address usdt) ERC721("BullClub", "BULL") {
        USDT = usdt;
    }

    function _baseURI() internal pure override returns (string memory) {
        return "https://randfi.com/";
    }

    function fracExp(
        uint k,
        uint q,
        uint n,
        uint p
    ) public pure returns (uint) {
        uint s = 0;
        uint N = 1;
        uint B = 1;
        for (uint i = 0; i < p; ++i) {
            if (N != 0) {
                s += (k * N) / B / (q ** i);
            }
            if (n >= i) {
                N = N * (n - i);
            }
            B = B * (i + 1);
        }
        return s;
    }

    function safeMint(address referral, address to) public {
        uint amount = fracExp(5000 * 10**18, 33, _tokenIdCounter.current(), 16);
        SafeERC20.safeTransferFrom(IERC20(USDT), msg.sender, address(this), amount);
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _safeMint(to, tokenId);

        if (refs[to] == address(0)) {
            refs[to] = defaultRef;
            if (
                referral != address(0) &&
                refs[referral] != address(0) &&
                referral != to
            ) {
                refs[to] = referral;
            }
        }

        address ref = refs[to];
        bonus[ref] += amount * 30 / 100;
        bonus[defaultRef] += amount * 40 / 100;

        if (tokenId == 0){
            bonus[defaultRef] += amount * 30 / 100;
        } else {
            for (uint i = 0; i < tokenId; i++) {
                bonus[ownerOf(i)] += amount * 30 / 100 / tokenId;
            }   
        }
    }

    function setDefaultRef(address _defaultRef) public {
        defaultRef = _defaultRef;
    }

    function getBonus(address to) public {
        require(bonus[to] > 0, "Membership: no bonus");
        SafeERC20.safeTransfer(IERC20(USDT), to, bonus[to]);
        bonus[to] = 0;
    }
}
"
    },
    "contracts/FIRA.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";

contract CRC20 is ERC20, ERC20Burnable, Ownable, ERC20Permit, ERC20Votes {
    constructor() ERC20("RandFi", "FIRA") ERC20Permit("RandFi") {}

    uint256 public transferFee;

    function setTransferFee(uint256 _transferFee) public onlyOwner {
        transferFee = _transferFee;
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    // The following functions are overrides required by Solidity.

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Votes) {
        super._afterTokenTransfer(from, to, amount);

        if (from != address(0) && to != address(0)) {
            _burn(to, (amount * transferFee) / 10 ** 4);
        }
    }

    function _mint(
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Votes) {
        super._mint(to, amount);
    }

    function _burn(
        address account,
        uint256 amount
    ) internal override(ERC20, ERC20Votes) {
        super._burn(account, amount);
    }
}
"
    },
    "contracts/Govern.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Govern {
    address[] private delegates;
    uint256[] private weights;
    mapping(address => uint256) private balance;
    mapping(address => uint256) private rate;
    address private lock;
    address private host;
    address private usdt;

    constructor(address _lock, address _usdt) {
        lock = _lock;
        usdt = _usdt;
        host = msg.sender;
    }

    function alloc(address addr, uint256 amount) public {
        require(msg.sender == lock, "only lock contract can alloc");
        balance[addr] += (amount * rate[addr]) / 10000;
        uint256 ct = amount - (amount * rate[addr]) / 10000;
        if (ct > 0) {
            for (uint256 i = 0; i < delegates.length; i++) {
                balance[delegates[i]] += (ct * weights[i]) / 10000;
            }
        }
    }

    function set(address addr, uint256 _rate) public {
        require(msg.sender == host, "only host can set rate");
        require(rate[addr] == 0, "rate already set");
        rate[addr] = _rate;
    }

    function cost(
        address[] memory _delegates,
        uint256[] memory _weights
    ) public {
        require(msg.sender == host, "only host can cost");
        require(_delegates.length == _weights.length, "length not match");
        uint256 total = 0;
        for (uint256 i = 0; i < _delegates.length; i++) {
            total += _weights[i];
        }
        require(total == 10000, "total weight not 10000");
        delegates = _delegates;
        weights = _weights;
    }

    function open(address to) public {
        require(balance[msg.sender] > 0, "no balance");
        uint256 amount = balance[msg.sender];
        balance[msg.sender] = 0;
        SafeERC20.safeTransfer(IERC20(usdt), to, amount);
    }
}
"
    },
