      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.10;
pragma abicoder v2;

import "Ownable.sol";
import "ERC2771Context.sol";
import "ECDSA.sol";

import "BarterAbstract.sol";
import "IBarterTrading.sol";

contract BarterTrading is IBarterTrading, BarterAbstract, ERC2771Context, Ownable {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;

    mapping(uint256 => Deposit) private _deposits;
    uint256 private nextDepositId;
    mapping(IERC20 => uint256) public collectedFees;
    uint256 internal _feePercent = 1; 
    uint256 private _placeFee = 10 * 100_000_000;

    IERC20 private _feeToken;

    event DepositCreated(uint256 depositId);
    event SaleCanceled(uint256 depositId);
    event DepositBought(uint256 depositId);
    event DepositWithdrawn(uint256 depositId);
    event MoneyWithdrawn(uint256 depositId);

    constructor(IERC20 feeToken_, address trustedForwarder) ERC2771Context(trustedForwarder) {
        _feeToken = feeToken_;
    }

    function getDeposit(uint256 depositId) public view override returns(Deposit memory) {
        return _deposits[depositId];
    }

    function placeFee() public view returns(uint256) {
        return _placeFee;
    }

    function feePercent() public view returns(uint256) {
        return _feePercent;
    }

    function setPlaceFee(uint256 placeFee_) public onlyOwner {
        _placeFee = placeFee_;
    }

    function setFeePercent(uint256 feePercent_) public onlyOwner {
        _feePercent = feePercent_;
    }

    function depositTokens(TokenValues calldata tokens, uint256 price, IERC20 paymentContract) public {
        address sender =_msgSender();
        require(price > 0, "BarterTrading: price must be >0");

        _feeToken.safeTransferFrom(sender, address(this), _placeFee);
        transferTokens(sender, address(this), tokens);
        collectedFees[_feeToken] += _placeFee;

        _deposits[nextDepositId] = Deposit ({
            owner:sender,
            tokens: tokens,
            buyer:address(0),
            paymentContract: paymentContract,
            price: price,
            status: DepositStatus.FOR_SALE
        });

        emit DepositCreated(nextDepositId);
        nextDepositId++;
    }

    function cancelSale(uint256 depositId) public nonReentrant {
        Deposit storage deposit = _deposits[depositId];
        address sender = _msgSender();
        require(deposit.owner == sender, "BarterTrading: You do not have permission to cancelSale");
        require(
            deposit.status == DepositStatus.FOR_SALE,
            "BarterTrading: You can't cancel this sale"
        );
        transferTokens(address(this), sender, deposit.tokens);

        deposit.status = DepositStatus.CANCELED;
        emit SaleCanceled(depositId);
    }

    function buyDeposit(uint256 depositId) public {
        address sender = _msgSender();
        Deposit storage deposit = _deposits[depositId];

        require(
            deposit.status == DepositStatus.FOR_SALE,
            "BarterTrading: You can't buy this deposit"
        );

        deposit.paymentContract.safeTransferFrom(
            sender,
            address(this),
            deposit.price
        );

        deposit.status = DepositStatus.SOLD;
        deposit.buyer = sender;

        emit DepositBought(depositId);
    }

    function withdrawDepositAfterDeal(uint256 depositId, bytes memory signature) public nonReentrant {
        address sender = _msgSender();
        Deposit storage deposit = _deposits[depositId];
        require(
            deposit.buyer == sender, "BarterTrading: You are not the buyer"
        );
        require(
            deposit.status == DepositStatus.SOLD,
            "BarterTrading: you can't withdraw this deposit"
        );
        require(keccak256(abi.encodePacked(sender, depositId, 
                    address(this))).toEthSignedMessageHash().recover(signature) == owner(), "Invalid signature");

        transferTokens(address(this), sender, deposit.tokens);

        deposit.status = DepositStatus.WITHDRAWN;
        emit DepositWithdrawn(depositId);
    }

    function withdrawMoneyAfterDeal(uint256 depositId) public nonReentrant {
        address sender = _msgSender();
        Deposit storage deposit = _deposits[depositId];
        require(
            deposit.status == DepositStatus.WITHDRAWN,
            "BarterTrading: you can't withdraw money"
        );
        require(
            sender == deposit.owner,
            "BarterTrading: It's not your deposit"
        );

        uint256 fee = deposit.price / 100 * _feePercent;

        deposit.paymentContract.safeTransfer(
            deposit.owner,
            deposit.price - fee
        );

        collectedFees[deposit.paymentContract] += fee;
        deposit.status = DepositStatus.MONEY_WITHDRAWN;
        emit MoneyWithdrawn(depositId);
    }

    function withdrawFees(IERC20 contractAddress) public onlyOwner {
        contractAddress.safeTransfer(owner(), collectedFees[contractAddress]);
        collectedFees[contractAddress] = 0;
    }

    function _msgSender() internal view virtual override(Context, ERC2771Context) returns (address sender) {
        return ERC2771Context._msgSender();
    }

    function _msgData() internal view virtual override(Context, ERC2771Context) returns (bytes calldata) {
        return ERC2771Context._msgData();
    }
}"
