    "BarterAbstract.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.10;

import "SafeERC20.sol";
import "ERC1155Receiver.sol";
import "ReentrancyGuard.sol";
import "IBarterAbstract.sol";

abstract contract BarterAbstract is IBarterAbstract, ERC1155Receiver, ReentrancyGuard {
    using SafeERC20 for IERC20;

    function transferTokens(address from, address to, TokenValues memory tokens) internal {
        require(tokens.token20.length + tokens.token721.length + tokens.token1155.length > 0, "B: Ntwp"); // No tokens were provided
        require(tokens.token20.length == tokens.token20Value.length &&
                tokens.token721.length == tokens.token721Id.length &&
                tokens.token1155.length == tokens.token1155Id.length && 
                tokens.token1155Id.length == tokens.token1155Value.length, "B: Ital"); //Invalid token array lengths

        if (from == address(this)){
            for (uint256 i = 0; i < tokens.token20.length; i++) {
                tokens.token20[i].safeTransfer(to, tokens.token20Value[i]);
            }
        }
        else {
            for (uint256 i = 0; i < tokens.token20.length; i++) {
                tokens.token20[i].safeTransferFrom(from, to, tokens.token20Value[i]);
            }
        }
        for (uint256 i = 0; i < tokens.token721.length; i++) {
            tokens.token721[i].safeTransferFrom(from, to, tokens.token721Id[i]);
        }
        for (uint256 i = 0; i < tokens.token1155.length; i++) {
            tokens.token1155[i].safeTransferFrom(from, to, tokens.token1155Id[i], tokens.token1155Value[i], "");
        }
    }

    function onERC721Received(address /* operator */, address /* from */, uint256 /* tokenId */, bytes calldata /* data */) override public pure returns (bytes4) {
        return this.onERC721Received.selector;
    }

    function onERC1155Received(address /* operator */, address /* from */, uint256 /* id */, uint256 /* value */, bytes calldata /* data */) override public pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(address /* operator */, address /* from */, uint256[] calldata /* ids */, uint256[] calldata /* values */, bytes calldata /* data */) override public pure returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

}"
