    "contracts/DogeTrixChainAI.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

pragma abicoder v2;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract DogeTrixChainAI is Ownable, Pausable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 private usdtContract;

    address private adminAddress;

    mapping(bytes => address) private transactionSender;
    mapping(bytes => address[]) private transactionRecipients;
    mapping(bytes => uint256[]) private transactionAmounts;

    event ActionEvent(
        address sender,
        bytes transactionId,
        address[] recipients,
        uint256[] amounts,
        uint256 totalAmount
    );

    event MultiSendEvent(
        address sender,
        address[] recipients,
        uint256[] amounts
    );
    event Pause();
    event Unpause();

    constructor(address _usdtContract) {
        usdtContract = IERC20(_usdtContract);
    }

    function setAdminAddress(
        address _adminAddress
    ) public whenNotPaused nonReentrant onlyOwner {
        require(
            adminAddress != _adminAddress,
            "New address is the same as the current one"
        );
        adminAddress = _adminAddress;
    }

    function getTransactionRecipients(
        bytes calldata transactionId
    ) public view returns (address[] memory) {
        return transactionRecipients[transactionId];
    }

    function getTransactionAmounts(
        bytes calldata transactionId
    ) public view returns (uint256[] memory) {
        return transactionAmounts[transactionId];
    }

    function action(
        bytes calldata transactionId,
        address[] calldata recipients,
        uint256[] calldata amounts,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public whenNotPaused {
        require(
            transactionSender[transactionId] == address(0),
            "META_TX: Transaction already executed"
        );
        require(
            recipients.length == amounts.length,
            "META_TX: Receipients and amounts are difference size"
        );

        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                prefix,
                keccak256(abi.encodePacked(transactionId, recipients.length))
            )
        );

        address signer = ECDSA.recover(messageHash, v, r, s);
        require(signer != address(0), "ECDSA: invalid signature");
        require(signer == adminAddress, "Invalid Signer");

        uint256 totalAmount = 0;
        for (uint i = 0; i < recipients.length; i++) {
            usdtContract.safeTransferFrom(
                msg.sender,
                recipients[i],
                amounts[i]
            );
            totalAmount += amounts[i];
        }

        transactionSender[transactionId] = msg.sender;
        transactionRecipients[transactionId] = recipients;
        transactionAmounts[transactionId] = amounts;

        emit ActionEvent(
            msg.sender,
            transactionId,
            recipients,
            amounts,
            totalAmount
        );
    }

    function multiSend(
        address[] memory recipients,
        uint256[] memory amounts
    ) public whenNotPaused nonReentrant {
        require(
            recipients.length == amounts.length,
            "Arrays must have the same length"
        );

        // Send the tokens to each recipient
        for (uint i = 0; i < recipients.length; i++) {
            usdtContract.safeTransferFrom(
                msg.sender,
                recipients[i],
                amounts[i]
            );
        }

        emit MultiSendEvent(msg.sender, recipients, amounts);
    }

    function pause() public nonReentrant whenNotPaused onlyOwner {
        _pause();
        emit Pause();
    }

    function unpause() public nonReentrant whenPaused onlyOwner {
        _unpause();
        emit Unpause();
    }
}
"
