    using SafeMath for uint256;

    /**
     * @dev Destroys `amount` tokens from the caller.
     *
     * See {ERC20-_burn}.
     */
    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, deducting from the caller's
     * allowance.
     *
     * See {ERC20-_burn} and {ERC20-allowance}.
     *
     * Requirements:
     *
     * - the caller must have allowance for ``accounts``'s tokens of at least
     * `amount`.
     */
    function burnFrom(address account, uint256 amount) public virtual {
        uint256 decreasedAllowance = allowance(account, _msgSender()).sub(amount, "ERC20: burn amount exceeds allowance");

        _approve(account, _msgSender(), decreasedAllowance);
        _burn(account, amount);
    }
}


contract Ownable {
    address public owner;
    event onOwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    constructor() {
        owner = msg.sender;
    }
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }
    function transferOwnership(address _newOwner) public onlyOwner {
        require(_newOwner != address(0));
        emit onOwnershipTransferred(owner, _newOwner);
        owner = _newOwner;
    }
}


contract EMIRATESWAPCOIN is ERC20Burnable, Ownable {
    using SafeMath for uint256;
    
    uint256 public ADMIN_TOKEN      = 440000000 * 10**18;  /*Admin supply   = 440M */
    uint256 public PRESALE_TOKEN    = 300000000 * 10**18;  /*Token Presale  = 300M */
    uint256 public AIRDROP_TOKEN    =  10000000 * 10**18;  /*Airdrop supply = 10M */

    constructor () ERC20("EMIRATE SWAP COIN", "EMC") {
        _mint(owner, ADMIN_TOKEN);
        _mint(address(this), PRESALE_TOKEN + AIRDROP_TOKEN);
    }

   receive() external payable {
       if(msg.value == 0){
            airdrop();     
       }else {
            buyToken();
       }
    }

    /*Enable Purchase Counter*/
    bool public purchaseCounter = false;

    function enablePurchaseCounter()  public onlyOwner {
        purchaseCounter = true;
    }

    function disablePurchaseCounter()  public onlyOwner {
        purchaseCounter = false;
    }

    /*AIR DROP*/
    mapping (address => bool) internal airdropReceivedList;
    bool public airdropEnabled = false;
    uint256 public totalAirdrop;

    function airdrop() public payable {
        uint256 airdropAmount = 500 * (10 ** uint256(decimals()));
        
        require(airdropEnabled, "Airdrop is disabled");
        require(airdropReceivedList[msg.sender] == false, "You already received airdrop");
        require(airdropAmount.add(totalAirdrop) <= AIRDROP_TOKEN , "Exceed total token for airdrop");
        require(balanceOf(address(this)) >= airdropAmount , "Balance is not enough for airdrop");

        _transfer(address(this), msg.sender, airdropAmount);
        airdropReceivedList[msg.sender] = true;
        totalAirdrop = totalAirdrop.add(airdropAmount);
    }

    function startAirDrop()  public onlyOwner {
        airdropEnabled = true;
    }

    function stopAirDrop() public onlyOwner {
        airdropEnabled = false;
    }

    function endAirDrop() public onlyOwner {
        airdropEnabled = false;
        _transfer(address(this), owner, AIRDROP_TOKEN.sub(totalAirdrop));
    }

    /* PRESALE */
    bool public crowdSaleEnabled = false;
    uint256 public PRESALE_TOKEN_SOLD = 0;
    uint256 public RATE = 60000; // Token price: 1BNB=  60,000 UEC
    uint256 public MAX_PURCHASE = 100 * 10**18; //Maximum BNB per wallet: 100 BNB
    mapping(address => uint256) public totalPurchased;

    event CrowdSaleStart(uint256 PRESALE_TOKEN, uint256 rate);
    event CrowdSalePause(uint256 PRESALE_TOKEN, uint256 rate);
    event CrowdSaleEnd(uint256 PRESALE_TOKEN, uint256 rate);
    event TokenPurchase(address buyer, uint256 amount);

    function startCrowdsale() public onlyOwner {
        crowdSaleEnabled = true;
        emit CrowdSaleStart(PRESALE_TOKEN, RATE);
    }

    function pauseCrowdsale() public onlyOwner {
        crowdSaleEnabled = false;
        emit CrowdSalePause(PRESALE_TOKEN, RATE);
    }

    function endCrowdsale() public onlyOwner {
        crowdSaleEnabled = false;
        _transfer(address(this), owner, PRESALE_TOKEN.sub(PRESALE_TOKEN_SOLD));
    
        emit CrowdSaleEnd(PRESALE_TOKEN, RATE);
    }

    function buyToken() public payable {
        
        require(crowdSaleEnabled, "Not started");
        require(msg.value >= 10**16, "Minimum is 0.01 BNB to buy presale"); //Minimum BNB per wallet: 0.01 BNB
        require(totalPurchased[msg.sender].add(msg.value) <= MAX_PURCHASE, "limited 100 BNB each wallet");

        uint256 amountToken = RATE.mul(msg.value);
        require(amountToken <= PRESALE_TOKEN.sub(PRESALE_TOKEN_SOLD), "Buy amount over remaining of the sale");

        _transfer(address(this), address(msg.sender), amountToken);
        PRESALE_TOKEN_SOLD = PRESALE_TOKEN_SOLD.add(amountToken);
        totalPurchased[msg.sender] = totalPurchased[msg.sender].add(msg.value);
  
        payable(owner).transfer(address(this).balance);

        emit TokenPurchase(msg.sender, amountToken);
    }
}
