    "contracts/Marketplace.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/// @custom:security-contact support@xcadnetwork.com
contract Marketplace is AccessControl, Pausable, ReentrancyGuard {
	using SafeERC20 for IERC20;

	struct Auction {
		// address of the seller
		address seller;
		// address of the token to sale
		address nftAddress;
		// if the auction is for ERC721 - true - or ERC1155 - false
		bool isErc721;
		// ID of the NFT
		uint tokenId;
		// Timestamp of end of auction
		uint endedAt;
		// Timestamp, in which the auction started.
		uint startedAt;
		// for ERC-1155 - how many tokens are for sale
		uint amount;
		// Actual highest bidder
		address bidder;
		// Actual highest bid
		uint highestBid;
	}

	modifier canList() {
		require(
			listingOpen || hasRole(STORE_ADMIN_ROLE, msg.sender),
			"New listings are paused"
		);
		_;
	}

	mapping(string => Auction) private _auctions;

	mapping(address => bool) private _allowedNfts;

	uint private _auctionCount;

	string[] private _openAuctions;

	bool public listingOpen = true;

	// in percents, what's the fee for the auction house, 1% - 100, 100% - 10000, range 1-10000 means 0.01% - 100%
	uint public auctionFee;
	uint public auctionCancellationFee;

	uint public idNonce = 0;

	uint private constant _auctionFeeDenominator = 100_00;
	uint private constant _auctionMinimumBidIncrement = 5_00;

	uint public minAuctionDuration = 300; // 5 minutes
	uint public maxAuctionDuration = 432_000; // default 5 days

	address public immutable erc20Address;

	address public auctionFeeRecipient;

	bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
	bytes32 public constant STORE_ADMIN_ROLE = keccak256("STORE_ADMIN_ROLE");
	bytes32 public constant TREASURY_ROLE = keccak256("TREASURY_ROLE");

	event AuctionCreated(
		bool indexed isErc721,
		address indexed nftAddress,
		uint indexed tokenId,
		string id,
		uint amount,
		uint endedAt,
		uint basePrice
	);

	event AuctionBid(address indexed buyer, uint indexed amount, string id);

	event AuctionSettled(string id);

	event AuctionCancelled(string id);

	event CancellationFeeChanged(uint fee);

	event AuctionFeeChanged(uint fee);

	event TreasuryChanged(address treasurer);

	event ListingDurationChanged(uint min, uint max);

	function onERC1155Received(
		address,
		address,
		uint,
		uint,
		bytes memory
	) public virtual returns (bytes4) {
		return this.onERC1155Received.selector;
	}

	function onERC1155BatchReceived(
		address,
		address,
		uint[] memory,
		uint[] memory,
		bytes memory
	) public virtual returns (bytes4) {
		return this.onERC1155BatchReceived.selector;
	}

	constructor(
		address _erc20Address,
		uint fee,
		uint cancellationFee,
		address feeRecipient
	) {
		require(feeRecipient != address(0), "Invalid Recipient Address");
		require(_erc20Address != address(0), "Invalid Token address");
		require(fee < _auctionFeeDenominator, "Invalid Auction Fee");
		auctionFee = fee;
		auctionFeeRecipient = feeRecipient;
		auctionCancellationFee = cancellationFee;
		erc20Address = _erc20Address;
		_grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
		_grantRole(PAUSER_ROLE, msg.sender);
		_grantRole(STORE_ADMIN_ROLE, msg.sender);
		_grantRole(TREASURY_ROLE, msg.sender);
	}

	function getOpenAuctions() public view virtual returns (string[] memory) {
		return _openAuctions;
	}

	function getOpenAuctionsPg(
		uint _page,
		uint _size
	) public view virtual returns (string[] memory) {
		require(_page > 0, "Page needs to be 1 or above");
		uint _auctionsIndex = _size * _page - _size;
		if (
			_openAuctions.length == 0 ||
			_auctionsIndex > _openAuctions.length - 1
		) {
			string[] memory empty;
			return empty;
		}
		string[] memory openAuctions = new string[](_size);
		uint _returnCounter = 0;
		for (; _auctionsIndex < _size * _page; ) {
			if (_auctionsIndex < _openAuctions.length - 1) {
				openAuctions[_returnCounter] = _openAuctions[_auctionsIndex];
			} else {
				break;
			}

			_returnCounter++;
			unchecked {
				_auctionsIndex++;
			}
		}
		return openAuctions;
	}

	function getAuction(
		string memory id
	) public view virtual returns (Auction memory) {
		return _auctions[id];
	}

	function getAuctions(
		string[] memory ids
	) public view virtual returns (Auction[] memory) {
		Auction[] memory auctions;
		for (uint i = 0; i < ids.length; i = i + 1) {
			Auction memory auction = _auctions[ids[i]];
			auctions[i] = auction;
		}
		return auctions;
	}

	/**
	 * Admin Functions
	 **/
	function pause() external onlyRole(PAUSER_ROLE) {
		_pause();
	}

	function unpause() external onlyRole(PAUSER_ROLE) {
		_unpause();
	}

	function setAuctionFee(uint fee) public virtual onlyRole(STORE_ADMIN_ROLE) {
		require(fee < _auctionFeeDenominator, "Invalid Auction Fee");
		auctionFee = fee;
		emit AuctionFeeChanged(fee);
	}

	/**
	 *	This is a value in tokens that users need to pay
	 *	to cancel an auction that has no bidders.
	 **/

	function setCancellationFee(uint fee) external onlyRole(STORE_ADMIN_ROLE) {
		auctionCancellationFee = fee;
		emit CancellationFeeChanged(fee);
	}

	function addNFT(address _nft) external onlyRole(STORE_ADMIN_ROLE) {
		_allowedNfts[_nft] = true;
	}

	function removeNFT(address _nft) external onlyRole(STORE_ADMIN_ROLE) {
		delete _allowedNfts[_nft];
	}

	function pauseListings() external onlyRole(PAUSER_ROLE) {
		listingOpen = false;
	}

	function resumeListings() external onlyRole(PAUSER_ROLE) {
		listingOpen = true;
	}

	function setAuctionFeeRecipient(
		address recipient
	) external onlyRole(TREASURY_ROLE) {
		require(recipient != address(0), "Invalid Recipient Address");
		auctionFeeRecipient = recipient;
		emit TreasuryChanged(recipient);
	}

	function setListingDuration(
		uint min,
		uint max
	) external onlyRole(STORE_ADMIN_ROLE) {
		require(min < max, "Invalid duration restrictions");
		minAuctionDuration = min;
		maxAuctionDuration = max;
		emit ListingDurationChanged(min, max);
	}

	function cancelAllAuctions() external onlyRole(STORE_ADMIN_ROLE) {
		uint _openAuctionCount = _openAuctions.length;
		require(_openAuctionCount > 0, "No open auctions");
		string memory _auctionId;
		for (uint i = _openAuctionCount; i > 0; ) {
			_auctionId = _openAuctions[i - 1];
			cancelAuction(_auctionId);
			unchecked {
				i = i - 1;
			}
		}
	}

	/**
	 * Internal Functions
	 **/

	/**
	 * Check if the seller is the owner of the token.
	 * We expect that the owner of the tokens approves the spending before he launch the auction
	 * The function escrows the tokens to sell
	 **/
	function _escrowTokensToSell(
		bool isErc721,
		address nftAddress,
		address seller,
		uint tokenId,
		uint amount
	) internal {
		if (!isErc721) {
			require(amount > 0, "Invalid Token Amount");
			IERC1155(nftAddress).safeTransferFrom(
				seller,
				address(this),
				tokenId,
				amount,
				""
			);
		} else {
			IERC721(nftAddress).transferFrom(seller, address(this), tokenId);
		}
	}

	/**
	 * Transfer NFT from the contract to the recipient
	 */
	function _transferNFT(
		bool isErc721,
		address nftAddress,
		address recipient,
		uint tokenId,
		uint amount
	) internal {
		if (!isErc721) {
			IERC1155(nftAddress).safeTransferFrom(
				address(this),
				recipient,
				tokenId,
				amount,
				""
			);
		} else {
			IERC721(nftAddress).transferFrom(address(this), recipient, tokenId);
		}
	}

	function _transferAssets(
		uint amount,
		address recipient,
		bool settleOrReturnFee
	) internal {
		uint fee = (amount * auctionFee) / _auctionFeeDenominator;
		if (settleOrReturnFee) {
			unchecked {
				IERC20(erc20Address).safeTransfer(recipient, amount - fee);
			}
			IERC20(erc20Address).safeTransfer(auctionFeeRecipient, fee);
		} else {
			IERC20(erc20Address).safeTransfer(recipient, amount);
		}
	}

	/**
	 * Public Functions
	 **/

	function createAuction(
		bool isErc721,
		address nftAddress,
		uint tokenId,
		uint amount,
		uint endedAt,
		uint basePrice
	) external whenNotPaused canList nonReentrant {
		string memory id = string(
			abi.encodePacked(
				Strings.toString(tokenId),
				"#",
				Strings.toString(endedAt),
				"#",
				Strings.toString(idNonce)
			)
		);
		require(
			_auctions[id].startedAt == 0,
			"Auction already existed for current auction Id"
		);
		require(_allowedNfts[nftAddress], "NFT unauthorized for sale");
		require(
			endedAt > block.timestamp + minAuctionDuration &&
				endedAt < block.timestamp + maxAuctionDuration,
			"Invalid Auction Duration"
		);

		_escrowTokensToSell(isErc721, nftAddress, msg.sender, tokenId, amount);

		unchecked {
			_auctionCount++;
			idNonce++;
		}

		Auction memory auction = Auction({
			seller: msg.sender,
			nftAddress: nftAddress,
			tokenId: tokenId,
			isErc721: isErc721,
			endedAt: endedAt,
			startedAt: block.timestamp,
			amount: amount,
			bidder: address(0),
			highestBid: basePrice
		});

		_auctions[id] = auction;
		_openAuctions.push(id);
		emit AuctionCreated(
			isErc721,
			nftAddress,
			tokenId,
			id,
			amount,
			endedAt,
			basePrice
		);
	}

	function bid(
		string memory id,
		uint bidValue
	) external whenNotPaused nonReentrant {
		Auction memory auction = _auctions[id];
		require(
			auction.endedAt > block.timestamp,
			"Auction has already ended. Unable to process bid. Aborting."
		);
		require(bidValue > 0, "Zero bids are not allowed");
		uint _bidIncrementMinimum = Math.ceilDiv(
			auction.highestBid * _auctionMinimumBidIncrement,
			_auctionFeeDenominator
		);
		require(
			bidValue >= auction.highestBid + _bidIncrementMinimum,
			"Bid is smaller than the required increment. Aborting."
		);

		IERC20(erc20Address).safeTransferFrom(
			msg.sender,
			address(this),
			bidValue
		);

		if (auction.bidder != address(0) && auction.highestBid != 0) {
			_transferAssets(auction.highestBid, auction.bidder, false);
		}

		auction.highestBid = bidValue;
		auction.bidder = msg.sender;

		_auctions[id] = auction;
		emit AuctionBid(msg.sender, bidValue, id);
	}

	function massSettleAuctions(string[] memory _ids) external {
		for (uint i = 0; i < _ids.length; i = i + 1) {
			settleAuction(_ids[i]);
		}
	}

	/**
	 * Settle the already ended auction -
	 */
	function settleAuction(string memory id) public nonReentrant {
		Auction memory auction = _auctions[id];
		require(
			auction.endedAt < block.timestamp && auction.endedAt != 0,
			"Auction can't be settled before it reaches the end."
		);

		bool isErc721 = auction.isErc721;
		address nftAddress = auction.nftAddress;
		uint amount = auction.amount;
		uint tokenId = auction.tokenId;
		uint highestBid = auction.highestBid;
		address bidder = auction.bidder;

		if (bidder == address(0)) {
			_transferNFT(isErc721, nftAddress, auction.seller, tokenId, amount);
		} else {
			_transferNFT(isErc721, nftAddress, bidder, tokenId, amount);
		}

		if (bidder != address(0) && highestBid != 0) {
			_transferAssets(highestBid, auction.seller, true);
		}

		_toRemove(id);
		unchecked {
			_auctionCount--;
		}
		emit AuctionSettled(id);
	}

	function _toRemove(string memory id) internal {
		uint _auctionLength = _openAuctions.length;
		for (uint x = 0; x < _auctionLength; ) {
			if (
				keccak256(abi.encodePacked(_openAuctions[x])) ==
				keccak256(abi.encodePacked(id))
			) {
				if (x != _auctionLength - 1) {
					_openAuctions[x] = _openAuctions[_auctionLength - 1];
				}
				_openAuctions.pop();
				break;
			}
			unchecked {
				x++;
			}
		}
		delete _auctions[id];
	}

	function cancelAuction(string memory id) public virtual nonReentrant {
		Auction memory auction = _auctions[id];
		bool isAdmin = hasRole(STORE_ADMIN_ROLE, msg.sender);
		require(
			auction.seller != address(0),
			"Auction is already settled. Aborting."
		);
		require(
			auction.seller == msg.sender || isAdmin,
			"Auction can't be cancelled, only by seller or admin. Aborting."
		);
		require(
			auction.bidder == address(0) || isAdmin,
			"Auction can't be cancelled with active bid. Aborting."
		);
		bool isErc721 = auction.isErc721;
		address nftAddress = auction.nftAddress;
		uint amount = auction.amount;
		uint tokenId = auction.tokenId;
		uint highestBid = auction.highestBid;
		address bidder = auction.bidder;

		if (!isAdmin) {
			IERC20(erc20Address).safeTransferFrom(
				msg.sender,
				address(this),
				auctionCancellationFee
			);
		}

		_transferNFT(isErc721, nftAddress, auction.seller, tokenId, amount);

		if (bidder != address(0) && highestBid != 0) {
			_transferAssets(highestBid, bidder, false);
		}
		unchecked {
			_auctionCount--;
		}
		_toRemove(id);
		emit AuctionCancelled(id);
	}
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 10000
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "metadata": {
      "useLiteralContent": true
    },
    "libraries": {}
  }
}}
