    "contracts/staking/ILockedStaking.sol": {
      "content": "// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.16;

/**
 * @dev Interface of OPN Staking.
 */
interface ILockedStaking {
    struct LockedStake {
        uint256 lockup;
        uint256 amount;
        uint256 unlockTime;
        uint256 rewardAmount;
        uint256 boostedAmount;
    }

    struct PoolConfig {
        bool isActive;
        uint256 duration;
        uint256 apr;
        uint256 allocationBoost;
    }

    /**
     * @dev Emitted when a user stakes `_amount` of tokens from `_poolIndex` pool.
     */
    event Staked(address indexed user, uint256 amount, uint8 indexed poolIndex);

    /**
     * @dev Emitted when a user withdraws `_amount` of tokens.
     */
    event Withdrawn(address indexed user, uint256 amount, uint256 reward);

    event EmergencyExited(address user, uint256 amount);

    /**
     * @dev Stakes `_amount` of tokens in the `_poolIndex` pool.
     * @param _amount Amount of tokens to stake.
     * @param _poolIndex Index of the pool config.
     *
     * Emits a {Staked} event.
     */
    function stake(uint256 _amount, uint8 _poolIndex) external;

    /**
     * @dev Withdraws total unlockable tokens and gives rewards.
     *
     * Emits a {Withdrawn} event.
     */
    function withdraw() external;

    /**
     * @dev Returns current pool configs.
     * @return The pool configs.
     */
    function pools() external view returns (PoolConfig[] memory);

    /**
     * @notice Returns the info about the pool at `_poolIndex` position.
     * @param _poolIndex Index of the pool config.
     * @return The pool config.
     */
    function poolAt(uint8 _poolIndex) external view returns (PoolConfig memory);

    /**
     * @dev Returns the amount of tokens staked by `_address`.
     * @param _address Address to check.
     */
    function stakedOf(address _address) external view returns (uint256);

    /**
     * @dev Returns the Locked stake instances of a user.
     * @param _address Address to check.
     */
    function stakeInstancesOf(address _address) external view returns (LockedStake[] memory);

    /**
     * @dev Returns the amount of rewards that will be earned by `_address`.
     * @param _address Address to check.
     */
    function rewardOf(address _address) external view returns (uint256);

    /**
     * @dev Returns the amount of tokens that can be withdrawn by `_address`.
     * @param _address Address to check.
     * @return The amount of tokens that can be withdrawn.
     */
    function claimableOf(address _address) external view returns (uint256);

    /**
     * @dev Returns the amount of boosted tokens for allocation purposes.
     * @param _address Address to check.
     * @return The amount of boosted tokens.
     */
    function totalBoostedStakeOf(address _address) external view returns (uint256);

    /**
     * @dev Returns the total amount of tokens staked.
     */
    function totalStaked() external view returns (uint256);

    /**
     * @dev Returns the total amount of rewards earned.
     */
    function participantCount() external view returns (uint256);

    /**
     * @dev Returns the addresses between `_start` and `_end`.
     */
    function addresses(uint256 _start, uint256 _end) external view returns (address[] memory);
}
"
