    "contracts/staking/LockedStaking.sol": {
      "content": "// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.16;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import "contracts/staking/ILockedStaking.sol";

/**
 * @title LockedStaking
 * @notice Staking contract for  entry locked staking
 * @author Finceptor
 */
contract LockedStaking is ILockedStaking, Ownable, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using EnumerableMap for EnumerableMap.AddressToUintMap;

    uint16 private constant PRECISION = 10000; /// @notice 10000 = 100%

    /// @notice The maximum number of different active stake instances
    uint16 public maxStakeInstanceCount = 20;

    /// @notice The staking token
    IERC20 public immutable token;

    /// @notice The address of the fee recipient
    address private feeRecipient;
    /// @notice The fee for staking
    uint256 public stakeFee;

    /// @notice The pool configs
    PoolConfig[] public poolConfigs;

    /// @notice The address of the wallet that provides rewards
    address public stakingBank;

    /// @notice The total staked amount
    uint256 private _totalStaked;
    /// @notice The staked balances of users
    EnumerableMap.AddressToUintMap private _balances;
    /// @notice The stake instances of each user
    mapping(address => LockedStake[]) private _lockedStakes;

    /**
     * @notice The constructor for the FlexanleLockedStaking contract
     * Sets the stakingToken and stakingBank
     * @param _stakingToken the address of the staking token
     * @param _stakingBank the address of the staking bank
     * @param _configs the array of pool configs
     */
    constructor(address _stakingToken, address _stakingBank, uint256[][] memory _configs) {
        require(_stakingToken != address(0), "Staking token address cannot be 0");
        require(_stakingBank != address(0), "Staking bank address cannot be 0");

        token = IERC20(_stakingToken);
        stakingBank = _stakingBank;

        // Initialize the default pool configs
        for (uint256 i = 0; i < _configs.length; i++) {
            require(_configs[i].length == 3, "StakingCompound: Config length must be 3.");
            poolConfigs.push(PoolConfig(true, _configs[i][0], _configs[i][1], _configs[i][2]));
        }
    }

    //====================== ONLY OWNER ======================//

    function emergencyExit(address[] memory _users) external nonReentrant onlyOwner {
        for (uint256 i = 0; i < _users.length; i++) {
            (bool contains, uint256 balance) = _balances.tryGet(_users[i]);
            if (contains) {
                _balances.set(_users[i], 0);
                _totalStaked -= balance;
                delete _lockedStakes[_users[i]];
                token.safeTransfer(_users[i], balance);
                emit EmergencyExited(_users[i], balance);
            }
        }
    }

    /**
     * @notice Disables the pool at `_poolIndex` position.
     * @param _poolIndex Index of the pool config.
     */
    function disablePool(uint8 _poolIndex) external onlyOwner {
        require(_poolIndex < poolConfigs.length, "StakingCompound: Pool index out of range.");
        poolConfigs[_poolIndex].isActive = false;
    }

    /**
     * @notice Enables the pool at `_poolIndex` position.
     * @param _poolIndex Index of the pool config.
     */
    function enablePool(uint8 _poolIndex) external onlyOwner {
        require(_poolIndex < poolConfigs.length, "StakingCompound: Pool index out of range.");
        poolConfigs[_poolIndex].isActive = true;
    }

    /**
     * @notice Add a new pool config.
     * @param _duration is the desired duration
     * @param _apr is the desired apr
     * @param _boosts is the desired boosts
     */
    function addPool(uint256 _duration, uint256 _apr, uint256 _boosts) external onlyOwner {
        poolConfigs.push(PoolConfig(true, _duration, _apr, _boosts));
    }

    /**
     * @notice The function to update the max stake instances
     * @param _max the new max stake instances
     */
    function updateMaxStakeInstanceCount(uint16 _max) external onlyOwner {
        maxStakeInstanceCount = _max;
    }

    /**
     * @notice The function to pause staking
     * Can only be called by the owner
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice The function to unpause staking
     * Can only be called by the owner
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice The function to update the staking bank
     * Can only be called by the owner
     * @param _stakingBank the address of the new staking bank
     */
    function updateStakingBank(address _stakingBank) external onlyOwner {
        require(_stakingBank != address(0), "StakingCompund: staking bank address cannot be 0");
        stakingBank = _stakingBank;
    }

    /**
     * @notice The function to update the fee details of staking and withdrawing.
     * @dev Can only be called by the owner
     * @param _recipient the address of the fee recipient
     * @param _stakeFee the fee for staking
     */
    function setFeeDetails(
        address _recipient,
        uint256 _stakeFee //%100
    ) external onlyOwner {
        require(_stakeFee <= 100, "StakingCompound: Stake fee cannot be greater than 100.");
        require(_recipient != address(0), "StakingCompound: Recipient cannot be the zero address.");
        feeRecipient = _recipient; //@audit-info split to many addresses. Make payment splitter contract
        stakeFee = _stakeFee;
    }

    /**
     * @notice The function to update the apr amounts for lock options.
     * @dev Can only be called by the owner
     * @param _apr is the desired new apr
     * @param _poolIndex the index of the pool
     */
    function updateAprs(uint256 _apr, uint256 _poolIndex) external onlyOwner {
        require(_poolIndex < poolConfigs.length, "StakingCompound: Pool index out of range.");

        PoolConfig storage poolConfig = poolConfigs[_poolIndex];
        poolConfig.apr = _apr;
    }

    /**
     * @notice The function to update the boost amounts for lock options.
     * @dev Can only be called by the owner
     * @param _boosts the array of boosts
     * @param _poolIndex the index of the pool
     */
    function updateBoosts(uint256 _boosts, uint256 _poolIndex) external onlyOwner {
        require(_poolIndex < poolConfigs.length, "StakingCompound: Pool index out of range.");

        PoolConfig storage poolConfig = poolConfigs[_poolIndex];
        poolConfig.allocationBoost = _boosts;
    }

    //====================== PUBLIC FUNCTIONS ======================//

    /**
     * @notice The function to do a locked stake.
     * The user will receive a reward after the stake unlocks.
     *
     * @param _amount is the amount to stake
     * @param _poolIndex is the index of the pool
     */
    function stake(uint256 _amount, uint8 _poolIndex) external override nonReentrant whenNotPaused {
        require(_amount > 0, "LockedStaking: Stake amount must be greater than 0.");
        require(_poolIndex < poolConfigs.length, "LockedStaking: Pool index out of range.");
        require(
            _lockedStakes[msg.sender].length < maxStakeInstanceCount,
            "LockedStaking: Max stake instances reached. Withdraw some stakes to stake again."
        );
        require(poolConfigs[_poolIndex].isActive, "LockedStaking: Pool is not active.");

        uint256 _feeAmount = (_amount * stakeFee) / 100;

        (bool contains, uint256 balance) = _balances.tryGet(msg.sender);
        if (contains) {
            _balances.set(msg.sender, balance + (_amount - _feeAmount));
        } else {
            _balances.set(msg.sender, _amount - _feeAmount);
        }
        _totalStaked = _totalStaked + (_amount - _feeAmount);

        uint256 lockup = poolConfigs[_poolIndex].duration;
        uint256 unlockTime = block.timestamp + lockup;
        uint256 _yearlyRewardAmount = (_amount * poolConfigs[_poolIndex].apr) / PRECISION;
        uint256 _rewardAmount = (_yearlyRewardAmount * lockup) / 360 days;

        _lockedStakes[msg.sender].push(
            LockedStake({
                lockup: lockup,
                amount: _amount - _feeAmount,
                unlockTime: unlockTime,
                rewardAmount: _rewardAmount,
                boostedAmount: ((_amount - _feeAmount) * poolConfigs[_poolIndex].allocationBoost) /
                    PRECISION
            })
        );

        token.safeTransferFrom(msg.sender, address(this), _amount);
        if (feeRecipient != address(0)) {
            token.safeTransfer(feeRecipient, _feeAmount);
        }

        emit Staked(msg.sender, _amount - _feeAmount, _poolIndex);
    }

    /**
     * @notice Withdraws all unocked stakes of a user and gives the rewards.
     */
    function withdraw() public override nonReentrant {
        (bool contains, uint256 balance) = _balances.tryGet(msg.sender);
        require(contains, "Staking: no balance for account");

        uint256 _amount;
        uint256 _reward;
        LockedStake[] storage stakes = _lockedStakes[msg.sender];

        for (uint256 i = stakes.length; i > 0; i--) {
            uint index = i - 1;

            if (stakes[index].unlockTime <= block.timestamp) {
                _amount += (stakes[index].amount);
                _reward += (stakes[index].rewardAmount);

                // Remove the stake from the array
                stakes[index] = stakes[stakes.length - 1];
                stakes.pop();
            }
        }

        require(_amount > 0, "Staking: no unlocked stakes");

        _balances.set(msg.sender, balance - _amount);
        _totalStaked -= _amount;

        token.safeTransfer(msg.sender, _amount);
        token.safeTransferFrom(stakingBank, msg.sender, _reward);
        emit Withdrawn(msg.sender, _amount, _reward);
    }

    function poolAt(uint8 _poolIndex) external view override returns (PoolConfig memory) {
        require(_poolIndex < poolConfigs.length, "StakingCompound: Pool index out of range.");
        return poolConfigs[_poolIndex];
    }

    function pools() external view override returns (PoolConfig[] memory) {
        return poolConfigs;
    }

    /**
     * @notice Public function to get the number of participants.
     * @return uint256 number of participants
     */
    function participantCount() external view override returns (uint256) {
        return _balances.length();
    }

    function addresses(
        uint256 _start,
        uint256 _end
    ) external view override returns (address[] memory) {
        require(_start < _end, "StakingCompound: start must be less than end");
        uint256 length = _end - _start;
        address[] memory _addresses = new address[](length);
        for (uint256 i = 0; i < length; i++) {
            (address user, ) = _balances.at(_start + i);
            _addresses[i] = user;
        }
        return _addresses;
    }

    function stakedOf(address _account) external view override returns (uint256) {
        (bool contains, uint256 balance) = _balances.tryGet(_account);
        if (contains) {
            return balance;
        }
        return 0;
    }

    function stakeInstancesOf(
        address _address
    ) external view override returns (LockedStake[] memory) {
        return _lockedStakes[_address];
    }

    function rewardOf(address _account) external view override returns (uint256) {
        uint256 _reward;
        LockedStake[] memory stakes = _lockedStakes[_account];
        for (uint256 i = 0; i < stakes.length; i++) {
            _reward += stakes[i].rewardAmount;
        }
        return _reward;
    }

    function claimableOf(address _account) external view override returns (uint256) {
        uint256 _claimable;
        LockedStake[] memory stakes = _lockedStakes[_account];
        for (uint256 i = 0; i < stakes.length; i++) {
            if (stakes[i].unlockTime <= block.timestamp) {
                _claimable += stakes[i].rewardAmount + stakes[i].amount;
            }
        }
        return _claimable;
    }

    function totalBoostedStakeOf(address _address) external view override returns (uint256) {
        uint256 _boostedAmount;
        LockedStake[] memory stakes = _lockedStakes[_address];

        for (uint256 i = 0; i < stakes.length; i++) {
            _boostedAmount += stakes[i].boostedAmount;
        }

        return _boostedAmount;
    }

    function totalStaked() external view override returns (uint256) {
        return _totalStaked;
    }
}
"
    }
