// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract AntiBaleineToken {
    string public name = "Anti Baleine";
    string public symbol = "ANTI";
    uint8 public decimals = 18;
    uint256 public totalSupply = 200000000000 * 10 ** uint256(decimals);

    mapping(address => uint256) private balances;
    mapping(address => mapping(address => uint256)) private allowances;

    address private taxWallet = 0xeF47A069447950fb7666262D98e6D205BD219103;
    uint256 private taxPercentage = 5;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor() {
        balances[msg.sender] = totalSupply;
        emit Transfer(address(0), msg.sender, totalSupply);
    }

    function balanceOf(address account) public view returns (uint256) {
        return balances[account];
    }

    function transfer(address recipient, uint256 amount) public returns (bool) {
        require(amount <= balances[msg.sender], "Insufficient balance");
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public returns (bool) {
        require(amount <= balances[sender], "Insufficient balance");
        require(amount <= allowances[sender][msg.sender], "Insufficient allowance");
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, allowances[sender][msg.sender] - amount);
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) private {
        uint256 tax = amount * taxPercentage / 100;
        uint256 afterTaxAmount = amount - tax;

        balances[sender] -= amount;
        balances[recipient] += afterTaxAmount;
        balances[taxWallet] += tax;

        emit Transfer(sender, recipient, afterTaxAmount);
        emit Transfer(sender, taxWallet, tax);
    }

    function _approve(address owner, address spender, uint256 amount) private {
        allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
}
