pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IOffchainOracle {
    function getRate(IERC20 srcToken, IERC20 dstToken, bool useWrappers) external view returns (uint256 weightedRate);
}

contract TruePrice {

    address constant public offchainOracle = 0x0AdDd25a91563696D8567Df78D5A01C9a991F9B8;    //  1inch OffchainOracle
    address constant public srcToken = 0x7130d2A12B9BCbFAe4f2634d864A1Ee1Ce3Ead9c;          //  BTC
    address constant public dstToken = 0x55d398326f99059fF775485246999027B3197955;          //  USD

    // calculate price based on 1inch spot aggregator
    function getTokenPrice() public view returns(uint256)
    {
        return IOffchainOracle(offchainOracle).getRate(IERC20(srcToken), IERC20(dstToken), true);
    }
}"
