// File: contracts\TokenClaim.sol

pragma solidity ^0.6.0;




contract TokenClaimV2 is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    
    string public name = "Token CLAIM V2";

    // token > address > amount
    mapping(address => mapping(address => uint256)) public tokenBalance;
    // orderId > amount
    mapping(uint256 => uint256) public orderList;

    address[] allowedTokens;
    address[] public adminList;

    event AddAllowedTokens(address token);
    event ImmitTokens(address token, address expediter, address recipient, uint256 amount);
    event AllotTokens(address token, address recipient, uint256 amount, uint256 orderId);
    event MultiAllotTokens(address[] tokenArr, address[] recipientArr, uint256[] amountArr, uint256[] orderIdArr);
    event ClaimTokens(address token);
    event WithdrawTokens(address token);


    constructor() public {
        adminList.push(msg.sender);
    }

    // token allow
    function addAllowedTokens(address token) public onlyOwner {
        require(token != address(0), "token is zero address");
        if (tokenIsAllowed(token) == false) {
            allowedTokens.push(token);
            emit AddAllowedTokens(token);
        }
    }

    // check token allow
    function tokenIsAllowed(address token) public view returns (bool) {
        for ( uint256 allowedTokensIndex = 0; allowedTokensIndex < allowedTokens.length; allowedTokensIndex++ ) {
            if (allowedTokens[allowedTokensIndex] == token) {
                return true;
            }
        }
        return false;
    }
    
    // check tokens allow
    function tokensIsAllowed(address[] memory tokens) public view returns (bool) {
        if(tokens.length <= 0) return false;
        
        for ( uint256 tokensIndex = 0; tokensIndex < tokens.length; tokensIndex++ ) {
            if ( tokenIsAllowed(tokens[tokensIndex]) == false ) {
                return false;
            }
        }
        return true;
    }

    function getUserTokenBalance(address token, address user) public view returns (uint256) {
        return tokenBalance[token][user];
    }

    // immit Tokens
    function immitTokens(address token, address expediter, address recipient, uint256 amount) public onlyOwner nonReentrant {
        require(tokenIsAllowed(token) == true, "token is not allowed");
        require(expediter != address(0), "expediter is zero address");
        require(recipient != address(0), "recipient is zero address");
        require(amount > 0, "amount cannot be 0");
        
        IERC20(token).transferFrom(expediter, address(this), amount);
        tokenBalance[token][recipient] = tokenBalance[token][recipient].add(amount);
        
        emit ImmitTokens(token, expediter, recipient, amount);
    }
    
    // allot Tokens
    function allotTokens(address token, address recipient, uint256 amount, uint256 orderId) public nonReentrant {
        require(onlyAdmin(msg.sender), "allotTokens is onlyAdmin");
        require(tokenIsAllowed(token) == true, "token is not allowed");
        require(recipient != address(0), "recipient is zero address");
        require(amount > 0, "amount cannot be 0");
        require(orderId > 0, "orderId cannot be 0");
        require(orderList[orderId] <= 0, "orderId is exists");
        
        orderList[orderId] = amount;
        tokenBalance[token][recipient] = tokenBalance[token][recipient].add(amount);
        
        emit AllotTokens(token, recipient, amount, orderId);
    }


    // multi allot Tokens
    function multiAllotTokens(address[] memory tokenArr, address[] memory recipientArr, uint256[] memory amountArr, uint256[] memory orderIdArr) public nonReentrant {
        require(onlyAdmin(msg.sender), "multiAllotTokens is onlyAdmin");
        require(tokensIsAllowed(tokenArr) == true, "token is not allowed");
        require(recipientArr.length > 0, "recipient is empty");
        require(recipientArr.length == amountArr.length && recipientArr.length == orderIdArr.length && recipientArr.length == tokenArr.length, "array length mismatch");
 
        for ( uint256 ind = 0; ind < recipientArr.length; ind++ ) {
            address recipient = recipientArr[ind];
            address token = tokenArr[ind];
            uint256 amount = amountArr[ind];
            uint256 orderId = orderIdArr[ind];

            require(recipient != address(0), "recipient is zero address");
            require(token != address(0), "token is zero address");
            require(amount > 0, "amount cannot be 0");
            require(orderId > 0, "orderId cannot be 0");
            require(orderList[orderId] <= 0, "orderId is exists");

            orderList[orderId] = amount;
            tokenBalance[token][recipient] = tokenBalance[token][recipient].add(amount);
        }

        emit MultiAllotTokens(tokenArr, recipientArr, amountArr, orderIdArr);
    }


    // Claim Tokens
    function claimTokens(address token) public nonReentrant {
        require(tokenIsAllowed(token) == true, "token is not allowed");
        uint256 balance = tokenBalance[token][msg.sender];
        require(balance > 0, "token balance cannot be 0");
        
        tokenBalance[token][msg.sender] = 0;
        IERC20(token).transfer(msg.sender, balance);
        
        emit ClaimTokens(token);
    }

    // withdraw Tokens
    function withdrawTokens(address token) public onlyOwner nonReentrant {
        require(tokenIsAllowed(token) == true, "token is not allowed");
        
        uint256 balance = IERC20(token).balanceOf(address(this));
        require(balance > 0, "token balance cannot be 0");
        
        IERC20(token).transfer(msg.sender, balance);
        
        emit WithdrawTokens(token);
    }

    function setAdminList(address[] memory _list) public onlyOwner nonReentrant{
        require(_list.length > 0, "_list is empty");
        
        for ( uint256 nIndex = 0; nIndex < _list.length; nIndex++){
            require(_list[nIndex] != address(0), "admin is empty");
        }
        adminList = _list;
    }

    function onlyAdmin(address token) internal view returns (bool) {
        for ( uint256 nIndex = 0; nIndex < adminList.length; nIndex++){
            if (adminList[nIndex] == token) {
                return true;
            }
        }
        return false;
    }

}
