    "/contracts/VisaMeta.sol": {
      "content": "pragma solidity ^0.8.11;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract VisaMeta is Context, IERC20, Ownable {
    struct LockBalance {
        uint256 Amount;
        uint256 UnlockTime;
    }
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    uint8 public _decimals;
    string public _symbol;
    string public _name;
    uint256 public transferFee;
    bool isAntibotModeEnabled = true;
    address public airdropContract;
    mapping(address => bool) antibotModeWhitelist;
    mapping(address => LockBalance[]) public locks;
    constructor() public {
        _name = "VisaMetaFi";
        _symbol = "VISA";
        _decimals = 18;
        _totalSupply = 999999999999 ether;
        _balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    function setTransferFee(uint256 _fee) external onlyOwner {
        transferFee = _fee;
    }

    function getOwner() external view returns (address) {
        return owner();
    }

    function decimals() external view returns (uint8) {
        return _decimals;
    }

    function symbol() external view returns (string memory) {
        return _symbol;
    }

    function name() external view returns (string memory) {
        return _name;
    }

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account)
        external
        view
        override
        returns (uint256)
    {
        return _balances[account];
    }

    function setAntibotMode(bool status) public onlyOwner {
        isAntibotModeEnabled = status;
    }

    function setAirdropContract(address _airdropContract) public onlyOwner {
        airdropContract = _airdropContract;
    }

    function setAntibotModeWhitelist(
        address[] memory toAddAddesses,
        address[] memory toRemoveAddesses
    ) public onlyOwner {
        for (uint256 i = 0; i < toAddAddesses.length; i++)
            antibotModeWhitelist[toAddAddesses[i]] = true;
        for (uint256 i = 0; i < toRemoveAddesses.length; i++)
            antibotModeWhitelist[toRemoveAddesses[i]] = false;
    }

    function antibotModeCheck(address from, address to) private view {
        if (!isAntibotModeEnabled) return;
        if (from == owner() || from == airdropContract) return;
        require(
            antibotModeWhitelist[from] && antibotModeWhitelist[to],
            "Address not in antibot mode whitelist"
        );
    }

    function transfer(address recipient, uint256 amount)
        external
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        external
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        external
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function mint(uint256 amount) public onlyOwner returns (bool) {
        _mint(_msgSender(), amount);
        return true;
    }

    function burn(uint256 amount) public returns (bool) {
        _burn(_msgSender(), amount);
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        antibotModeCheck(sender, recipient);
        uint256 totalLock = 0;
        for (uint256 i = 0; i < locks[sender].length; i++) {
            if (locks[sender][i].UnlockTime >= block.timestamp) {
                totalLock = totalLock.add(locks[sender][i].Amount);
            }
        }
        require(
            _balances[sender].sub(totalLock) >= amount,
            "ERC20: transfer amount exceeds unlocked balance"
        );
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        _balances[sender] = _balances[sender].sub(
            amount,
            "ERC20: transfer amount exceeds balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);

        emit Transfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: burn from the zero address");

        _balances[account] = _balances[account].sub(
            amount,
            "ERC20: burn amount exceeds balance"
        );
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _burnFrom(address account, uint256 amount) internal {
        _burn(account, amount);
        _approve(
            account,
            _msgSender(),
            _allowances[account][_msgSender()].sub(
                amount,
                "ERC20: burn amount exceeds allowance"
            )
        );
    }

    function lockBalance(
        address _account,
        uint256 _amount,
        uint256 _unlockTime
    ) external {
        require(msg.sender == airdropContract, "Invalid sender");

        for (uint256 i = 0; i < locks[_account].length; i++) {
            if (locks[_account][i].UnlockTime == _unlockTime) {
                locks[_account][i].Amount = locks[_account][i].Amount.add(
                    _amount
                );
                return;
            }
        }
        LockBalance memory lock = LockBalance(_amount, _unlockTime);
        locks[_account].push(lock);
    }
}
"
