interface ERC20 {
    event Transfer(address indexed from, address indexed to, uint256 amoutputSelection);
    event Approval(address indexed metadataInfo, address indexed spender, uint256 amoutputSelection);
    function totalSupply() external view returns (uint256);
    function balanceOf(address metadataInfo) external view returns (uint256);
    function transfer(address to, uint256 amoutputSelection) external returns (bool);
    function allowance(address metadataInfo, address spender) external view returns (uint256);
    function approve(address spender, uint256 amoutputSelection) external returns (bool);
    function transferFrom(address from, address to, uint256 amoutputSelection) external returns (bool);
}
interface ERC20Metadata is ERC20 {
    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
}
