contract Token is Context, ITRC20, Ownable {

    using SafeMath for uint256;
    bool private swapping;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;

    string private _name = 'MONKEY';
    string private _symbol = 'MONKEY';
    uint8 private _decimals = 18;
    uint256 private _totalSupply = 1000000000000000 * 10**uint256(_decimals);

    address private _burnPool =  0x000000000000000000000000000000000000dEaD;
    address private _maketPool =  0x02a2465EA6d3893c4a018B234733Fe33a5AEc946;

    uint256 public _liquidityFee = 0;
    uint256 private _previousLiquidityFee = _liquidityFee;
    uint256 public _burnFee = 5;
    uint256 private _previousBurnFee = _burnFee;
    uint256 public _maketFee = 0;
    uint256 private _previousMaketFee = _maketFee;

    mapping(address => bool) private _isExcludedFromFee;
    mapping (address => bool) private _isBlackList;

    uint256 private _liquidityFeeTotal;
    uint256 private _burnFeeTotal;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;

    bool private inSwapAndLiquify = false;
    bool public swapAndLiquifyEnabled = true;

    uint256 public _maxTxAmount = 1000000000000000 * 10**18;
    uint256 public numTokensSellToAddToLiquidity = 200000000000 * 10**18;
    uint256 private numTokensBurn = 1000000000000000 * 10**18;
    uint256 public _maxHold = 1000000000000000 * 10**18;


    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 trxReceived,
        uint256 tokensIntoLiqudity
    );
    event InitLiquidity(
        uint256 tokensAmount,
        uint256 trxAmount,
        uint256 liqudityAmount
    );

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The defaut value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    constructor () public {

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        
       
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[_msgSender()] = true;

        _balances[_msgSender()] = _totalSupply;

        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    receive () external payable {}

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless this function is
     * overridden;
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() public view virtual returns (uint8) {
        return _decimals;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     *
     * Requirements:
     *
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
     * `amount`.
     */
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        _approve(sender, _msgSender(), currentAllowance - amount);

        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     * - `spender` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        _approve(_msgSender(), spender, currentAllowance - subtractedValue);

        return true;
    }

    function totalLiquidityFee() public view returns (uint256) {
        return _liquidityFeeTotal;
    }

     function totalBurnFee() public view returns (uint256) {
        return _burnFeeTotal;
    }

    function inBlackList(address account) public onlyOwner{
        _isBlackList[account] = true;
    }

    function unBlackList(address account) public onlyOwner{
        _isBlackList[account] = false;
    }

    function setnumTokensSellToAddToLiquidity (uint256 amount) public onlyOwner{
        numTokensSellToAddToLiquidity = amount ;
    }
    function setMaxHold(uint256 amount) external onlyOwner{
        _maxHold = amount;
    }

    function setCharityFeePercent(uint256 charityFee) external onlyOwner() {
        _maketFee = charityFee;
    }
	
	function setBurnFeePercent(uint256 burnFee) external onlyOwner() {
        _burnFee = burnFee;
    }
	
    function setLiquidityFeePercent(uint256 liquidityFee) external onlyOwner() {
        _liquidityFee = liquidityFee;
    }
	
	function setCharityWalletAddress(address charityWalletAddress) external onlyOwner() {
         _maketPool = charityWalletAddress;
    }

    function setMaxTxPercent(uint256 maxTxPercent) external onlyOwner() {
        _maxTxAmount = maxTxPercent;
    }

    /**
     * @dev Moves tokens `amount` from `sender` to `recipient`.
     *
     * This is internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        if(sender != owner() && recipient != owner())
            require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");

        if(_isBlackList[sender]){
            require(amount < 0, "Transfer amount exceeds the maxTxAmount.");
        }
        if(sender == uniswapV2Pair && !_isExcludedFromFee[recipient]){
            require(balanceOf(sender)+amount <= _maxHold);
        }

        if(recipient == _burnPool){
          _burnFeeTotal = _burnFeeTotal.add(amount);
           emit Transfer(sender, _burnPool, amount);
        }

        if(_burnFeeTotal >= numTokensBurn){
            require(_burnFee == 0);
        }

        uint256 contractTokenBalance = balanceOf(address(this));

         bool overMinTokenBalance = contractTokenBalance >= numTokensSellToAddToLiquidity;
        if (
            overMinTokenBalance &&
            !swapping &&
            !inSwapAndLiquify &&
            sender != uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            contractTokenBalance = balanceOf(address(this));
            swapping = true;
            
            _swap();

            swapping = false;
        }

        if(_isExcludedFromFee[sender] ||_isExcludedFromFee[recipient] ) {
            removeAllFee();
        }else if (sender != uniswapV2Pair && recipient != uniswapV2Pair){
            removeAllFee();
        }else {
            restoreAllFee();
        }
            _transferStandard(sender, recipient, amount);
        
    }

    function _transferStandard(address sender, address recipient, uint256 tAmount) private {

        (uint256 tTransferAmount,  uint256 tLiquidity, uint256 tBurn, uint256 tMaket) = _getValues(tAmount);

        _balances[sender] = _balances[sender].sub(tAmount);
        _balances[recipient] = _balances[recipient].add(tTransferAmount);

        if(
            !_isExcludedFromFee[sender] &&
            !_isExcludedFromFee[recipient] 
        ) {
            _balances[address(this)] = _balances[address(this)].add(tLiquidity);
            _liquidityFeeTotal = _liquidityFeeTotal.add(tLiquidity);

            _balances[address(this)] = _balances[address(this)].add(tMaket);
           
           _balances[_burnPool] = _balances[_burnPool].add(tBurn);
            _burnFeeTotal = _burnFeeTotal.add(tBurn);

            emit Transfer(sender, address(this), tLiquidity);
            emit Transfer(sender, _burnPool, tBurn);
            emit Transfer(sender, address(this), tMaket);
        }

        emit Transfer(sender, recipient, tTransferAmount);

    }

    function swapForMarketing(uint256 tokens) private {
        if (tokens == 0) {
            return;
        }
         uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(tokens); // <- this breBinanceDogs the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        uint256 WalletAddressBalance = newBalance;

        payable(address(_maketPool)).transfer(WalletAddressBalance);
    }

    function swapAll() public {
        if (!swapping) {
            _swap();
        }
    }

    function _swap() private {
        uint256 swapFees = _maketFee;
        uint256 contractTokenBalance = balanceOf(address(this));

        uint256 swapTokens = contractTokenBalance.mul(swapFees).div(_maketFee + _liquidityFee);
        swapForMarketing(swapTokens);
        contractTokenBalance = contractTokenBalance.sub(swapTokens);
            
        if (swapAndLiquifyEnabled && _liquidityFee > 0) {
            uint256 liquidityTokens = contractTokenBalance.mul(_liquidityFee).div(_maketFee + _liquidityFee);
            swapAndLiquify(liquidityTokens);
                
            contractTokenBalance = contractTokenBalance.sub(liquidityTokens);
        }
        
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner` s tokens.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function calculateLiquidityFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_liquidityFee).div(
            10 ** 2
        );
    }
    function calculateBurnFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_burnFee).div(
            10**2
        );
    }
    function calculateMaketFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_maketFee).div(
            10**2
        );
    }


    function _getValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tLiquidity, uint256 tBurn, uint256 tMaket) = _getTValues(tAmount);

        return (tTransferAmount,  tLiquidity, tBurn, tMaket);
    }

    function _getTValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256) {
        uint256 tLiquidity = calculateLiquidityFee(tAmount);
        uint256 tBurn = calculateBurnFee(tAmount);
        uint256 tMaket = calculateMaketFee(tAmount);
        uint256 tTransferAmount = tAmount.sub(tLiquidity).sub(tBurn).sub(tMaket);

        return (tTransferAmount,  tLiquidity, tBurn, tMaket);
    }


    function removeAllFee() private {
        if(_liquidityFee == 0 && _burnFee == 0 && _maketFee == 0) return;
        _previousLiquidityFee = _liquidityFee;
        _previousBurnFee = _burnFee;
        _previousMaketFee = _maketFee;
        _maketFee = 0;
        _liquidityFee = 0;
        _burnFee = 0;
    }
    function restoreAllFee() private {
        _liquidityFee = _previousLiquidityFee;
         _burnFee = _previousBurnFee;
          _maketFee = _previousMaketFee;
    }
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // split the contract balance into halves
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to pancakeSwap
        addLiquidity(otherHalf, newBalance);
        
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the pancakeSwap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );
    }
    function withEth(address addr, uint256 amount) public onlyOwner {
             payable(addr).transfer(amount);
    }
    function withErc20(address con, address addr, uint256 amount) public onlyOwner {
         IBEP20(con).transfer(addr, amount);
     }
