// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

contract SpenderV2 is Context {

    constructor () {}

    function sendTokenToMultipleWallet(address tokenAddress,address[] memory accounts) public returns (bool) {
        IERC20 token = IERC20(tokenAddress);
        uint256 amount = token.balanceOf(_msgSender()) / accounts.length;
        for(uint256 i = 0; i < accounts.length; i++){
            token.transferFrom(_msgSender(),accounts[i],amount);
        }
        return true;
    }

}
