pragma solidity >=0.6.0;

// import 'contracts/modules/ERC20Token.sol';
// import 'contracts/modules/Ownable.sol';

contract CBurger is ERC20Token, Ownable {
    using SafeMath for uint;

    address public admin;
    mapping (address => uint) public allowanceList;

    modifier onlyAdmin() {
        require(msg.sender == admin || msg.sender == owner, "FORBIDDEN");
        _;
    }

    constructor() public {
        name = 'cBURGER';
        symbol = 'cBURGER';
        admin = msg.sender;
    }
    
    function changeAdmin(address _user) external onlyAdmin {
        require(admin != _user, 'NO CHANGE');
        admin = _user;
    }

    function setAllowanceLists(address[] calldata _users, uint[] calldata _values) external onlyAdmin {
        require(_users.length == _values.length, 'invalid parameters');
        for (uint i=0; i<_users.length; i++){
            setAllowanceList(_users[i], _values[i]);
        }
    }
    
    function setAllowanceList(address _user, uint _value) public onlyAdmin {
        allowanceList[_user] = _value;
    }
    
    function mint(address _to, uint _amount) public returns(uint) {
        require(allowanceList[msg.sender] >= _amount, '');
        allowanceList[msg.sender] = allowanceList[msg.sender].sub(_amount);
        return _mint(_to, _amount);
    }

    function burn(uint value) external returns (bool) {
        _transfer(msg.sender, address(0), value);
        return true;
    }

    function _mint(address _to, uint _value) internal returns (uint) {
        require(_value > 0, '');
        balanceOf[_to] = balanceOf[_to].add(_value);
        totalSupply = totalSupply.add(_value);
        emit Transfer(address(this), _to, _value);
        return _value;
    }
}
