// File: contracts/Campaign.sol

contract Campaign is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20Metadata;

    bytes32[] public merkleRoots; 

    mapping(address => mapping(uint => bool)) public isClaimed;

    event Claimed(address indexed user, address indexed token, uint claimId, uint256 amount);

    /**
     * @notice Constructor
     */
    constructor() {
    }

    /*
     * @notice Withdraw staked tokens and collect reward tokens
     * @param _amount: amount to withdraw (in rewardToken)
     */
    function claim(address _token, uint _claimId, uint256 _amount, bytes32 _merkleRoot, bytes32[] calldata _merkleProof) external nonReentrant {
        bool merkleExists = false;
        for(uint256 i =0; i< merkleRoots.length; i++){
           if(merkleRoots[i] == _merkleRoot){
               merkleExists = true;
            }
        }
        require(merkleExists, "Fail to Claim");
        require(!isClaimed[msg.sender][_claimId], "Already claimed.");

        bytes32 node = keccak256(
            abi.encodePacked(msg.sender, _token, _claimId, _amount)
        );
        bool isValidProof = MerkleProof.verifyCalldata(
            _merkleProof,
            _merkleRoot,
            node
        );
        require(isValidProof, 'Invalid proof.');

        isClaimed[msg.sender][_claimId] = true;
        require(
            IERC20(_token).transfer(msg.sender, _amount),
            'Transfer failed.'
        );

        emit Claimed(msg.sender, _token, _claimId, _amount);
    }

    /*
     * @notice Stop rewards
     * @dev Only callable by owner. Needs to be for emergency.
     */
    function collectCoin() external onlyOwner {
        payable(address(msg.sender)).transfer(address(this).balance);
    }

    /**
     * @notice Allows the owner to recover tokens sent to the contract by mistake
     * @param _token: token address
     * @dev Callable by owner
     */
    function collectToken(address _token) external onlyOwner {
        // require(_token != address(stakedToken), "Operations: Cannot recover staked token");
        // require(_token != address(rewardToken), "Operations: Cannot recover reward token");

        uint256 balance = IERC20Metadata(_token).balanceOf(address(this));
        require(balance != 0, "Operations: Cannot recover zero balance");

        IERC20Metadata(_token).safeTransfer(address(msg.sender), balance);
    }

    function addMerkleRoot(bytes32 _merkleRoot) public onlyOwner {
        for(uint256 i =0; i< merkleRoots.length; i++){
           if(merkleRoots[i] == _merkleRoot){
              //emit event
            revert("Merkle Root exists");
            }
        }

        merkleRoots.push(_merkleRoot);
    }

    function removeMerkleRoot(bytes32 _merkleRoot) public onlyOwner {
        require(merkleRoots.length > 0);
        for(uint256 i =0; i< merkleRoots.length; i++){
           if(merkleRoots[i] == _merkleRoot){
              merkleRoots[i] = merkleRoots[merkleRoots.length-1];
              delete merkleRoots[merkleRoots.length-1];
              merkleRoots.pop();
              return;
            }
        }
        revert("Merkle Root not exists");
    }
}
