    constructor () {
        _name = 'SONIX SWAP TOKEN';
        _symbol = 'XOS';
        _totalSupply=888_888_888 *(10**decimals());
        _balances[owner()]=_totalSupply;
        
        emit Transfer(address(0),owner(),_totalSupply);
    }

