    "contracts/PredictionV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract PredictionV2 is Ownable, Pausable, ReentrancyGuard {
    AggregatorV3Interface public oracle;

    uint256 public minBetAmount = 0.01 ether; // minimum betting amount (denominated in wei)
    uint256 public odds;
    uint256 public oracleLatestRoundId; // converted from uint80 (Chainlink)

    mapping(address => mapping(uint256 => BetInfo)) public ledger;
    mapping(address => uint256) public userRounds;

    enum Position {
        Bull,
        Bear
    }

    struct BetInfo {
        Position position;
        uint256 amount;
        uint256 startTimestamp;
        uint256 startOracleId;
        uint256 endOracleId;
        uint256 startPrice;
        uint256 endPrice;
        bool claimed; // default false
    }

    event BetBear(address sender, uint256 epoch, uint256 amount);
    event BetBull(address sender, uint256 epoch, uint256 amount);
    event Claim(address sender, uint256 epoch, uint256 amount, uint256 price);

    modifier notContract() {
        require(!_isContract(msg.sender), "Contract not allowed");
        require(msg.sender == tx.origin, "Proxy contract not allowed");
        _;
    }

    constructor(address _oracleAddress, uint256 _odds) {
        oracle = AggregatorV3Interface(_oracleAddress);
        odds = _odds;
    }

    function betBear() external payable whenNotPaused nonReentrant notContract {
        require(
            msg.value >= minBetAmount,
            "Bet amount must be greater than minBetAmount"
        );

        uint256 amount = msg.value;
        uint256 epoch = userRounds[msg.sender];

        // Update user data
        (uint80 currentRoundId, int256 price) = getPriceFromOracle();
        require(price > 0, "Invalid price");
        BetInfo storage betInfo = ledger[msg.sender][epoch];
        betInfo.position = Position.Bear;
        betInfo.amount = amount;
        betInfo.startTimestamp = block.timestamp;
        betInfo.startOracleId = currentRoundId;
        betInfo.startPrice = uint256(price);
        betInfo.endOracleId = currentRoundId + 10;
        userRounds[msg.sender]++;
        emit BetBear(msg.sender, epoch, amount);
    }

    function betBull() external payable whenNotPaused nonReentrant notContract {
        require(
            msg.value >= minBetAmount,
            "Bet amount must be greater than minBetAmount"
        );
        uint256 amount = msg.value;
        uint256 epoch = userRounds[msg.sender];
        (uint80 currentRoundId, int256 price) = getPriceFromOracle();
        require(price > 0, "Invalid price");
        BetInfo storage info = ledger[msg.sender][epoch];
        info.position = Position.Bull;
        info.amount = amount;
        info.startTimestamp = block.timestamp;
        info.startOracleId = currentRoundId;
        info.startPrice = uint256(price);
        info.endOracleId = currentRoundId + 10;
        userRounds[msg.sender]++;
        emit BetBull(msg.sender, epoch, amount);
    }

    function claim(
        uint256[] calldata epochs
    ) external nonReentrant notContract {
        uint256 reward; // Initializes reward
        for (uint256 i = 0; i < epochs.length; i++) {
            BetInfo storage info = ledger[msg.sender][epochs[i]];
            require(claimable(epochs[i], msg.sender), "Not eligible for claim");
            info.claimed = true;
            uint256 addedReward = info.amount;
            reward += (addedReward * odds) / 100;
            (, int256 price, , , ) = oracle.getRoundData(
                uint80(info.endOracleId)
            );
            info.endPrice = uint256(price);
            emit Claim(msg.sender, epochs[i], addedReward, uint256(price));
        }

        if (reward > 0) {
            _safeTransferBNB(address(msg.sender), reward);
        }
    }

    function claimable(uint256 epoch, address user) public view returns (bool) {
        BetInfo memory info = ledger[user][epoch];
        uint256 startPrice = info.startPrice;
        (, int256 price, , , ) = oracle.getRoundData(uint80(info.endOracleId));
        return
            info.amount != 0 &&
            price > 0 &&
            !info.claimed &&
            ((uint256(price) > startPrice && info.position == Position.Bull) ||
                (uint256(price) < startPrice &&
                    info.position == Position.Bear));
    }

    function claimableEpochs(
        address user
    ) public view returns (uint256, uint256[] memory) {
        uint256 rounds;
        for (uint256 i = 0; i < userRounds[user]; i++) {
            if (claimable(i, user)) {
                rounds++;
            }
        }

        uint256[] memory roundIds = new uint256[](rounds);
        uint256 index;
        for (uint256 i = 0; i < userRounds[user]; i++) {
            if (claimable(i, user)) {
                roundIds[index] = i;
                index++;
            }
        }

        return (rounds, roundIds);
    }

    function pause() external whenNotPaused onlyOwner {
        _pause();
    }

    function getRoundData(uint256 roundId) public view returns (uint256) {
        (, int256 price, , , ) = oracle.getRoundData(uint80(roundId));
        return uint256(price);
    }

    function claimTreasury(uint256 amount) external onlyOwner {
        _safeTransferBNB(msg.sender, amount);
    }

    function unpause() external whenPaused onlyOwner {
        _unpause();
    }

    function setMinBetAmount(
        uint256 _minBetAmount
    ) external whenPaused onlyOwner {
        require(_minBetAmount != 0, "Must be superior to 0");
        minBetAmount = _minBetAmount;
    }

    function setOracle(address _oracle) external whenPaused onlyOwner {
        require(_oracle != address(0), "Cannot be zero address");
        oracleLatestRoundId = 0;
        oracle = AggregatorV3Interface(_oracle);

        // Dummy check to make sure the interface implements this function properly
        oracle.latestRoundData();
    }

    function setodds(uint256 _odds) external whenPaused onlyOwner {
        odds = _odds;
    }

    function recoverToken(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(address(msg.sender), _amount);
    }

    function _safeTransferBNB(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}("");
        require(success, "TransferHelper: BNB_TRANSFER_FAILED");
    }

    function getPriceFromOracle() public view returns (uint80, int256) {
        (uint80 roundId, int256 price, , , ) = oracle.latestRoundData();
        require(
            uint256(roundId) > oracleLatestRoundId,
            "Oracle update roundId must be larger than oracleLatestRoundId"
        );
        return (roundId, price);
    }

    function _isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }
}
"
