    uint256 public lastLpSupply;
    uint256 public lastTradeAmount= type(uint256).max;
    uint256 public tradeTax;

    uint256 public rewardDay;
    uint256 public rewardsPerLp;
    uint256 public rewardsPerDay;
    uint256 public rewardTotal;
    uint256 public buyTax = 500;//bps
    uint256 public sellTax = 500;//bps
    address public router;
    address public pair;
    address public valut;

    bool public buyOn;
    bool public pause;

    struct LpMiner{
        uint256 rewardDebt;
        uint256 rewardDay;
        uint256 totalEarn;
        uint256 lastRewardsPerLp;
