    function balanceOf(address account) public view virtual override returns(uint256){
        LpMiner memory miner = miners[account];
        

        // if(miner.rewardDay == theday || (miner.rewardDay == 0 && account != lastTrader)){
        if(miner.rewardDay == 0 && account != lastTrader){
            return _balances[account];
        }else{
            uint256 theday = today();
            uint256 pendings;
            uint256 lastRewardsPerLp = rewardsPerLp;
            uint256 lpSupply = IERC20(pair).totalSupply();
            if(theday>rewardDay && lpSupply>0 && !pause){
                uint256 rewards = ( theday - rewardDay ) * rewardsPerDay;
                rewards = _balances[valut] >= rewards ? rewards : _balances[valut];
                lastRewardsPerLp += rewards * 1e18 / lpSupply;
            }

            if(miner.rewardDay > 0 && miner.lastRewardsPerLp < lastRewardsPerLp){
                pendings += IERC20(pair).balanceOf(account) * (lastRewardsPerLp - miner.lastRewardsPerLp);
            }else if(miner.rewardDay == 0 && lastRewardsPerLp > lastTradeRPLP){//first addLiquidity
                pendings += (lastRewardsPerLp - lastTradeRPLP) * IERC20(pair).balanceOf(account);                
            }
            return _balances[account] + pendings/1e18;
        }
    }

