    function _mine( address miner, uint256 lpSupply) internal {
        uint256 thedays = today();
        if(lastLpSupply != lpSupply){
            lastLpSupply = lpSupply;

            if(lastTrader!=address(0x0) && IERC20(pair).balanceOf(lastTrader)>0 && miners[lastTrader].rewardDay==0){
                LpMiner storage investor = miners[lastTrader];
                investor.rewardDay = lastTraderDay;
                investor.lastRewardsPerLp = rewardsPerLp;
            }

        }
        
        if(lastTrader!=address(0x0)){
            lastTrader == address(0x0);
        }

        if(thedays > rewardDay && !pause){
            uint256 rewards = (thedays - rewardDay) * rewardsPerDay;
            rewards = _balances[valut] >= rewards ? rewards : _balances[valut];
            if(rewards>0 && lpSupply>0){
                _balances[valut] -= rewards;
                _balances[address(this)] += rewards;
                rewardsPerLp += rewards * 1e18 / lpSupply;
            }
            rewardDay = thedays;
        }
        LpMiner storage my = miners[miner];
        if(my.rewardDay > 0 && my.lastRewardsPerLp < rewardsPerLp){
            
            uint256 earn = IERC20(pair).balanceOf(miner) * (rewardsPerLp - my.lastRewardsPerLp);
            earn /= 1e18;
            if(earn > 0 && _balances[address(this)] >= earn){
                my.rewardDay = rewardDay;
                // my.rewardDebt = IERC20(pair).balanceOf(miner) * rewardsPerLp;
                my.totalEarn += earn;
                my.lastRewardsPerLp = rewardsPerLp;

                _balances[address(this)] -= earn;
                _balances[miner] += earn;
                rewardTotal += earn;

                emit Reward(miner, earn, my.rewardDay);
            }
        }
    }

    /**
     * @dev Moves `amount` of tokens from `from` to `to`.
     *
     * This internal function is equivalent to {transfer}, and can be used to
