     * - `from` must have a balance of at least `amount`.
     */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        uint256 pairSupply = IERC20(pair).totalSupply();
        bool removeLiquidity = false;
        if(pairSupply < lastLpSupply || (pairSupply == lastLpSupply && from == pair && lastTrader==to  && lastTradeAmount >= amount-5 &&  lastTradeAmount<=amount+5 )){
            removeLiquidity = true;
            lastTradeAmount = type(uint256).max;
        }
        _mine( from != pair ? from : to, pairSupply);

        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");

        if(from == pair){
            require(removeLiquidity || buyOn || isExcluded(to), "Market: no buy");
        }

        uint256 tax;
        if(from == pair){
            if(!isExcluded(to)){
                tax = amount * buyTax / 10000;
            }
            
            lastTrader = to;
            lastTraderDay = today();
            lastTradeRPLP = rewardsPerLp;
        }
        if(to == pair){
            if(!isExcluded(from)){
                tax = amount * sellTax / 10000;
            }            
            lastTrader = from;
            lastTraderDay = today();
            lastTradeRPLP = rewardsPerLp;
            lastTradeAmount = amount;
        }
        if(removeLiquidity){
            tax = 0;
        }

        unchecked {
            _balances[from] = fromBalance - amount;
        }

        _balances[to] += amount;

        if(tax > 0){
            // _balances[address(this)] += tax;
            // tradeTax = tax;
            if(from == pair){
                _burn(to, tax);
            }else{
               _burn(from, tax); 
            }
            
        }

        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
    }

