contract JOYLINK is ERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;
    using SafeERC20 for IERC20;

    IUniswapV2Router02 public uniswapV2Router;
    // IUniswapV2Router02 public uniswapV2RouterJF;
    address public uniswapV2Pair;
    // address public usdt = 0x55d398326f99059fF775485246999027B3197955;
    // address public usdt = 0xa71EdC38d189767582C38A3145b5873052c3e47a;
    bool private swapping;
    bool public iniMint = true;
    address public FormerJolin =0xD3f0c10Aac71994CCEE2bEaf347a41FAA0fDCB82;
    address public rewardToken = 0xbA2aE424d960c26247Dd6c32edC70B295c744C43;
    uint256 public swapTokensAtAmount;
    uint256 public inviteAmount = 10 ** 10 ;
    uint256 public buyAmount = 10 ** 18 ;
    uint256 public liquidFee = 5;
    uint256 public burnFee = 1;
    uint256 public rewardFee = 4;
    uint256 public inviteFee = 5;
    uint256 public pidLength = 4;
    uint256 public startTime;
    bool public liqudityOpen =  true ;

    address public _burnAddress = 0x000000000000000000000000000000000000dEaD;
    // address public _mintAddress = 0x8Ca13BB2824E17c90507d2f7DDA1Ab1076E7a7D2;
    address public _mintAddress = 0x16Fe30254c944a3bc22B21d9F0003B3788EF4711;
    address public _walletAddress = 0x509788030814D517FfcA1709D49e53Ba29C60791;
    bool public divideOpen = true;

    // exlcude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => address) public inviter;
    mapping(address => bool) public _blackList;
    mapping(address => uint256) public joyLevel;
    mapping(address => uint256) public jolinLimit;
    mapping(address => uint256) public inviterNum;
    mapping(address => bool) public inviteStatus;
    mapping(uint256 => uint256) public DURATION;
    mapping(uint256 => uint256) public Hi;
    mapping(uint256 => uint256) public Low;
    mapping(uint256 => uint256) public initreward;
    mapping(uint256 => uint256) public periodFinish;
    mapping(uint256 => uint256) public rewardRate;
    mapping(uint256 => uint256) public lastUpdateTime;
    mapping(uint256 => uint256) public rewardPerTokenStored;
    mapping(uint256 => uint256) public totalStakeSupply;
    mapping(uint256 => address) public rToken;
    mapping(address=>mapping(uint256 => uint256)) public  userRewardPerTokenPaid;
    mapping(address=>mapping(uint256 => uint256)) public  rewards;
    mapping(address=>mapping(uint256 => uint256)) public  stakeBalanceOf;
    mapping(address=>mapping(uint256 => bool)) public  stakeStatus;



    event UpdateUniswapV2Router(
        address indexed newAddress,
        address indexed oldAddress
    );

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event LiquidityWalletUpdated(
        address indexed newLiquidityWallet,
        address indexed oldLiquidityWallet
    );
    event RewardPaid(address acc_,uint256 amount_);
    event TokenCreated(address owner_, address tokenaddr_);
    event RewardAdded(uint256 amount_);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    constructor( uint256 time_
        // string memory name_,
        // string memory symbol_,
        // uint256 totalSupply_,
        // address[3] memory addrs, // router, routerjf, dividendTracker
        // uint256 minimumTokenBalanceForDividends_
        // address serviceFeeReceiver_,
        // uint256 serviceFee_
    ) payable ERC20("NEW JOYLINK TOKEN", "JOLIN") {
        startTime = time_;
        swapTokensAtAmount = 10**25; // 0.01%

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;
        // uniswapV2RouterJF = _uniswapV2RouterJF;
        uniswapV2Pair = _uniswapV2Pair;
        excludeFromFees(owner(), true);
        excludeFromFees(address(this), true);
        excludeFromFees(_mintAddress, true);
        excludeFromFees(_walletAddress, true);
        Hi[0] = 550000 * 10 ** 18;
        Low[0] = 450000 * 10 ** 18;
        Hi[1] = 1550000 * 10 ** 18;
        Low[1] = 1450000 * 10 ** 18;
        Hi[2] = 2550000 * 10 ** 18;
        Low[2] = 2450000 * 10 ** 18;
        Hi[3] = 5050000 * 10 ** 18;
        Low[3] = 4950000 * 10 ** 18;
        DURATION[0] = 1 days;
        DURATION[1] = 1 days;
        DURATION[2] = 1 days;
        DURATION[3] = 1 days;
        emit TokenCreated(owner(), address(this));

        // payable(serviceFeeReceiver_).transfer(serviceFee_);
    }

    receive() external payable {}

    function setSwapTokensAtAmount(uint256 amount) external onlyOwner {
        swapTokensAtAmount = amount;
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(
            newAddress != address(uniswapV2Router),
            "JOLIN: The router already has that address"
        );
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
        address _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), uniswapV2Router.WETH());
        uniswapV2Pair = _uniswapV2Pair;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(
            _isExcludedFromFees[account] != excluded,
            "JOLIN: Account is already the value of 'excluded'"
        );
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(
        address[] calldata accounts,
        bool excluded
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function initialMint(address[] memory accounts) external onlyOwner {
        require(totalSupply() <= 10**29,"totalSupply should less than 10**29");
        for (uint256 i = 0; i < accounts.length; i++) {
            address mintAccount = accounts[i];
            uint256 mintAmount = IERC20(FormerJolin).balanceOf(accounts[i]);
            _mint(accounts[i],mintAmount);
            if(IJOLIN(FormerJolin).inviter(mintAccount) != address(0)){
                inviter[mintAccount] = IJOLIN(FormerJolin).inviter(mintAccount);
            }
        }
    }

    function updateLevel(address _addr) internal {

        if(inviterNum[_addr] >=20){
            joyLevel[_addr] = 3;
            jolinLimit[_addr] = 1000 * 10 ** 8 * 10 ** 18;
        }
        else if(inviterNum[_addr] >=10){
            joyLevel[_addr] = 2;
            jolinLimit[_addr] = 2* 10 ** 6 * 10 ** 18;
        }
        else if(inviterNum[_addr] >=5){
            joyLevel[_addr] = 1;
            jolinLimit[_addr] = 10 ** 6 * 10 ** 18;
        }

    }

    function setinviterNum(address _addr,uint256 value) external onlyOwner {
        inviterNum[_addr] = value;
        updateLevel(_addr);
    }

    function setLiqudityOpen(bool value) external onlyOwner {
        liqudityOpen = value;
    } 

    function setRewardFee(uint256 value) external onlyOwner {
        rewardFee = value;
    }

    function setBurnFee(uint256 value) external onlyOwner {
        burnFee = value;
    }

    function setLiquidFee(uint256 value) external onlyOwner {
        liquidFee = value;
    }

    function setInviter(address _account, address _inviter) external onlyOwner {
        inviter[_account] = _inviter;
    }

    function setDivideOpen(bool value) external onlyOwner {
        divideOpen = value;
    }

    function setInviteFee(uint256 value) external onlyOwner {
        inviteFee = value;
    }

    function setInviteAmount(uint256 value) external onlyOwner {
        inviteAmount = value;
    }

    function setBuyAmount(uint256 value) external onlyOwner {
        buyAmount = value;
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }


    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(!_blackList[from],"JOLIN: transfer from this address is forbidden");
        require(from != address(0), "JOLIN: transfer from the zero address");
        require(to != address(0), "JOLIN: transfer to the zero address");

		if(!Address.isContract(from)&&!Address.isContract(to)&&from != uniswapV2Pair && balanceOf(to) == 0 && inviter[to] == address(0) && amount >= inviteAmount) {
            inviter[to] = from;
        }

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (
            canSwap &&
            !swapping &&
            from != _mintAddress &&
            to != _mintAddress
        ) {
            swapping = true;

            // uint256 marketingTokens = contractTokenBalance
            //     .mul(marketingFee)
            //     .div(totalFees);
            // swapAndSendToFee(marketingTokens);

            uint256 swapTokens = contractTokenBalance.mul(liquidFee).div(liquidFee+rewardFee);
            swapAndLiquify(swapTokens);

           if(divideOpen){ 
            uint256 divideTokens = contractTokenBalance - swapTokens;
            swapAndSendDividends(divideTokens);
           }
            swapping = false;
        }

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if (takeFee) {
        
        if (to != uniswapV2Pair){
        require(balanceOf(to) + amount <= jolinLimit[to] + 10 ** 24, "JOLIN: transfer reach the limit of address");
        }

        uint256 burnAmount = amount.mul(burnFee).div(100);

        if (from == uniswapV2Pair) {
            getReward(from);
            if(!Address.isContract(to) && amount >= buyAmount && !inviteStatus[to] && inviter[to]!= address(0)){
                address invt = inviter[to];
                inviteStatus[to] = true;
                inviterNum[invt] += 1;
                updateLevel(invt);
            }
            if(amount >= Low[0] && amount <= Hi[0])
            {
                uint256 initialLPBalance = IERC20(uniswapV2Pair).balanceOf(address(this));
                super._transfer(from, address(this), amount);
                swapAndStake(amount);
                uint256 stakeLP = IERC20(uniswapV2Pair).balanceOf(address(this)) - initialLPBalance;
                stakeBalanceOf[to][0] += stakeLP;
                totalStakeSupply[0] += stakeLP;
                stakeStatus[to][0] = true;
                return;
            }
            if(amount >= Low[1] && amount <= Hi[1])
            {
                uint256 initialLPBalance = IERC20(uniswapV2Pair).balanceOf(address(this));
                super._transfer(from, address(this), amount);
                swapAndStake(amount);
                uint256 stakeLP = IERC20(uniswapV2Pair).balanceOf(address(this)) - initialLPBalance;
                stakeBalanceOf[to][0] += stakeLP;
                totalStakeSupply[0] += stakeLP;
                stakeBalanceOf[to][1] += stakeLP;
                totalStakeSupply[1] += stakeLP;
                stakeStatus[to][0] = true;
                stakeStatus[to][1] = true;
                return;
            }
            if(amount >= Low[2] && amount <= Hi[2])
            {
                uint256 initialLPBalance = IERC20(uniswapV2Pair).balanceOf(address(this));
                super._transfer(from, address(this), amount);
                swapAndStake(amount);
                uint256 stakeLP = IERC20(uniswapV2Pair).balanceOf(address(this)) - initialLPBalance;
                stakeBalanceOf[to][0] += stakeLP;
                totalStakeSupply[0] += stakeLP;
                stakeBalanceOf[to][1] += stakeLP;
                totalStakeSupply[1] += stakeLP;
                stakeBalanceOf[to][2] += stakeLP;
                totalStakeSupply[2] += stakeLP;
                stakeStatus[to][0] = true;
                stakeStatus[to][1] = true;
                stakeStatus[to][2] = true;
                return;
            }
            if(amount >= Low[3] && amount <= Hi[3])
            {
                uint256 initialLPBalance = IERC20(uniswapV2Pair).balanceOf(address(this));
                super._transfer(from, address(this), amount);
                swapAndStake(amount);
                uint256 stakeLP = IERC20(uniswapV2Pair).balanceOf(address(this)) - initialLPBalance;
                stakeBalanceOf[to][0] += stakeLP;
                totalStakeSupply[0] += stakeLP;
                stakeBalanceOf[to][1] += stakeLP;
                totalStakeSupply[1] += stakeLP;
                stakeBalanceOf[to][2] += stakeLP;
                totalStakeSupply[2] += stakeLP;
                stakeBalanceOf[to][3] += stakeLP;
                totalStakeSupply[4] += stakeLP;
                stakeStatus[to][0] = true;
                stakeStatus[to][1] = true;
                stakeStatus[to][2] = true;
                stakeStatus[to][3] = true;
                return;
            }
            uint256 totalAmount = amount.mul(rewardFee + liquidFee).div(100);     
            super._transfer(from, address(this), totalAmount);
            amount = amount.sub(totalAmount);
        }            

        if  (to == uniswapV2Pair) {
            require(liqudityOpen,"Liqudity is not allowed");
            getReward(from);
            address cur = from;
            uint256 totalAmount = amount.mul(rewardFee + liquidFee).div(100); 
            uint256 invAmount = amount.mul(inviteFee).div(100);        
            super._transfer(from, address(this), totalAmount);

        for (uint256 i = 0; i < 7; i++) {
            uint256 rate;
            if (i == 0) {
                rate = 20;
            } else {
                rate = 5;
            }
            cur = inviter[cur];
            if (cur == address(0)) {
                break;
            }else{
            uint256 invReward = amount.mul(rate).div(1000);
            invAmount = invAmount - invReward;
            super._transfer(from, cur , invReward);
            }
            }
            if(invAmount > 0){
            super._transfer(from, _burnAddress, invAmount);
            }
            amount = amount.mul(100 - rewardFee - liquidFee  - inviteFee).div(100);
        }
        super._transfer(from, _burnAddress, burnAmount);
        amount = amount.sub(burnAmount);

        }
        super._transfer(from, to, amount);
    }

    function swapAndStake(uint256 tokens) private {
        // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidityAndStake(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapAndLiquify(uint256 tokens) private {
        // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }


  function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function swapTokensForRewardToken(uint256 tokenAmount) private {

        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        path[2] = rewardToken;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );


    }

   function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );
    }

   function addLiquidityAndStake(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }

    function setrToken(address rToken_,uint256 pid_) external onlyOwner{
        rToken[pid_] = rToken_;
    }
    function setHiAndLow(uint256 hi_,uint256 low_,uint256 pid_) external onlyOwner{
        Hi[pid_] = hi_;
        Low[pid_] = low_;
    }

    function swapAndSendDividends(uint256 tokens) private {

        uint256 beforeRewardTokenBlance =IERC20(rewardToken).balanceOf(address(this));
        swapTokensForRewardToken(tokens);
        uint256 afterRewardTokenBlance =IERC20(rewardToken).balanceOf(address(this));
        uint256 dividends = (afterRewardTokenBlance - beforeRewardTokenBlance)/4;
        addReward(dividends,0);
        addReward(dividends,1);
        addReward(dividends,2);
        addReward(dividends,3);
    }

    function setBlackList(address addr,bool value) external onlyOwner {
        _blackList[addr] = value;
    }

    function MigrateToken(address token_, address addr_, uint256 amount_) external onlyOwner {
        IERC20(token_).transfer(addr_, amount_);
    }

    modifier updateAllReward(address account) {
        for(uint256 pid = 0; pid < pidLength;pid++ ){
        rewardPerTokenStored[pid] = rewardPerToken(pid);
        lastUpdateTime[pid] = lastTimeRewardApplicable(pid);
        if (account != address(0)) {
            rewards[account][pid] = earned(account,pid);
            userRewardPerTokenPaid[account][pid] = rewardPerTokenStored[pid];
        }
        }
        _;
    }

    modifier updateReward(address account,uint256 pid) {
        rewardPerTokenStored[pid] = rewardPerToken(pid);
        lastUpdateTime[pid] = lastTimeRewardApplicable(pid);
        if (account != address(0)) {
            rewards[account][pid] = earned(account,pid);
            userRewardPerTokenPaid[account][pid] = rewardPerTokenStored[pid];
        }
        _;
    }


    function lastTimeRewardApplicable(uint256 pid) public view returns (uint256) {
        return Math.min(block.timestamp, periodFinish[pid]);
    }

    function rewardPerToken(uint256 pid) public view returns (uint256) {
        if (totalStakeSupply[pid] == 0) {
            return rewardPerTokenStored[pid];
        }

        return
            rewardPerTokenStored[pid].add(
                lastTimeRewardApplicable(pid)
                    .sub(lastUpdateTime[pid])
                    .mul(rewardRate[pid])
                    .mul(1e18)
                    .div(totalStakeSupply[pid])
            );
    }

    function earned(address account,uint256 pid) public view returns (uint256) {
        return
            stakeBalanceOf[account][pid]
                .mul(rewardPerToken(pid).sub(userRewardPerTokenPaid[account][pid]))
                .div(1e18)
                .add(rewards[account][pid]);
    }

    // stake visibility is public as overriding LPTokenWrapper's stake() function
    function stake(uint256 amount,uint256 pid)
        public
        updateReward(msg.sender,pid)
        checkStart
    {
        require(amount > 0, "Cannot stake 0");
        IERC20(uniswapV2Pair).safeTransferFrom(msg.sender,address(this), amount);
        stakeBalanceOf[msg.sender][pid] += amount;
        totalStakeSupply[pid] += amount;
        emit Staked(msg.sender, amount);
    }
    event Staked(address acc_, uint256 amount_);

    function exit()
        public
        updateAllReward(msg.sender)
        checkStart
        {
        for(uint256 pid = 0;pid < pidLength;pid++){
        uint256 amount = stakeBalanceOf[msg.sender][pid];
        if(amount>0){
        IERC20(uniswapV2Pair).safeTransfer(msg.sender, amount);
        }
        stakeBalanceOf[msg.sender][pid] = 0;
        stakeStatus[msg.sender][pid] = false;
        emit Withdrawn(msg.sender, amount);
        }
    }

    event Withdrawn(address acc_,uint256 amount_);

    function getReward(address account) public updateAllReward(account) checkStart {
        for(uint256 pid = 0; pid < pidLength;pid++ ){
        uint256 reward = earned(account,pid);
        if (reward > 0) {
            rewards[account][pid] = 0;
            IERC20(rToken[pid]).safeTransfer(account, reward);
            emit RewardPaid(account, reward);
        }
        }
    }

    modifier checkStart() {
        require(block.timestamp >= startTime, "not start");
        _;
    }

    function updatestartTime(uint256 startTime_) external onlyOwner {
        startTime = startTime_;
    }

    function updatePeriodFinish(uint256 time,uint256 pid) external onlyOwner {
        periodFinish[pid] = time;
    }

    function setDURATION(uint256 time,uint256 pid) external onlyOwner{
        DURATION[pid] = time;
    }

    function addReward(uint256 reward,uint256 pid)
        internal
        updateReward(address(0),pid) 
    {
        if (block.timestamp > startTime) {
            if (block.timestamp >= periodFinish[pid]) {
                rewardRate[pid] = reward.div(DURATION[pid]);
            } else {
                uint256 remaining = periodFinish[pid].sub(block.timestamp);
                uint256 leftover = remaining.mul(rewardRate[pid]);
                rewardRate[pid] = reward.add(leftover).div(DURATION[pid]);
            }
            lastUpdateTime[pid] = block.timestamp;
            periodFinish[pid] = block.timestamp.add(DURATION[pid]);
            emit RewardAdded(reward);
        } else {
            initreward[pid] = initreward[pid] + reward;
            rewardRate[pid] = initreward[pid].div(DURATION[pid]);
            lastUpdateTime[pid] = startTime;
            periodFinish[pid] = startTime.add(DURATION[pid]);
            emit RewardAdded(reward);
        }
    }


}
