    "contracts/nft_staking_final.sol": {
      "content": "//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

abstract contract NFTStaking is ReentrancyGuard, Ownable {
    struct Plan {
        uint256 apr;
        uint256 stakeDuration;
        address stakingToken;
        bool conclude;
    }

    struct Staking {
        EnumerableSet.UintSet tokenIds;
        mapping(uint256 => uint256) stakeAt;
    }

    mapping(uint256 => mapping(address => Staking)) internal stakes;

    address public rewardToken;
    mapping(uint256 => Plan) public plans;

    constructor(address _rewardToken) {
        rewardToken = _rewardToken;
    }

    function stake(uint256 _stakingId, uint256[] memory tokenIds)
        public
        virtual;

    function unstake(uint256 _stakingId, uint256[] memory tokenIds)
        public
        virtual;

    function getEarnedRewards(uint256 _stakingId, address account)
        public
        view
        virtual
        returns (uint256);

    function claimEarnedReward(uint256 _stakingId) public virtual;

    function getStakedTokens(uint256 _stakingId, address _account)
        public
        view
        virtual
        returns (uint256[] memory);
}

contract DSDCNFTStaking is NFTStaking {
    using EnumerableSet for EnumerableSet.UintSet;

    uint16 immutable planLimit = 3;

    constructor(
        address _dsdcToken,
        address _mutantsToken,
        address _stonedToken,
        address _rewardToken
    ) NFTStaking(_rewardToken) {
        plans[0].stakingToken = _dsdcToken;
        plans[0].apr = 2433333333000000000000000;
        plans[0].stakeDuration = 0 days;

        plans[1].stakingToken = _mutantsToken;
        plans[1].apr = 12166667000000000000000000;
        plans[1].stakeDuration = 30 days;

        plans[2].stakingToken = _stonedToken;
        plans[2].apr = 6083333500000000000000000;
        plans[2].stakeDuration = 30 days;
    }


    function stake(uint256 _stakingId, uint256[] memory tokenIds)
        public
        override
    {
        require(_stakingId <= planLimit, "INVALID_PLAN");

        Plan memory plan = plans[_stakingId];
        require(!plan.conclude, "ERR_POOL_CONCLUDED");

        Staking storage staking = stakes[_stakingId][msg.sender];

        for (uint256 i = 0; i < tokenIds.length; i++) {
            require(
                IERC721(plan.stakingToken).ownerOf(tokenIds[i]) == msg.sender,
                "ERR_INVALID_NFT_OWNER"
            );

            staking.tokenIds.add(tokenIds[i]);
            staking.stakeAt[tokenIds[i]] = block.timestamp;

            // Transfer the deposited token to this contract
            IERC721(plan.stakingToken).transferFrom(
                msg.sender,
                address(this),
                tokenIds[i]
            );
        }
    }

    function unstake(uint256 _stakingId, uint256[] memory tokenIds)
        public
        override
    {
        Plan memory plan = plans[_stakingId];
        Staking storage staking = stakes[_stakingId][msg.sender];
        uint256 rewards = 0;

        for (uint256 i = 0; i < tokenIds.length; i++) {
            uint256 tokenId = tokenIds[i];

            require(staking.tokenIds.contains(tokenId), "ERR_TOKEN_NOT_STAKED");

            uint256 timeElapsed = block.timestamp - staking.stakeAt[tokenId];

            require(timeElapsed >= plan.stakeDuration,"ERR_LOCK_DURATION_ONGOING");

            uint256 aprPerPeriod = (plan.apr * timeElapsed) / 100 / 365 days;

            rewards += aprPerPeriod;

            staking.tokenIds.remove(tokenId);

            IERC721(plan.stakingToken).transferFrom(
                address(this),
                msg.sender,
                tokenId
            );
        }

        if (rewards > 0) {
            IERC20(rewardToken).transfer(msg.sender, rewards);
        }
    }

    function claimEarnedReward(uint256 _stakingId) public override {
        Plan memory plan = plans[_stakingId];
        Staking storage staking = stakes[_stakingId][msg.sender];
        uint256 totalRewards = 0;

        for (uint256 i = 0; i < staking.tokenIds.length(); i++) {
            uint256 tokenId = staking.tokenIds.at(i);
           
            uint256 timeElapsed = block.timestamp - staking.stakeAt[tokenId];
            
            require(timeElapsed >= plan.stakeDuration,"ERR_LOCK_DURATION_ONGOING");
            
            uint256 rewards = (timeElapsed * plan.apr) / 100 / 365 days;

            staking.stakeAt[tokenId] = block.timestamp;
            totalRewards += rewards;
        }

        require(totalRewards > 0, "ERR_NO_AMOUNT_TO_CLAIM");
        IERC20(rewardToken).transfer(msg.sender, totalRewards);
    }

    function getEarnedRewards(uint256 _stakingId, address _account)
        public
        view
        override
        returns (uint256)
    {
        uint256 canClaim = 0;
        Plan memory plan = plans[_stakingId];
        Staking storage staking = stakes[_stakingId][_account];
        if (staking.tokenIds.length() > 0) {
            uint256 timeElapsed = block.timestamp -
                staking.stakeAt[staking.tokenIds.at(0)];
            uint256 aprPerPeriod = (plan.apr * timeElapsed) / 100 / 365 days;

            for (uint256 i = 0; i < staking.tokenIds.length(); i++) {
                canClaim += aprPerPeriod;
            }
        }
        return canClaim;
    }

    function getStakedTokens(uint256 _stakingId, address _account)
        public
        view
        override
        returns (uint256[] memory)
    {
        Staking storage _staking = stakes[_stakingId][_account];
        uint256[] memory stakedTokenIds = new uint256[](
            _staking.tokenIds.length()
        );
        for (uint256 i = 0; i < _staking.tokenIds.length(); i++) {
            stakedTokenIds[i] = _staking.tokenIds.at(i);
        }

        return stakedTokenIds;
    }

    function getTokenEndStake(
        uint256 _stakingId,
        address _account,
        uint256 _tokenId
    ) public view returns (uint256) {
        Staking storage _staking = stakes[_stakingId][_account];
        uint256 _planDuration = plans[_stakingId].stakeDuration;
        uint256 tokenEndStake = _staking.stakeAt[_tokenId] + _planDuration;
        return tokenEndStake;
    }

    function setAPR(uint256 _stakingId, uint256 _percent) external onlyOwner {
        plans[_stakingId].apr = _percent;
    }

    function setDuration(uint256 _stakingId, uint256 _newDuration)
        external
        onlyOwner
    {
        plans[_stakingId].stakeDuration = _newDuration;
    }

    function setStakeConclude(uint256 _stakingId, bool _conclude)
        external
        onlyOwner
    {
        plans[_stakingId].conclude = _conclude;
    }

    function recoverToken(address _token) external onlyOwner {
        uint256 balance = IERC20(_token).balanceOf(address(this));
        require(balance != 0, "Cannot recover zero balance");

        IERC20(_token).transfer(address(msg.sender), balance);
    }
}
