pragma solidity ^0.8.14;

contract WalkDogs is ERC20, Ownable {
    using SafeMath for uint256;

    mapping(address => bool) private isExcludedFromFee;

    uint private constant TARGET_FEE = 100;
    uint private constant INITIAL_SELL_FEE = 2200;
    uint private constant INITIAL_BUY_FEE = 10;
    uint private constant FEE_DELIMETER = 10000;
    uint private constant FEE_STABILITY_TIME = 14 days;

    uint private constant INITIAL_TOKENS = 10000000 * (10 ** 18);
    uint private constant MIN_TOKEN_TO_SWAP = 500 * (10 ** 18);

    address private constant marketingAddress = 0xF90Aa3B392Ece1e28C0f09131f27b1b06f0DD373;

    IUniswapV2Router02 private uniswapV2Router;
    address public uniswapV2Pair;

    uint public launchTime;

    bool private inSwap;

    constructor() ERC20("Walk Dogs", "WLD") {
        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());

        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[owner()] = true;

        _mint(owner(), INITIAL_TOKENS);
    }

    receive() external payable {}

    function _transfer(address _from, address _to, uint _amount) internal override {
        if (balanceOf(address(this)) > MIN_TOKEN_TO_SWAP && _from != uniswapV2Pair && launchTime > 0 && !inSwap) {
            inSwap = true;

            _swapTokensForEth(MIN_TOKEN_TO_SWAP);
            payable(marketingAddress).transfer(address(this).balance);

            inSwap = false;
        }

        if (isExcludedFromFee[_from] || isExcludedFromFee[_to]) {
            _baseTransfer(_from, _to, _amount);

            return;
        }

        require(launchTime > 0, "StepDog: Not launched yet");

        uint _commission = TARGET_FEE;
        (uint _sellFee, uint _buyFee) = getFees();
        if (_from == uniswapV2Pair) {
            _commission = _buyFee;
        }
        if (_to == uniswapV2Pair) {
            _commission = _sellFee;
        }

        uint _commissionAmount = _amount.mul(_commission).div(FEE_DELIMETER);

        _baseTransfer(_from, address(this), _commissionAmount);
        _baseTransfer(_from, _to, _amount.sub(_commissionAmount));
    }

    function _swapTokensForEth (uint _amount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), _amount);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(_amount, 0, path, address(this), block.timestamp);
    }

    function getFees() public view returns (uint sellFee, uint buyFee) {
        if (launchTime == 0) {
            return (0, 0);
        }

        if (block.timestamp.sub(launchTime) > FEE_STABILITY_TIME) {
            return (TARGET_FEE, TARGET_FEE);
        }

        uint _timeAfterLauncher = block.timestamp.sub(launchTime);
        return (
            INITIAL_SELL_FEE.sub(INITIAL_SELL_FEE.sub(TARGET_FEE).mul(_timeAfterLauncher).div(FEE_STABILITY_TIME)),
            INITIAL_BUY_FEE.add(TARGET_FEE.sub(INITIAL_BUY_FEE).mul(_timeAfterLauncher).div(FEE_STABILITY_TIME))
        );
    }

    function burn(uint256 _amount) external {
        _burn(msg.sender, _amount);
    }

    function burnFrom(address _account, uint256 _amount) external {
        _spendAllowance(_account, msg.sender, _amount);
        _burn(_account, _amount);
    }

    function launch() external onlyOwner {
        launchTime = block.timestamp;
