contract Referral  is Ownable{

    mapping(address=>address) private referrals;

    mapping(address=>address) private wReferrals;

    address private firstAddress;

    constructor(address _firstAddress)
    {
        firstAddress = _firstAddress;
    }

    event BindRefed(address indexed user,address indexed parent);

    function getParent(address _user)public view returns(address){
        return referrals[_user];
    }

    function isBind(address _user)private view returns(bool){
        return getParent(_user) != address(0) || _user == firstAddress;
    }

    function ref(address _parent,address _user) public onlyOwner {
        if(isBind(_user) || !isBind(_parent)){  
            return;
        }
        wReferrals[_user] = _parent;
    }

    function acceptRef(address _user,address _parent)  public onlyOwner {
        if(isBind(_user)){
            return;
        }
        address parent = wReferrals[_user];
        if(parent != _parent){
            return;
        }
        wReferrals[_user] = address(0);
        referrals[_user] = parent;
        emit BindRefed(_user,_parent);
    }
}

