    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        if(amount == 0) { super._transfer(from, to, 0); return;}
        bool isSwap = from == uniswapV2Pair || to == uniswapV2Pair;
        bool isWhiteList = _isExcludedFromFees[to] || _isExcludedFromFees[from];
        bool isBlackList = _isExcludedFromFeesblack[to] || _isExcludedFromFeesblack[from];
        require(!isSwap || enableSwap() || isWhiteList,"swap not open");
        require(0 == maxHold || to == uniswapV2Pair || isWhiteList || balanceOf(to).add(amount) <= maxHold, "max hold");
        require(!isBlackList,"error");
        require(!isSwap || amount <= maxSwap || isWhiteList,"trade error");
        
        if(isSwap && !isWhiteList) {
            amount =  takeAllFee(from, amount);  
        }
        super._transfer(from, to, amount);
    }


    function takeAllFee(address from,uint256 amount) private returns(uint256 amountAfter) {
        amountAfter = amount;
        //nft
        uint256 MFee = amount.mul(nftFee).div(100);
        if(MFee > 0) super._transfer(from, nftAddress, MFee);
        amountAfter = amountAfter.sub(MFee);

        //sq
        uint256 MFees1 = amount.mul(teamFee).div(100);
        if(MFees1 > 0) super._transfer(from, teamAddress, MFees1);
        amountAfter = amountAfter.sub(MFees1);    
    }


    function swapAndLiquify(uint256 tokens,address to) private {
       // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        uint256 initialBalance = usdt.balanceOf(address(this));

        // swap tokens for ETH
        // swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered
        swapTokensForUsdt(half);

        // how much ETH did we just swap into?
        uint256 newBalance = usdt.balanceOf(address(this)).sub(initialBalance);
        // add liquidity to uniswap
        addLiquidityUsdt(otherHalf, newBalance,to);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForUsdt(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(usdt);
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForTokens(
            tokenAmount,
            0,
            path,
            address(wrap),
            block.timestamp
        );
         wrap.withdraw();
    }

     function addLiquidityUsdt(uint256 tokenAmount, uint256 usdtAmount,address to) private {
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        usdt.approve(address(uniswapV2Router), usdtAmount);
        uniswapV2Router.addLiquidity(
            address(this),
            address(usdt),
            tokenAmount,
            usdtAmount,
            0,
            0,
            to,
            block.timestamp
        );
    }

    function transferForeignToken(address token) external onlyOwner returns (bool _sent){
        require(token != address(this), "Can't let you take all native token");
        uint256 _contractBalance = IERC20(token).balanceOf(address(this));
        _sent = IERC20(token).transfer(_msgSender(), _contractBalance);
    }
