contract NFTDividendDistributor is IDividendDistributor {
   using SafeMath for uint256;

    address owner;
    address nftDividend;

    struct Share {
        uint256 totalExcluded;
        uint256 totalRealised;
    }

    IERC20 shareToken = IERC20(0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56);

    uint256 shareholders;
    mapping (uint256 => uint256) shareholderIndexes;
    mapping (uint256 => uint256) shareholderClaims;

    mapping (uint256 => Share) public shares;
    mapping (address => uint256) public sharesAmount;

    uint256 public totalShares;
    uint256 public totalDividends;
    uint256 public totalDistributed;
    uint256 public dividendsPerShare;
    uint256 public dividendsPerShareAccuracyFactor = 10 ** 36;

    uint256 public minPeriod = 1 hours / 2;
    uint256 public minDistribution = 1 * (10 ** 8);

    uint256 currentIndex;

    bool initialized;
    modifier initialization() {
        require(!initialized);
        _;
        initialized = true;
    }

    modifier onlyToken() {
        require(msg.sender == owner);
        _
        ;
    }

    constructor (address _shareToken, address _nftDividend) {
        shareToken = IERC20(_shareToken);
        nftDividend = _nftDividend;
        owner = msg.sender;
    }

    function setDistributionCriteria(uint256 _minPeriod, uint256 _minDistribution) external override onlyToken {
        minPeriod = _minPeriod;
        minDistribution = _minDistribution;
    }


    function setShare(uint256 _shareholders, uint256 totleHolders) internal  {
        if(_shareholders < totleHolders){
          for(uint shareholder = _shareholders + 1; shareholder <= totleHolders; shareholder++ ){
            shares[shareholder].totalExcluded = getCumulativeDividends(1);
          }
        }
        totalShares = totleHolders;
    }


   function deposit(uint256 _amount) external payable override onlyToken {
      shareToken.transferFrom(msg.sender , address(this), _amount);
      totalDividends = totalDividends.add(_amount);

      uint256 totleHolders = IERC721(nftDividend).totalSupply();
      setShare(shareholders , totleHolders);
      shareholders = totleHolders;
      dividendsPerShare = dividendsPerShare.add(dividendsPerShareAccuracyFactor.mul(_amount).div(shareholders));
    }

    function process(uint256 gas) external override onlyToken {
        uint256 shareholderCount = shareholders;

        if(shareholderCount == 0 || dividendsPerShare == 0) { return; }

        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();

        uint256 iterations = 0;
        address shareHolderOwenr;

        while(gasUsed < gas && iterations < shareholderCount) {
            if(currentIndex >= shareholderCount){
                currentIndex = 0;
            }
            shareHolderOwenr = IERC721(nftDividend).ownerOf(1 + currentIndex);

            if(shouldDistribute(currentIndex + 1) && shareHolderOwenr != address(0)){
                distributeDividend(currentIndex + 1, shareHolderOwenr);
            }

            gasUsed = gasUsed.add(gasLeft.sub(gasleft()));
            gasLeft = gasleft();
            currentIndex++;
            iterations++;
        }
    }

    function shouldDistribute(uint256 shareholder) internal view returns (bool) {
        return shareholderClaims[shareholder] + minPeriod < block.timestamp
                && getUnpaidEarnings(shareholder) > minDistribution;
    }


    function distributeDividend(uint256 shareholder, address shareHolderOwenr ) internal {

        uint256 amount = getUnpaidEarnings(shareholder);
        if(amount > 0){
            totalDistributed = totalDistributed.add(amount);
            shareToken.transfer(shareHolderOwenr, amount);
            sharesAmount[shareHolderOwenr] = sharesAmount[shareHolderOwenr].add(amount);
            shareholderClaims[shareholder] = block.timestamp;
            shares[shareholder].totalRealised = shares[shareholder].totalRealised.add(amount);
            shares[shareholder].totalExcluded = getCumulativeDividends(1);
        }
    }


    function getUnpaidEarnings(uint256 shareholder) public view returns (uint256) {

        uint256 shareholderTotalDividends = getCumulativeDividends(1);
        uint256 shareholderTotalExcluded = shares[shareholder].totalExcluded;

        if(shareholderTotalDividends <= shareholderTotalExcluded){ return 0; }

        return shareholderTotalDividends.sub(shareholderTotalExcluded);
    }


    function getCumulativeDividends(uint256 share) internal view returns (uint256) {
        return share.mul(dividendsPerShare).div(dividendsPerShareAccuracyFactor);
    }

    function getSharesAmount(address account) public view returns (uint256) {
        return sharesAmount[account];
    }

    function claimToken(address stoken, address recipient, uint256 amount) external  onlyToken{
      require(recipient != address(0), "zero_address");
      uint256 balance = IERC20(stoken).balanceOf(address(this));
      if (balance > 0) {
          IERC20(stoken).transfer(recipient, balance > amount? amount : balance);
      }
    }

}

