        uintDecimals = (10**18);
        uint256 totalSupply = Supply * uintDecimals;
        swapTokensAtAmount = totalSupply.mul(6).div(10**6); // 0.002%

        // use by default 300,000 gas to process auto-claiming dividends
        gasForProcessing = 300000;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(RouterAddress);
        _approve(address(this), address(_uniswapV2Router), MAX);
        // _allowances[address(this)][address(_uniswapV2Router)] = MAX;
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), rewardToken);

        uniswapV2Router = _uniswapV2Router;
        uniswapPair = _uniswapV2Pair;

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        _mint(ReceiveAddress, totalSupply);

        tokenDistributor = new  TokenDistributor(UsdtAddress);
        ShareHolderNFTDividend = new NFTDividendDistributor(rewardToken, ShareHolderNFTAddress);
        StudioNFTDividend = new NFTDividendDistributor(rewardToken, StudioNFTAddress);

        IERC20(UsdtAddress).approve(address(ShareHolderNFTDividend), MAX);
        IERC20(UsdtAddress).approve(address(StudioNFTDividend), MAX);

        // exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        excludeFromFees(ReceiveAddress, true);
        excludeFromFees(_marketingWalletAddress, true);
        excludeFromFees(address(this), true);

        _limitAmount = LimitAmount * uintDecimals;
    }

