        ShareHolderNFTDividend = new NFTDividendDistributor(rewardToken, _shareHolderNFTAddress);
        IERC20(rewardToken).approve(address(ShareHolderNFTDividend), MAX);
    }

    function setStudioNFTAddress(address _studioNFTAddress) external onlyOwner {
