        StudioNFTDividend = new NFTDividendDistributor(rewardToken, _studioNFTAddress);
        IERC20(rewardToken).approve(address(StudioNFTDividend), MAX);
    }
    function startTrade() external onlyOwner {
        require(0 == startTradeBlock, "T");
        startTradeBlock = block.number;
        // _releaseLPStartTime = block.timestamp;
    }

    function setTaxes(uint256 _lpFee, uint256 _shareHolderNFTFee, uint256 _studioNFTFee, uint256 _marketingFee) external onlyOwner {
        require(_lpFee.add(_shareHolderNFTFee).add(_studioNFTFee).add(_marketingFee) <= 2500, "Total buy fee is over 25%");
        lpFee = _lpFee;
        shareHolderNFTFee = _shareHolderNFTFee;
        studioNFTFee = _studioNFTFee;
        marketingFee = _marketingFee;

    }

    function setDistributionCriteria(address distributionAddress, uint256 _minPeriod, uint256 _minDistribution) external onlyOwner {
        require(distributionAddress != address(0), "zero_address");
        NFTDividendDistributor(distributionAddress).setDistributionCriteria(_minPeriod,_minDistribution );
    }

    function claimDividendToken(address distributionAddress, address stoken, address recipient, uint256 amount) external onlyOwner{
      require(distributionAddress != address(0), "zero_address");
      NFTDividendDistributor(distributionAddress).claimToken(stoken ,recipient, amount );
    }

    function claimToken(address stoken, address recipient, uint256 amount) external onlyOwner {
      require(recipient != address(0), "zero_address");
      uint256 balance = IERC20(stoken).balanceOf(address(this));
      if (balance > 0) {
          IERC20(stoken).transfer(recipient, balance > amount? amount : balance);
      }
    }

    function getSharesAmount(address account) public view returns(uint256){
      uint256 shareHolderAmount = ShareHolderNFTDividend.getSharesAmount(account);
      uint256 studioAmount = StudioNFTDividend.getSharesAmount(account);
      return shareHolderAmount.add(studioAmount);
    }

    function setLPDividendPool(address _pool) external onlyOwner {
        require(_pool != address(0), "_pool is zore");
        // excludeFromFees(_pool, true);
        IERC20(rewardToken).approve(_pool, MAX);
        LpPool = _pool;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;
        if( canSwap && !swapping && !automatedMarketMakerPairs[from] && from != owner() && to != owner() && swapAndLiquifyEnabled) {
            swapping = true;
            if(contractTokenBalance > 0) swapAndSendToFee(contractTokenBalance);
            swapping = false;
        }

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if(takeFee) {

          uint256 feeAmount;
          uint256 fees;
          if(automatedMarketMakerPairs[from] || automatedMarketMakerPairs[to]){
            fees = amount.mul(lpFee.add(shareHolderNFTFee).add(studioNFTFee).add(marketingFee)).div(totleFee);
            if (0 == startTradeBlock) {
              require(0 < startAddLPBlock && automatedMarketMakerPairs[to], "!startAddLP");
            }
            if (block.number < startTradeBlock + 4) {
              feeAmount = amount * 40 / 100;
              super._transfer(from, _marketingWalletAddress, feeAmount);
            }
          }
          if(fees > 0){
            amount = amount.sub(fees).sub(feeAmount);
            super._transfer(from, address(this), fees);
          }
        }

        super._transfer(from, to, amount);

        uint256 limitAmount = _limitAmount;
        if (limitAmount > 0) {
            if (!automatedMarketMakerPairs[to] && !(to == address(this))) {             //Hold Limit
                require(limitAmount >= balanceOf(to), "Limit");
            }
        }

        if(!swapping) {
            uint256 gas = gasForProcessing;
            uint256 blockNum = block.number;
            processShareHolderNFTReward(gas);
            if (processShareHolderNFTBlock != blockNum) {
                processStudioNFTReward(gas);
            }
        }
    }

    uint256 public processShareHolderNFTBlock;
    uint256 public processShareHolderNFTBlockDebt = 100;

    function processShareHolderNFTReward(uint256 gas) private {
      if (processShareHolderNFTBlock + processShareHolderNFTBlockDebt > block.number) { return; }
      try ShareHolderNFTDividend.process(gas) {} catch {}
      processShareHolderNFTBlock = block.number;
    }

    function setProcessShareHolderNFTBlockDebt(uint256 blockDebt) external onlyOwner {
        processShareHolderNFTBlockDebt = blockDebt;
    }

    uint256 public processStudioNFTBlock;
    uint256 public processStudioNFTBlockDebt = 0;

    function processStudioNFTReward(uint256 gas) private {
      if (processStudioNFTBlock + processStudioNFTBlockDebt > block.number) {
        return;
      }
      try  StudioNFTDividend.process(gas) {} catch {}
      processStudioNFTBlock = block.number;
    }
    function setProcessStudioNFTBlockDebt(uint256 blockDebt) external onlyOwner {
        processStudioNFTBlockDebt = blockDebt;
    }


    function swapAndSendToFee(uint256 tokenAmount) private  {
        swapTokensForCake(tokenAmount);
        uint256 rewardBalance = IERC20(rewardToken).balanceOf(address(this));
        if(rewardBalance == 0) return;

        uint256 totleFees = lpFee.add(shareHolderNFTFee).add(studioNFTFee).add(marketingFee);
        uint256 lpBalance = rewardBalance.mul(lpFee).div(totleFees);
        if(LpPool != address(0)){
            IDividendPool(LpPool).addTokenReward(lpBalance);
        }
        uint256 shareHolderNFTBalance = rewardBalance.mul(shareHolderNFTFee).div(totleFees);
        if(address(ShareHolderNFTDividend) != address(0)){
            ShareHolderNFTDividend.deposit(shareHolderNFTBalance);
        }
        uint256 studioNFTBalance = rewardBalance.mul(studioNFTFee).div(totleFees);
        if(address(StudioNFTDividend) != address(0)){
            StudioNFTDividend.deposit(studioNFTBalance);
        }
        uint256 marketingBalance = rewardBalance.sub(lpBalance).sub(shareHolderNFTBalance).sub(studioNFTBalance);
        IERC20(rewardToken).transfer(_marketingWalletAddress, marketingBalance);

    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        // _approve(address(this), address(uniswapV2Router), tokenAmount);
        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );

    }

    function swapTokensForCake(uint256 tokenAmount) public {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = rewardToken;
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(tokenDistributor),
            block.timestamp
        );

        uint256 initialUSDTBalance = IERC20(rewardToken).balanceOf(address(tokenDistributor));
