     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * will be transferred to `to`.
     * - when `from` is zero, `amount` tokens will be minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    /**
     * @dev Hook that is called after any transfer of tokens. This includes
     * minting and burning.
     *
     * Calling conditions:
     *
     * - when `from` and `to` are both non-zero, `amount` of ``from``'s tokens
     * has been transferred to `to`.
     * - when `from` is zero, `amount` tokens have been minted for `to`.
     * - when `to` is zero, `amount` of ``from``'s tokens have been burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

contract STINKv2 is ERC20, Ownable {
    // Mapping of addresses that are tax exempt
    mapping(address => bool) public taxExempt;
    // Mapping of addresses that are pairs
    mapping(address => bool) public pair;

    // Tax Related Wallets
    address public marketing;
    address public vault; // This address holds the STINKv2 tokens for staking
    address public liquidityVault; // This address holds the liquidity tokens

    uint public liquidityFee; // Amount collected that belongs to liquidity
    uint public marketingFee; // Amount collected that belongs to marketing

    uint public sellThreshold; // Amount of tokens needed in collection to trigger a swap

    uint public totalLiquidity; // total liquidity tokens created and sent to liquidity vault
    uint public totalStaking; // total STINKv2 tokens sent to staking vault
    uint public totalMarketing; // total ETH sent to marketing wallet

    // Taxes are: Marketing, Liquidity, Staking
    uint8[3] public buyTaxes = [1, 1, 1];
    uint8[3] public sellTaxes = [2, 1, 3];

    IUniswapV2Router02 public router; // PancakeSwap Router
    IUniswapV2Pair public mainPair; // Main pair for the token STINKv2 / BNB

    bool public swapping = false; // Prevents reentrancy

    event SwapAndLiquify(
        uint tokensSwapped,
        uint ethReceived,
        uint tokensIntoLiqudity
    );

    /// @notice Locks the contract for a single tx
    modifier lockTheSwap() {
        swapping = true;
        _;
        swapping = false;
    }

    constructor(
        address _marketing,
        address _vault,
        address _liquidityVault
    ) ERC20("STINKv2", "STINKv2") {
        _mint(msg.sender, 1_000_000_000 ether);
        sellThreshold = 100 ether;
        // SET DEFAULT INIT PAIR
        router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        IUniswapV2Factory pancakeFactory = IUniswapV2Factory(router.factory());
        address bnbPair = pancakeFactory.createPair(
            address(this),
            router.WETH()
        );
        mainPair = IUniswapV2Pair(bnbPair);
        pair[bnbPair] = true;

        taxExempt[msg.sender] = true;
        marketing = _marketing;
        vault = _vault;
        liquidityVault = _liquidityVault;
    }

    /// @notice Allowed to receive ETH
    receive() external payable {}

    /// @notice Execute transfer, if enough tokens, swap and distribute fes
    /// @param sender Sender address
    /// @param recipient Recipient address
    /// @param amount Amount of tokens to transfer
    /// @dev Override ERC20 transfer function
    /// @dev If the sender is not tax exempt, calculate the fees, subtract from amount and transfer staking fee to vault
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        uint balance = balanceOf(address(this));
        // Cant distribute Fees on a BUY transaction
        if (balance > sellThreshold && !swapping && !pair[sender]) {
            distributeFees(balance);
        }

        if (swapping || taxExempt[sender] || taxExempt[recipient]) {
            super._transfer(sender, recipient, amount);
        } else {
            uint fees = takeFees(sender, recipient, amount);
            amount -= fees;
            super._transfer(sender, recipient, amount);
        }
    }

    /// @notice Manually distribute fees
    /// @dev This function is to be called by anyone to distribute fees if they feel fees aren't being distributed
    function manualDistribute() external {
        uint balance = balanceOf(address(this));
        distributeFees(balance);
    }

    /// @notice Creates liquidity from current balance and sends ETH to marketing wallet
    /// @param amount Amount of tokens to swap
    /// @dev We always use the total amount of fees collected, if the amount is different, we calculate the percentage
    /// @dev we first create liquidity to strengthen the pool, then swap marketing for ETH and send to marketing wallet
    function distributeFees(uint amount) private lockTheSwap {
        uint256 tokensToMarketing = marketingFee;
        uint256 tokensToLiquidity = liquidityFee;

        // In cases where the balance is not in sync with the variables
        // send 60% to marketing and 40% to liquidity
        if (tokensToMarketing + tokensToLiquidity != amount) {
            tokensToMarketing = (amount / 10) * 6;
            tokensToLiquidity = amount - tokensToMarketing;
        }

        // You only swap half of the balance to go to liquidity
        uint256 marketingTokensToSwap = tokensToMarketing;

        uint256 liquidityTokensToSwap = tokensToLiquidity / 2;
        uint256 liquidityTokensToAdd = tokensToLiquidity - liquidityTokensToSwap;

        uint256 totalTokensToSwap = marketingTokensToSwap + liquidityTokensToSwap;

        uint256 initialBalance = address(this).balance;

        // Swap to ETH
        _swapForEth(totalTokensToSwap);

        // Total ETH that has been swapped
        uint256 ethSwapped = address(this).balance - initialBalance;

        uint256 swappedMarketingEthBps = tokensToMarketing * 10_000 / totalTokensToSwap;

        uint256 ethToMarketing = (ethSwapped * swappedMarketingEthBps) / 10_000;
        totalMarketing += ethToMarketing;
        marketingFee = 0;

        if (ethToMarketing > 0) {
            (bool success, ) = payable(marketing).call{value: ethToMarketing}("");
            require(success, "Unable to send to marketing address");
        }

        uint256 ethToLiquidity = ethSwapped - ethToMarketing;
        liquidityFee = 0;
        totalLiquidity += ethToLiquidity;
        if (ethToLiquidity > 0) {
            _addLiquidity(liquidityTokensToAdd, ethToLiquidity);
        }
    }

    /// @notice Adds to liquidity pair internally
    /// @param tokenAmount Token half of the pair
    /// @param ethAmount ETH half of the pair
    /// @dev Please note that actual liquidity created vs amount allocated to liquidity vault is not 1:1 due to fees on creating liquidity
    function _addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        _approve(address(this), address(router), tokenAmount);

        (,,uint256 liquidity) = router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0,
            0,
            liquidityVault,
            block.timestamp
        );

        totalLiquidity += liquidity;

        emit SwapAndLiquify(tokenAmount, ethAmount, liquidity);
    }

    /// @notice Swap tokens for ETH
    function _swapForEth(uint tokens) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        _approve(address(this), address(router), tokens);

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokens,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    /// @notice Calculates fees and transfers them to the relevant contract in a transaction
    /// @param sender Sender address
    /// @param recipient Recipient address
    /// @param amount Amount of tokens to transfer
    /// @dev by determining whether the transaction is a buy or sell, we can apply the correct taxes
    function takeFees(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (uint256) {
        // No fees if a transfer
        if (!pair[sender] && !pair[recipient]) {
            return 0;
        }

        uint256 marketingTax = pair[sender] ? buyTaxes[0] : sellTaxes[0];
        uint256 liquidityTax = pair[sender] ? buyTaxes[1] : sellTaxes[1];
        uint256 stakingTax = pair[sender] ? buyTaxes[2] : sellTaxes[2];

        uint256 currentMarketingFee = (amount * marketingTax) / 100;
        uint256 currentLiquidityFee = (amount * liquidityTax) / 100;
        uint256 currentStakingFee = (amount * stakingTax) / 100;

        // The cumulative amounts that have been raised on the contract
        marketingFee += currentMarketingFee;
        liquidityFee += currentLiquidityFee;

        // The total amount of STINK that has been added to staking
        totalStaking += currentStakingFee;

        if (currentStakingFee > 0) {
            super._transfer(sender, vault, currentStakingFee);
        }
        if (currentMarketingFee + currentLiquidityFee > 0) {
            super._transfer(sender, address(this), currentMarketingFee + currentLiquidityFee);
        }

        return currentMarketingFee + currentLiquidityFee + currentStakingFee;
    }

    /// @notice BURN tokens (Reduces total supply and removes tokens from circulation)
    /// @param amount amount of tokens to BURN
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    /// @notice BURN tokens from another account (Reduces total supply and removes tokens from circulation)
    /// @param account account to burn tokens from
    /// @param amount amount of tokens to BURN
    function burnFrom(address account, uint256 amount) external {
        uint allowed = allowance(account, msg.sender);
        require(amount <= allowed, "STINKv2: Not enough allowance");
        uint256 decreasedAllowance = allowed - amount;
        _approve(account, msg.sender, decreasedAllowance);
        _burn(account, amount);
    }

    /// @notice Set Exempt status for a single address
    /// @param account Address to set exempt status for
    /// @param exempt Exempt status (True = exempt, False = not exempt)
    /// @dev only OWNER of contract can set exempt status
    function setTaxExempt(address account, bool exempt) external onlyOwner {
        require(account != address(0), "STINKv2: zero address");
        taxExempt[account] = exempt;
    }

    /// @notice Set the marketing wallet address
    /// @param account Address of the marketing wallet
    /// @dev only OWNER of contract can set marketing wallet address
    function setMarketingWallet(address account) external onlyOwner {
        require(account != address(0), "STINKv2: zero address");
        marketing = account;
    }

    /// @notice Set the vault address
    /// @param account Address of the vault
    /// @dev only OWNER of contract can set vault address
    function setVaultAddress(address account) external onlyOwner {
        require(account != address(0), "STINKv2: zero address");
        vault = account;
    }

    /// @notice Set the liquidity vault address
    /// @param account Address of the liquidity vault
    /// @dev only OWNER of contract can set liquidity vault address
    function setLiquidityVaultAddress(address account) external onlyOwner {
        require(account != address(0), "STINKv2: zero address");
        liquidityVault = account;
    }

    ///@notice get tokens sent "mistakenly" to the contract
    ///@param _token Address of the token to be recovered
    function recoverToken(address _token) external {
        require(_token != address(this), "Cannot withdraw SELF");
        uint256 balance = IERC20(_token).balanceOf(address(this));
        require(balance > 0, "No tokens to withdraw");
        require(IERC20(_token).transfer(marketing, balance), "Transfer failed");
    }

    /// @notice recover ETH sent to the contract
    function recoverETH() external {
        (bool succ, ) = payable(marketing).call{value: address(this).balance}(
            ""
        );
        require(succ, "ETH transfer failed");
    }

    /// @notice Set the threshold to trigger tax distribution
    /// @param _threshold Amount of tokens to trigger tax distribution
    /// @dev only OWNER of contract can set threshold and threshold should be lower than 1% of total supply
    function setThreshold(uint256 _threshold) external onlyOwner {
        require(_threshold <= (totalSupply() * 1) / 100, "Threshold too high");
        sellThreshold = _threshold;
    }

    /// @notice Removes taxes for when user is buying a token
    /// @dev only OWNER of contract can remove the buy taxes
    function removeBuyTaxes() external onlyOwner {
        buyTaxes = [0, 0, 0];
    }

    /// @notice Sets buy tax to 1% going to liquidity
    /// @dev only OWNER of contract can remove the buy taxes
    function setLowBuyTaxes() external onlyOwner {
        buyTaxes = [0, 1, 0];
    }

    /// @notice Restores taxes for when user is buying a token
    /// @dev only OWNER of contract can restore the buy taxes
    function restoreBuyTaxes() external onlyOwner {
        buyTaxes = [1, 1, 1];
    }

    /// @notice Removes taxes for when user is selling a token
    /// @dev only OWNER of contract can remove the sell taxes
    function removeSellTaxes() external onlyOwner {
        sellTaxes = [0, 0, 0];
    }

    /// @notice Sets sell tax to 1% going to liquidity
    /// @dev only OWNER of contract can remove the buy taxes
    function setLowSellTaxes() external onlyOwner {
        sellTaxes = [0, 1, 0];
    }

    /// @notice Restores taxes for when user is selling a token
    /// @dev only OWNER of contract can restore the sell taxes
    function restoreSellTaxes() external onlyOwner {
        sellTaxes = [2, 1, 3];
    }
}
