    "contracts/FaceStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "hardhat/console.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// todo:
// generate terms timestamp of 1st and 15th for each month
// staking endtime should be timestamp in terms
contract FaceStaking is Ownable {
    IERC20 public token;

    // uint256 public constant TERM_LENGTH = 30 days; // Define term length as 30 days
    // uint256 public constant WEIGHT_INCREMENT = 2; // Define weight 2
    uint256 constant DECIMAL_PRECISION = 10 ** 18;

    struct Stake {
        uint256 amount;
        uint256 startTime;
        uint256 endTime;
        uint256 startTerm;
        uint256 weight;
        uint256[] points;
    }

    uint256[] public terms;
    uint256 constant totalTerms = 20;
    mapping(address => Stake[]) public stakes;
    mapping(address => uint256) public userWeight; // weight value is 0 for each user at beginning
    mapping(address => uint256) public stakingLimits;
    mapping(uint256 => uint256) public termPoints;
    uint256 public rewardPerSecond;
    mapping(address => bool) public isAdmin;
    mapping(address => bool) public isWhitelisted;

    event StakeStarted(address indexed staker, uint256 amount, uint256 endTime);
    event StakeEnded(address indexed staker, uint256 amount, uint256 reward);
    event AdminAdded(address indexed admin);
    event AdminRemoved(address indexed admin);
    event WhitelistAdded(address indexed user);
    event WhitelistRemoved(address indexed user);

    event StakingLimitChanged(address indexed user, uint256 newLimit);

    // Define staking status
    enum StakingStatus {
        INACTIVE,
        ACTIVE
    }
    StakingStatus public stakingStatus = StakingStatus.INACTIVE;

    uint256 public totalStakingAmount = 0; // Total staked tokens

    uint256 public contractStartTime;

    // uint256 public totalDebt;

    constructor(
        IERC20 _token,
        uint256 _rewardPerSecond,
        uint256[] memory _terms
    ) {
        token = _token;
        rewardPerSecond = _rewardPerSecond;
        for (uint256 i = 0; i < _terms.length; i++) {
            terms.push(_terms[i]);
        }
        isAdmin[msg.sender] = true;
        emit AdminAdded(msg.sender);
    }

    modifier onlyAdmin() {
        require(isAdmin[msg.sender], "Not an admin");
        _;
    }

    function enterStaking(uint256 _amount, uint256 _endTime) public {
        require(
            stakingStatus == StakingStatus.ACTIVE,
            "Contract is currently not active"
        );
        require(_amount > 0, "Stake amount must be greater than 0");
        require(isWhitelisted[msg.sender], "Not whitelisted");
        require(
            _endTime > block.timestamp && isInTerms(_endTime),
            "Invalid end time"
        );
        require(
            stakes[msg.sender].length < stakingLimits[msg.sender],
            "Exceeded staking limit"
        );

        token.transferFrom(msg.sender, address(this), _amount);

        stakes[msg.sender].push(
            Stake({
                amount: _amount,
                startTime: block.timestamp,
                endTime: _endTime,
                startTerm: 0,
                weight: 0,
                points: new uint256[](0)
            })
        );

        uint256 stakeIndex = stakes[msg.sender].length - 1;
        Stake storage stake = stakes[msg.sender][stakeIndex];

        // Increase weight based on number of full terms the user joins
        // uint256 duration = _endTime - block.timestamp;
        uint256 weight = (_endTime - block.timestamp) / 1 days;
        stake.weight = (userWeight[msg.sender] + 100) * weight; // default is 100 for each user

        bool startTermIsSet = false;

        for (uint256 i = 0; i < totalTerms; i++) {
            if (terms[i] > stake.endTime) {
                break;
            }
            // inside term

            if (terms[i] > stake.startTime && terms[i] <= stake.endTime) {
                if (!startTermIsSet) {
                    stake.startTerm = i;
                    startTermIsSet = true;
                }
                uint256 termStart = max(
                    stake.startTime,
                    i > 0 ? terms[i - 1] : 0
                );
                uint256 termEnd = min(stake.endTime, terms[i]);
                uint256 termDuration = (termEnd - termStart) / 1 days;
                uint256 points = (_amount * stake.weight * termDuration);
                stake.points.push(points);
                termPoints[terms[i]] += points;
            }
        }
        totalStakingAmount += _amount;
        emit StakeStarted(msg.sender, _amount, _endTime);
    }

    function calculateRewardForStake(
        Stake storage stake
    ) internal view returns (uint256) {
        uint256 totalReward = 0;

        for (uint256 i = stake.startTerm; i < totalTerms - 1; i++) {
            uint256 pointsIndex = i - stake.startTerm;
            if (terms[i] > stake.endTime) {
                break;
            }
            console.log("stake.points: %s", stake.points[pointsIndex]);

            if (stake.points[pointsIndex] == 0) {
                continue;
            }
            // first term should use contracStartTime instead of stk.startTime
            uint256 termStart = pointsIndex == 0
                ? contractStartTime
                : terms[i - 1];
            uint256 termEnd = min(terms[i], stake.endTime); // Assuming terms are consecutive
            uint256 termDuration = termEnd - termStart;

            uint256 totalRewardForTerm = termDuration * rewardPerSecond;
            uint256 termReward = (totalRewardForTerm *
                stake.points[pointsIndex]) / termPoints[terms[i]];

            totalReward += termReward;
        }
        console.log("total stake Reward: %s", totalReward);
        return totalReward;
    }

    function calculateRewardForSpecificStake(
        address _address,
        uint256 stakeIndex
    ) public view returns (uint256) {
        require(stakeIndex < stakes[_address].length, "Invalid stake index");

        Stake storage stake = stakes[_address][stakeIndex];

        return calculateRewardForStake(stake);
    }

    function calculateReward(address _address) public view returns (uint256) {
        uint256 totalReward = 0;
        for (
            uint stakeIndex = 0;
            stakeIndex < stakes[_address].length;
            stakeIndex++
        ) {
            Stake storage stake = stakes[_address][stakeIndex];
            totalReward += calculateRewardForStake(stake);
        }
        return totalReward;
    }

    function getNearestExpiredStakingTime(
        address _address
    ) public view returns (uint256) {
        uint256 nearestExpiredTime = type(uint256).max; // initialize with maximum possible uint256 value

        // Loop over all stakes for the user
        for (
            uint256 stakeIndex = 0;
            stakeIndex < stakes[_address].length;
            stakeIndex++
        ) {
            Stake storage stake = stakes[_address][stakeIndex];

            // If stake has expired and its endTime is less than current nearestExpiredTime, update it
            if (
                block.timestamp > stake.endTime &&
                stake.endTime < nearestExpiredTime
            ) {
                nearestExpiredTime = stake.endTime;
            }
        }

        // Return 0 if no expired stakes are found
        if (nearestExpiredTime == type(uint256).max) {
            return 0;
        } else {
            return nearestExpiredTime;
        }
    }

    function exitStaking() public {
        require(
            stakingStatus == StakingStatus.ACTIVE,
            "Contract is currently not active"
        );
        require(stakes[msg.sender].length > 0, "You have no stakes");

        // Find nearest expired staking time
        uint256 nearestExpiredTime = getNearestExpiredStakingTime(msg.sender);

        // Check if current timestamp is less than the nearest expired staking time
        require(
            block.timestamp >= nearestExpiredTime && nearestExpiredTime != 0,
            "You cannot exit staking before the nearest staking period has ended"
        );

        uint256 totalAmount = 0;
        uint256 totalReward = 0;

        // Initialize a new array to keep stakes that are not ended yet
        Stake[] memory notEndedStakes = new Stake[](stakes[msg.sender].length);
        uint256 notEndedStakesCount = 0;

        // Loop over all stakes for the user and clean up
        for (
            uint256 stakeIndex = 0;
            stakeIndex < stakes[msg.sender].length;
            stakeIndex++
        ) {
            Stake storage stake = stakes[msg.sender][stakeIndex];
            // Skip the stake if it has not ended yet or if it does not exist
            if (block.timestamp < stake.endTime) {
                notEndedStakes[notEndedStakesCount] = stake;
                notEndedStakesCount++;
                continue;
            }

            // Calculate reward for this particular stake
            totalReward += calculateRewardForStake(stake);

            totalAmount += stake.amount;

            for (uint256 i = 0; i < stake.points.length; i++) {
                stake.points[i] = 0;
            }
            stake.amount = 0;
        }

        // Clear the user's stakes array in storage
        delete stakes[msg.sender];

        // Copy the stakes that haven't ended yet from memory to storage
        for (uint256 i = 0; i < notEndedStakesCount; i++) {
            if (notEndedStakes[i].amount > 0)
                stakes[msg.sender].push(notEndedStakes[i]);
        }
        totalStakingAmount -= totalAmount;

        // Transfer the total reward and staked amount
        token.transfer(msg.sender, totalAmount + totalReward);
        emit StakeEnded(msg.sender, totalAmount, totalReward);
    }

    function getCurrentAPY(address _address) public view returns (uint256) {
        uint256 totalDuration = 0;
        uint256 totalAmount = 0;

        for (
            uint256 stakeIndex = 0;
            stakeIndex < stakes[_address].length;
            stakeIndex++
        ) {
            Stake storage stake = stakes[_address][stakeIndex];
            totalDuration += stake.endTime - stake.startTime;
            totalAmount += stake.amount;
        }

        // Return zero if totalDuration is 0 to avoid division by zero error
        if (totalDuration == 0) {
            return 0;
        }

        uint256 totalReward = calculateReward(_address);

        uint256 decimalReward = totalReward * DECIMAL_PRECISION * 100;
        uint256 rewardPerAmount = decimalReward / totalAmount;
        uint256 dailyReward = rewardPerAmount / totalDuration;
        uint256 annualReward = dailyReward * 365; // assuming days
        uint256 result = annualReward / DECIMAL_PRECISION;

        // Calculate APY as the proportion of the user's reward to their staked amount, normalized to a year
        return result;
    }

    function getStakePoints(
        address user,
        uint256 term
    ) public view returns (uint256[] memory) {
        require(term < stakes[user].length, "Stake index out of bounds");
        return stakes[user][term].points;
    }

    function setRewardRate(uint256 _rewardPerSecond) public onlyOwner {
        rewardPerSecond = _rewardPerSecond;
    }

    function startStaking() public onlyOwner {
        stakingStatus = StakingStatus.ACTIVE;
        contractStartTime = block.timestamp;
    }

    function pauseStaking() public onlyOwner {
        stakingStatus = StakingStatus.INACTIVE;
    }

    function unpauseStaking() public onlyOwner {
        stakingStatus = StakingStatus.ACTIVE;
    }

    function getTotalStakingAmount() public view returns (uint256) {
        return totalStakingAmount;
    }

    function getTotalAPY() public view returns (uint256) {
        // The total APY is simply the reward per second times the number of seconds in a year, divided by the total staking amount
        // This is a simplified calculation and assumes that a year has exactly 365 days
        if (totalStakingAmount == 0) {
            return 0;
        }
        return ((rewardPerSecond * 365 days) * 100) / totalStakingAmount;
    }

    function getTotalReward() public view returns (uint256) {
        uint256 duration = block.timestamp - contractStartTime;
        return duration * rewardPerSecond;
    }

    function getNumberOfStakes(address _address) public view returns (uint256) {
        return stakes[_address].length;
    }

    function addAdmin(address _admin) public onlyOwner {
        isAdmin[_admin] = true;
        emit AdminAdded(_admin);
    }

    function removeAdmin(address _admin) public onlyOwner {
        isAdmin[_admin] = false;
        emit AdminRemoved(_admin);
    }

    function batchAddWhitelist(address[] memory users) public onlyAdmin {
        for (uint256 i = 0; i < users.length; i++) {
            if (isWhitelisted[users[i]]) {
                // no need to add if already whitelisted
                continue;
            }
            isWhitelisted[users[i]] = true;
            stakingLimits[users[i]] = 2; // make default staking limit 2
            emit WhitelistAdded(users[i]);
        }
    }

    function removeWhitelist(address user) public onlyAdmin {
        isWhitelisted[user] = false;
        stakingLimits[user] = 0;
        userWeight[user] = 0;
        emit WhitelistRemoved(user);
    }

    // Function to update the userWeight for a specific address
    function setUserWeight(address user, uint256 weight) public onlyAdmin {
        userWeight[user] = weight;
    }

    function setStakingLimit(
        address _addr,
        uint256 _newLimit
    ) public onlyAdmin {
        require(
            stakingLimits[_addr] < _newLimit,
            "Can only increase staking limit"
        );
        stakingLimits[_addr] = _newLimit;
        emit StakingLimitChanged(_addr, _newLimit); // Emit the event after successfully changing the limit
    }

    function addTerm(uint256 newTerm) public onlyOwner {
        require(newTerm > block.timestamp, "Term should be in the future");

        uint256 i;
        for (i = 0; i < terms.length; i++) {
            if (terms[i] > newTerm) {
                break;
            }
        }

        terms.push(terms[terms.length - 1]); // duplicate last element at the end

        // move all elements from i to the end one position to the right
        for (uint256 j = terms.length - 1; j > i; j--) {
            terms[j] = terms[j - 1];
        }

        terms[i] = newTerm;
    }

    function withdrawTokens(uint256 amount) public onlyOwner {
        token.transfer(owner(), amount);
    }

    function withdrawEthers(uint256 amount) public onlyOwner {
        payable(owner()).transfer(amount);
    }

    function min(uint256 a, uint256 b) private pure returns (uint256) {
        return a < b ? a : b;
    }

    function max(uint256 a, uint256 b) private pure returns (uint256) {
        return a > b ? a : b;
    }

    function isInTerms(uint256 _endTime) private view returns (bool) {
        for (uint256 i = 0; i < terms.length; i++) {
            if (terms[i] == _endTime) {
                return true;
            }
        }
        return false;
    }
}
