contract BCLToken is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) private _isExcludedFromFee;

    mapping(address => bool) private _isExcluded;
    address[] private _excluded;

    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal = 2100000 * 1e9;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;
    
    string private _name = "BCL Token";
    string private _symbol = "BCL";
    uint8 private _decimals = 9;

    uint256 public _taxFee = 2; // whole network dividend

    IPancakeRouter02 public pancakeV2Router;

    address public wBNB = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    
    address public pancakePair_wBNB; // wBNB-lp token address
    
   // 7 significant addresses
    address public airdrop = 0x36E658a8a3217040E4EeeBfC3FaD40644CE3B42a;
    address public marketing = 0x5FbFfB8A323658c14bB292fF8A67b926ebb61FFC;
    address public platform = 0xe523E79d45b2a2779cF4E3c0355316d30ff9B2dD;
    address public taibao = 0x6b3395C490A9011D62426298F6A16624E40438dA;
    address public node = 0x3fffD784Fe6FB2125c458f6bF64aD0f2AEC1aa10;
    address public sub_node =0xd34364779895E97dcE24dAaE32c817734E5eBcE6;
    
    
    // fee to each address
    mapping(address=>uint256) public divisor;
    
    // Anti-arbitrage and disturbance
    mapping(address=>bool) public antiArbitrage;
    
    uint256 public burnRate = 5; // burn ratio for each trade
    uint256 public accBurned; // accumulating burned amount
    uint256 public holder_n; // number of holder addresses    
    
  

    constructor() public {
        _rOwned[_msgSender()] = _rTotal;
        holder_n += 1;
        
        IPancakeRouter02 _pancakeRouter = IPancakeRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a uniswap pair for this new token
        pancakePair_wBNB = IPancakeFactory(_pancakeRouter.factory()).createPair(address(this), _pancakeRouter.WETH());
        // set the rest of the contract variables
        pancakeV2Router = _pancakeRouter;

        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;

        emit Transfer(address(0), _msgSender(), _tTotal);
        
        // initialize dividend rate
        divisor[airdrop] = 1;
        divisor[marketing] = 2;
        divisor[node] = 2;
        divisor[sub_node] = 3;
    
        divisor[taibao] = 5;
        divisor[platform] = 10;
    
        
        
    }
    
    function setDivAddr(address[6] calldata addr) public onlyOwner {
        (airdrop,marketing,platform,taibao,node,sub_node) = (addr[0],addr[1],addr[2],addr[3],addr[4],addr[5]);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function isExcludedFromReward(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }
    // formally, it is private
    function _getRate() public view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (
                _rOwned[_excluded[i]] > rSupply ||
                _tOwned[_excluded[i]] > tSupply
            ) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

   

    function tokenFromReflection(uint256 rAmount)
        public
        view
        returns (uint256)
    {
        require(
            rAmount <= _rTotal,
            "Amount must be less than total reflections"
        );
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner() {
        require(!_isExcluded[account], "Account is already excluded");
        if (_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeInReward(address account) external onlyOwner() {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

   
    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }
    
    function isExcludedFromFee(address account) public view returns(bool){
        return _isExcludedFromFee[account];
    }

    function setTaxFeePercent(uint256 taxFee) external onlyOwner() {
        _taxFee = taxFee;
    }
    
    function setAnti_arbitrage(address[] calldata addr, bool status) public onlyOwner{
        for(uint i=0;i<addr.length;i++){antiArbitrage[addr[i]] = status;}
    }
    
    
    function _mint(address account, uint256 tAmount) private{
        if(balanceOf(account)==0){holder_n += 1;}
        
        uint256 rAmount = tAmount.mul(_getRate());
        _rOwned[account] = _rOwned[account].add(rAmount);
        _rTotal = _rTotal.add(rAmount);
        if(_isExcluded[account]){_tOwned[account] = _tOwned[account].add(tAmount);}
        emit Mint(account, tAmount, rAmount);
        
        
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }
 
    
    function _approve(address owner, address spender, uint amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    
    
    
    function _transfer(address from, address to, uint256 tAmount) private{
        require(!antiArbitrage[from] && !antiArbitrage[to], "either from or to addresses illegal");
        require(from != address(0), "ERC20: transfer from the zero address");
        require(tAmount > 0, "Transfer amount must be greater than zero");
        
        uint256 currentRate = _getRate();
        uint256 tBurn;
        
        // whitelist
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){_transferExecution(from, to, tAmount,0);}
        // sell
        else if(to == pancakePair_wBNB){
            
            uint256 p = divisor[platform];
            if(accBurned<50000*1e9){
                p = divisor[platform].add(20);
            }else if(accBurned<100000*1e9){
                p = divisor[platform].add(10);
            }
            
            uint256 costRate = p + divisor[taibao];
            
            _rOwned[taibao] = _rOwned[taibao].add(divisor[taibao].mul(tAmount).mul(currentRate).div(100));
            _rOwned[platform] = _rOwned[platform].add(p.mul(tAmount).mul(currentRate).div(100));
            if(_isExcludedFromFee[taibao]){
                _tOwned[taibao] = _tOwned[taibao].add(divisor[taibao].mul(tAmount).div(100));
            }
            
            if(_isExcludedFromFee[platform]){
                _rOwned[platform] = _rOwned[platform].add(p.mul(tAmount).mul(currentRate).div(100));

            }
            
            _transferExecution(from, to, tAmount, costRate);
            
            
        }
        
        // buy or usual transfer
        else{
            
            uint256 costRate = burnRate + _taxFee + divisor[airdrop] + divisor[marketing] + divisor[node] + divisor[sub_node];
            // burn 5 %
            uint256 rBurn = burnRate.mul(tAmount).mul(currentRate).div(100);
            tBurn = burnRate.mul(tAmount).div(100);
            
            _rOwned[address(0)] += rBurn;
            _tOwned[address(0)] += tBurn;
            accBurned = accBurned.add(tBurn);
            
            emit Transfer(from, address(0), tBurn);
            
            
            // tax fee
            uint tFee = _taxFee.mul(tAmount).div(100);
            uint rFee = tFee.mul(currentRate);
            _reflectFee(rFee, tFee);
                        
            // divisor
            _rOwned[marketing] = _rOwned[marketing].add(divisor[marketing].mul(tAmount).mul(currentRate).div(100));
            _rOwned[airdrop] = _rOwned[airdrop].add(divisor[airdrop].mul(tAmount).mul(currentRate).div(100));
            _rOwned[node] = _rOwned[node].add(divisor[node].mul(tAmount).mul(currentRate).div(100));
            _rOwned[sub_node] = _rOwned[sub_node].add(divisor[sub_node].mul(tAmount).mul(currentRate).div(100));
            if(_isExcluded[marketing]){_tOwned[marketing].add(divisor[marketing].mul(tAmount).div(100));}
            if(_isExcluded[airdrop]){_tOwned[airdrop].add(divisor[airdrop].mul(tAmount).div(100));}
            if(_isExcluded[node]){_tOwned[node].add(divisor[node].mul(tAmount).div(100));}
            if(_isExcluded[sub_node]){_tOwned[sub_node].add(divisor[sub_node].mul(tAmount).div(100));}
            
            _transferExecution(from, to, tAmount, costRate);

            
        }
        
        emit Transfer(from, to, tAmount.sub(tBurn));
        
        
    }
    
    event TransferExecution(address indexed from, address indexed to, uint256 indexed amount, uint256 receiveAmount);
    event Mint(address indexed account, uint256 indexed tAmount, uint256 indexed rAmount);
    
    function _transferExecution(address from, address to, uint256 tAmount, uint256 costRate) private{
        if(balanceOf(to)==0){holder_n += 1;}
        uint256 currentRate = _getRate();
        uint256 rAmount_from = tAmount.mul(currentRate);
        uint256 rAmount_to = tAmount.mul(uint256(100).sub(costRate)).div(100).mul(currentRate);
    
        _rOwned[from] = _rOwned[from].sub(rAmount_from);
        _rOwned[to] = _rOwned[to].add(rAmount_to);
        
        if(_isExcluded[from]){_tOwned[from] = _tOwned[from].sub(tAmount);}
        if(_isExcluded[to]){_tOwned[to] = _tOwned[to].add(tAmount.mul(uint256(100).sub(costRate)).div(100));}
        
        if(balanceOf(from)==0 && holder_n>0){holder_n -= 1;}
        emit TransferExecution(from, to, tAmount, tAmount.mul(uint256(100).sub(costRate)).div(100));
    }
}
