    "/contracts/VestingCreator.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

import "@openzeppelin/contracts/proxy/TransparentUpgradeableProxy.sol";
import "./utils/AdminableUpgradeable.sol";

import "./interfaces/IVestingCreator.sol";

/**
 * @title Vesting creator contract
 * @dev Allow deal contract to create vesting contract
 */
contract VestingCreator is IVestingCreator, AdminableUpgradeable {

    string internal constant VESTING_HASH = "__DealVesting_init(address,address,address,uint256,uint256,uint256)";

    address public vestingImpl;
    address public proxyAdmin;

    event SetProxyAdmin(address proxyAdmin);
    event SetVestingImpl(address vestingImpl);

    event VestingCreated(
        address vestingAddress,
        address dealAddress,
        address dealOwner,
        address rewardToken,
        uint256 start,
        uint256 interval,
        uint256 duration
    );

    function __VestingCreator_init(address _vestingImpl, address _proxyAdmin) public initializer {
        __Adminable_init();

        vestingImpl = _vestingImpl;
        proxyAdmin = _proxyAdmin;
    }

    function createVesting(
        address _dealAddress,
        address _dealOwner,
        IERC20Upgradeable _rewardToken,
        uint256 _start,
        uint256 _interval,
        uint256 _duration
    ) external override returns (address) {
        require(vestingImpl != address(0), "createVesting: vestingImpl has not been set");

        bytes memory data = abi.encodeWithSignature(VESTING_HASH, _dealAddress, _dealOwner, address(_rewardToken), _start, _interval, _duration);
        TransparentUpgradeableProxy proxy = new TransparentUpgradeableProxy(vestingImpl, proxyAdmin, data);

        emit VestingCreated(
            address(proxy),
            _dealAddress,
            _dealOwner,
            address(_rewardToken),
            _start,
            _interval,
            _duration
        );

        return address(proxy);
    }

    function setVestingImpl(address _vestingImpl) external onlyOwnerOrAdmin {
        vestingImpl = _vestingImpl;

        emit SetVestingImpl(_vestingImpl);
    }

    function setProxyAdmin(address _proxyAdmin) external onlyOwnerOrAdmin {
        proxyAdmin = _proxyAdmin;

        emit SetProxyAdmin(_proxyAdmin);
    }

    // ============ Version Control ============
    function version() external pure returns (uint256) {
        return 101; // 1.0.1
    }
}"
