pragma solidity ^0.6.0;


import './ERC20Capped.sol';
import './Ownable.sol';

import './CelestialNameServiceInterface.sol';
import "./CelestialInterface.sol";

contract Celestial is CelestialInterface, ERC20Capped, Ownable {

    string constant _name_ = "Celestial";
    string constant _symbol_ = "Celt";
    uint256 constant _cap_ = 1 * 10 ** 9 * 10 ** 18;

    address internal _cns;

    mapping(address => bool) internal _blockList;
    mapping(address => bool) internal _privilegeList;

    bool internal _supportTransfer;

    uint256 internal _transferTxs;
    uint256 internal _transferAmounts;
    mapping(address => bool) internal _interactRecord;
    uint256 internal _interactAmounts;

    constructor(
        address cns_,
        address[] memory init_holders_,
        uint256[] memory init_percents_
    ) public
    ERC20Capped(_cap_)
    ERC20(_name_, _symbol_){

        _cns = cns_;

        _supportTransfer = true;

        require(init_holders_.length == init_percents_.length, "init length");
        uint256 totalPercent = 0;
        uint256 totalCap = 0;
        for (uint256 i = 0; i < init_holders_.length; i++) {

            uint256 toMint = _cap_.mul(init_percents_[i]).div(100);
            totalPercent = totalPercent.add(init_percents_[i]);
            totalCap = totalCap.add(toMint);
            _mint(init_holders_[i], toMint);
        }
        require(totalPercent == 100, "init_percents_?");
        require(totalCap == _cap_, "init_cap_?");

    }

    modifier interact(){
        if (!_interactRecord[msg.sender]) {
            _interactRecord[msg.sender] = true;
            _interactAmounts = _interactAmounts.add(1);
        }
        _;
    }

    function cns() override view external returns (address){
        return _cns;
    }

    function isBlocked(address who) override view external returns (bool){
        return _blockList[who];
    }

    function isPrivileged(address who) override view external returns (bool){
        return _privilegeList[who];
    }

    function setBlockLists(address[] memory whos, bool[] memory flags) override external onlyOwner {
        require(whos.length == flags.length, "param length");

        for (uint256 i = 0; i < whos.length; i++) {
            _blockList[whos[i]] = flags[i];

        }
    }

    function setPrivilegeLists(address[] memory whos, bool[] memory flags) override external onlyOwner {
        require(whos.length == flags.length, "param length");

        for (uint256 i = 0; i < whos.length; i++) {
            _privilegeList[whos[i]] = flags[i];

        }
    }

    function setSupportTransfer(bool supportTransfer_) override external onlyOwner {
        _supportTransfer = supportTransfer_;
    }

    function setCns(address cns_) override external onlyOwner {
        _cns = cns_;
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) virtual override interact internal {

        if (_cns != address(0) &&
            (
            CelestialNameServiceInterface(_cns).isMiner(from) ||
            CelestialNameServiceInterface(_cns).isErc20Pool(from) ||
            CelestialNameServiceInterface(_cns).isNftPool(from) ||
            CelestialNameServiceInterface(_cns).isNftLimitedPool(from) ||
            CelestialNameServiceInterface(_cns).isMiner(to) ||
            CelestialNameServiceInterface(_cns).isErc20Pool(to) ||
            CelestialNameServiceInterface(_cns).isNftPool(to) ||
            CelestialNameServiceInterface(_cns).isNftLimitedPool(to)
            )
        ) {

        } else if (_privilegeList[from] || _privilegeList[to]) {
            //privilege overrides all following condition
        } else if (from != address(0) && to != address(0)) {
            require(_supportTransfer, "celt, supportTransfer");
        } else if (_blockList[from] || _blockList[to]) {
            revert("celt, blocked");
        }

        //impossible to hit the 2^256 :)
        _transferTxs = _transferTxs.add(1);

        //unreasonable to hit the 2^256 :)
        _transferAmounts = _transferAmounts.add(amount);

        super._beforeTokenTransfer(from, to, amount);
    }


    function transferTxs() override view external returns (uint256){
        return _transferTxs;
    }

    function transferAmounts() override view external returns (uint256){
        return _transferAmounts;
    }
}
