    "contracts/nonUpgradeable/Pool/Standard-TBased/TagPool.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.2;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";


import "../../../interfaces/iTagToken.sol";
import "../../../interfaces/iTagNFT.sol";
import "../../Interface/iTagStaking.sol";
import "./iTagPoolStaking.sol";

contract TagPoolT1 is AccessControl {
    using SafeCast for *;
    using SafeMath for uint256;
    using Address for address;

    bytes32 public constant POOL_ROLE = keccak256("POOL_ROLE");

    iTagToken FT;

    iTagNFT NFT;

    iTagStaking staking;

    IERC20 PoolToken;

    uint256 private UnStakePTVal;       // TAG moon Required to unstake a NFT

    address private stakingAddress;

    address private AdminFeeAddress;

    address private LiquiditityFeeAddress;

    uint256 private AdminFee;

    uint256 private LiquiditityFee;

    mapping(address => mapping(uint256 => bool)) private Addr2NFT;

    mapping(uint256 => address) private NFT2Addr;

    uint256 private TCoin;

    bool private isPStaking;

    address private dead = 0x000000000000000000000000000000000000dEaD;

    modifier isValidUnstakeValue  (){
        require( ( PoolToken.allowance( msg.sender , address( this ) ) >= UnStakePTVal ) && ( PoolToken.balanceOf(msg.sender) >= UnStakePTVal )  , "TagPool : Does not have allownace for enough Pool Tokens " );
        _;
    }

    modifier isStaking() {
        require(address(staking) == msg.sender, "TagPool : Can only be fired by staking contract");
        _;
    }

    modifier hasOperatorApproval() {
        require(NFT.isApprovedForAll(msg.sender, address(this)), "TagPool : Operator Approval Required");
        _;
    }

    modifier isValidStake() {
        require(NFT.balanceOf(address(this)) <= staking.fetchNFTLimit(), "TagPool : NFT Limit Reached");
        _;
    }

    modifier isStaker(uint256 _ids) {
        require(NFT2Addr[_ids] == msg.sender ,"TagPool : In Valid staker");
        require(PoolToken.balanceOf( msg.sender) >= UnStakePTVal , "TagPool : Does not have enough Pool Tokens");
        _;
    }

    modifier isValidAdminFee(uint256 _val) {
        uint256 val = _val.add(LiquiditityFee);
        require(val <= 10000, "TagPool : Invalid Admin Fee");
        _;
    }

    modifier isValidLiquidityFee(uint256 _val) {
        uint256 val = _val.add(AdminFee);
        require(val <= 10000, "TagPool : Invalid Liquidity Fee");
        _;
    }

    event deposit(uint256 _ctr, uint256 _amt);

    constructor(
        address _FTAddress,
        address _NFTAddress,
        address _staking,
        address _poolToken,
        address _DFA
    ) public AccessControl() {
        FT = iTagToken(_FTAddress);
        NFT = iTagNFT(_NFTAddress);
        staking = iTagStaking(_staking);
        PoolToken = IERC20(_poolToken);

        _setupRole(DEFAULT_ADMIN_ROLE, _DFA);
        _setRoleAdmin(POOL_ROLE, DEFAULT_ADMIN_ROLE);

    }

    fallback() external payable {}

    //FETCH FX
    function fetchFT (  ) public view virtual returns ( address ){
        return address(FT);
    }

    function fetchNFT (  ) public view virtual returns ( address ){
        return address(NFT);
    }

    function fetchStakingContract (  ) public view virtual returns ( address ){
        return address(staking);
    }

    function fetchUnstakeValue ( uint256 _value ) public view virtual returns ( uint256 ){
        return UnStakePTVal;
    }

    function fetchStaking() public view virtual returns (address) {
        return address(staking);
    }

    function fetchstakingAddress() public view virtual returns (address) {
        return stakingAddress;
    }

    function fetchAdminFeeAddress() public view virtual returns (address) {
        return AdminFeeAddress;
    }

    function fetchAdminFee() public view  virtual returns (uint256) {
        return AdminFee;
    }

    function fetchLiquidityFee() public view  virtual returns (uint256) {
        return LiquiditityFee;
    }

    function fetchLiquidityFeeAddress() public view  virtual returns (address) {
        return LiquiditityFeeAddress;
    }

    function fetchTokensOwner(uint256 _id) public view virtual returns (address) {
        return NFT2Addr[_id];
    }

    function fetchStakedTokens(address _addr, uint256 _id) public view virtual returns (bool) {
        return Addr2NFT[_addr][_id];
    }

    function fetchDispatchClaim() public view virtual returns (uint256) {
        return TCoin;
    }

    function fetchPStaking() public view virtual returns (bool) {
        return isPStaking;
    }
    //FETCH FX

    //POOL FX
    function PoolDispatch(uint256 _ctr, uint256 _amt) public virtual isStaking {
        TCoin = TCoin.add(_amt);
        iwithdrawTAG();
        emit deposit(_ctr, _amt);
    }

    // Add Cool down period for pools
    function claim() public payable virtual onlyRole(POOL_ROLE) {
        uint256 fees = staking.fetchFees();
        require(fees <= msg.value, "TagPool: Insufficient fees");
        staking.claim{ value: msg.value }();
    }

    function iwithdrawTAG() internal virtual {
        uint256 claimval = TCoin;
        uint256 comm = claimval.mul(AdminFee).div(10000);
        uint256 liquid = claimval.mul(LiquiditityFee).div(10000);
        uint256 balance = claimval.sub(comm);
        balance = balance.sub(liquid);
        if( isPStaking == true ){
            iTagPoolStaking pool = iTagPoolStaking(stakingAddress);
            pool.TDeposit(balance);
        }
        FT.transfer(AdminFeeAddress, comm);
        FT.transfer(LiquiditityFeeAddress, liquid);
        FT.transfer(stakingAddress, balance);
        TCoin = 0;
    }
    //POOL FX

    // Stake FX

    function iStake(uint256 ids) internal virtual {
        Addr2NFT[msg.sender][ids] = true;
        NFT2Addr[ids] = msg.sender;
        NFT.transferFrom(msg.sender, address(this), ids);
    }

    function iunStake(uint256 ids) internal virtual isStaker(ids) {
        Addr2NFT[msg.sender][ids] = false;
        NFT2Addr[ids] = address(0);
        NFT.transferFrom(address(this), msg.sender, ids);
        PoolToken.transferFrom( msg.sender, dead , UnStakePTVal ); // Add Burn
    }

    function stake(uint256 ids) public virtual hasOperatorApproval isValidStake {
        iStake(ids);
    }

    function stakeBulk(uint256[] memory ids) public virtual hasOperatorApproval isValidStake{
        uint256 ctr = 0;
        uint256 len = ids.length;
        while (ctr < len) {
            iStake(ids[ctr]);
            ctr = ctr.add(1);
        }
    }

    function unstake(uint256 ids) public virtual {
        iunStake(ids);
    }
    // Stake FX

    // Setters
    function setUnstakeValue ( uint256 _value ) public virtual onlyRole(DEFAULT_ADMIN_ROLE){
        UnStakePTVal = _value;
    }

    function setAdminFee(uint256 _AdminFee) public virtual isValidAdminFee(_AdminFee) onlyRole(POOL_ROLE) {
        AdminFee = _AdminFee;
    }

    function setAdminFeeAddress(address _AdminFeeAddr) public virtual onlyRole(POOL_ROLE) {
        AdminFeeAddress = _AdminFeeAddr;
    }

    function setLiquidityFee(uint256 _LQF) public virtual isValidLiquidityFee(_LQF) onlyRole(POOL_ROLE) {
        LiquiditityFee = _LQF;
    }

    function setLiquidityFeeAddress(address _LQFA) public virtual onlyRole(POOL_ROLE) {
        LiquiditityFeeAddress = _LQFA;
    }

    function setstakingAddress(address _forwardingAddr) public virtual onlyRole(POOL_ROLE) {
        stakingAddress = _forwardingAddr;
    }

    function withdrawDust(address _to, uint256 _value) public virtual onlyRole(POOL_ROLE) {
        payable(_to).transfer(_value);
    }

    function setPStake(bool value) public virtual onlyRole(POOL_ROLE) {
        isPStaking = value;
    }
    // Setters

}
"
