}

interface AggregatorV3Interface {
    function latestRoundData()
        external
        view
        returns (
            uint80 roundId,
            uint answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        );
}


contract SpectivePair is ISpectivePair, SpectiveERC20 {
    using SafeMath  for uint;
    using UQ112x112 for uint224;

    uint public constant MINIMUM_LIQUIDITY = 10**3;
    bytes4 private constant SELECTOR = bytes4(keccak256(bytes('transfer(address,uint256)')));

    address public token0;
    address public token1;

    uint112 private reserve0;           // uses single storage slot, accessible via getReserves
    uint112 private reserve1;           // uses single storage slot, accessible via getReserves
    uint32  private blockTimestampLast; // uses single storage slot, accessible via getReserves

    uint public price0CumulativeLast;
    uint public price1CumulativeLast;

    uint private unlocked = 1;
    modifier lock() {
        require(unlocked == 1, 'Spective: LOCKED');
        unlocked = 0;
        _;
        unlocked = 1;
    }

    function getReserves() public view returns (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast) {
        _reserve0 = reserve0;
        _reserve1 = reserve1;
        _blockTimestampLast = blockTimestampLast;
    }

    function _safeTransfer(address token, address to, uint value) private {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(SELECTOR, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'Spective: TRANSFER_FAILED');
    }

    event Mint(address indexed sender, uint amount0, uint amount1);
    event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint amount0In,
        uint amount1In,
        uint amount0Out,
        uint amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    constructor() public {
        factory = msg.sender;
    }

    // called once by the factory at time of deployment
    function initialize(address _token0, address _token1) external {
        require(msg.sender == factory, 'Spective: FORBIDDEN'); // sufficient check
        token0 = _token0;
        token1 = _token1;
    }

    // update reserves and, on the first call per block, price accumulators
    function _update(uint balance0, uint balance1, uint112 _reserve0, uint112 _reserve1) private {
        require(balance0 <= uint112(-1) && balance1 <= uint112(-1), 'Spective: OVERFLOW');
        uint32 blockTimestamp = uint32(block.timestamp % 2**32);
        uint32 timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired
        if (timeElapsed > 0 && _reserve0 != 0 && _reserve1 != 0) {
            // * never overflows, and + overflow is desired
            price0CumulativeLast += uint(UQ112x112.encode(_reserve1).uqdiv(_reserve0)) * timeElapsed;
            price1CumulativeLast += uint(UQ112x112.encode(_reserve0).uqdiv(_reserve1)) * timeElapsed;
        }
        reserve0 = uint112(balance0);
        reserve1 = uint112(balance1);
        blockTimestampLast = blockTimestamp;
        emit Sync(reserve0, reserve1);
    }

    function claimable(address account) public view returns(bool) {
        return lastRewardTime[account] < now - ISpectiveRouter(ISpectiveFactory(factory).router()).LPRewardPeriod() && getPendingReward(account) > 0;
    }

    function claim(address account) external returns(uint reward){
        require(claimable(account), "there is no amount to claim");
        require(msg.sender == ISpectiveFactory(factory).router(), "only Router can call this function");
        updateReward(account);
        reward = pendingReward[account];
        pendingReward[account] = 0;
        uint LPRewarPeriod = ISpectiveRouter(ISpectiveFactory(factory).router()).LPRewardPeriod();
        lastRewardTime[account] += ((now - lastRewardTime[account]) / LPRewarPeriod) * LPRewarPeriod;
    }

    function updateReward(address account) public {
        // 1e-9 = 10 * 1e18 / (100 * 1e8 * 1e18)
        uint tokenPriceInUSD = ISpectiveFactory(factory).getPrice(token0);
        uint tokenAmountInUSD = 0;
        if(totalSupply != 0)
            tokenAmountInUSD = 2 * tokenPriceInUSD * IERC20(token0).balanceOf(address(this)) * balanceOf[account] / totalSupply;
        pendingReward[account] += tokenAmountInUSD.mul(now - lastActionTime[account]).div(1e9).div(ISpectiveRouter(ISpectiveFactory(factory).router()).LPRewardPeriod());
        lastActionTime[account] = now;
    }

    function getPendingReward(address account) public view returns(uint) {
        uint tokenPriceInUSD = ISpectiveFactory(factory).getPrice(token0);
        uint tokenAmountInUSD = 0;
        if(totalSupply != 0)
            tokenAmountInUSD = 2 * tokenPriceInUSD * IERC20(token0).balanceOf(address(this)) * balanceOf[account] / totalSupply;
        return pendingReward[account] + tokenAmountInUSD.mul(now - lastActionTime[account]).div(1e9).div(ISpectiveRouter(ISpectiveFactory(factory).router()).LPRewardPeriod());
    }

    // this low-level function should be called from a contract which performs important safety checks
    function mint(address to) external lock returns (uint liquidity) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        uint balance0 = IERC20(token0).balanceOf(address(this));
        uint balance1 = IERC20(token1).balanceOf(address(this));
        uint amount0 = balance0.sub(_reserve0);
        uint amount1 = balance1.sub(_reserve1);

        uint _totalSupply = totalSupply; // gas savings
        if (_totalSupply == 0) {
            liquidity = Math.sqrt(amount0.mul(amount1)).sub(MINIMUM_LIQUIDITY);
           _mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
        } else {
            liquidity = Math.min(amount0.mul(_totalSupply) / _reserve0, amount1.mul(_totalSupply) / _reserve1);
        }
        require(liquidity > 0, 'Spective: INSUFFICIENT_LIQUIDITY_MINTED');
        updateReward(to);
        _mint(to, liquidity);

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Mint(msg.sender, amount0, amount1);
    }

    // this low-level function should be called from a contract which performs important safety checks
    function burn(address to) external lock returns (uint amount0, uint amount1) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        address _token0 = token0;                                // gas savings
        address _token1 = token1;                                // gas savings
        uint balance0 = IERC20(_token0).balanceOf(address(this));
        uint balance1 = IERC20(_token1).balanceOf(address(this));
        uint liquidity = balanceOf[address(this)];

        uint _totalSupply = totalSupply; // gas savings
        amount0 = liquidity.mul(balance0) / _totalSupply; // using balances ensures pro-rata distribution
        amount1 = liquidity.mul(balance1) / _totalSupply; // using balances ensures pro-rata distribution
        require(amount0 > 0 && amount1 > 0, 'Spective: INSUFFICIENT_LIQUIDITY_BURNED');
        _burn(address(this), liquidity);
        _safeTransfer(_token0, to, amount0);
        _safeTransfer(_token1, to, amount1);
        balance0 = IERC20(_token0).balanceOf(address(this));
        balance1 = IERC20(_token1).balanceOf(address(this));

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Burn(msg.sender, amount0, amount1, to);
    }

    // this low-level function should be called from a contract which performs important safety checks
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data, uint feePercent) external lock {
        require(amount0Out > 0 || amount1Out > 0, 'Spective: INSUFFICIENT_OUTPUT_AMOUNT');
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        require(amount0Out < _reserve0 && amount1Out < _reserve1, 'Spective: INSUFFICIENT_LIQUIDITY');
    
        uint balance0;
        uint balance1;
        { // scope for _token{0,1}, avoids stack too deep errors
        address _token0 = token0;
        address _token1 = token1;
        require(to != _token0 && to != _token1, 'Spective: INVALID_TO');
        if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out); // optimistically transfer tokens
        if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out); // optimistically transfer tokens
        if (data.length > 0) ISpectiveCallee(to).spectiveCall(msg.sender, amount0Out, amount1Out, data);
        balance0 = IERC20(_token0).balanceOf(address(this));
        balance1 = IERC20(_token1).balanceOf(address(this));
        }

        uint amount0In = balance0 > _reserve0 - amount0Out ? balance0 - (_reserve0 - amount0Out) : 0;
        uint amount1In = balance1 > _reserve1 - amount1Out ? balance1 - (_reserve1 - amount1Out) : 0;
        
        if(feePercent == 75) {
            if(amount0In > 0)
            {
                IERC20(token0).transfer(ISpectiveRouter(ISpectiveFactory(factory).router()).reserveAddr(), amount0In.mul(10).div(10000));
                IERC20(token0).transfer(ISpectiveRouter(ISpectiveFactory(factory).router()).burnAddr(), amount0In.mul(5).div(10000));
            }
            if(amount1In > 0) {
                IERC20(token1).transfer(ISpectiveRouter(ISpectiveFactory(factory).router()).reserveAddr(), amount1In.mul(10).div(10000));
                IERC20(token1).transfer(ISpectiveRouter(ISpectiveFactory(factory).router()).burnAddr(), amount1In.mul(5).div(10000));
            }
        }
        require(feePercent <= 75, 'Spective: INVALID FEE');
        require(amount0In > 0 || amount1In > 0, 'Spective: INSUFFICIENT_INPUT_AMOUNT');
        { // scope for reserve{0,1}Adjusted, avoids stack too deep errors
        uint balance0Adjusted = (balance0.mul(10000).sub(amount0In.mul(feePercent)));
        uint balance1Adjusted = (balance1.mul(10000).sub(amount1In.mul(feePercent)));
        require(balance0Adjusted.mul(balance1Adjusted) >= uint(_reserve0).mul(_reserve1).mul(10000**2), 'Spective: K');
        }

        _update(IERC20(token0).balanceOf(address(this)), IERC20(token1).balanceOf(address(this)), reserve0, reserve1);
        emitSwap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
    }

    function emitSwap(address accout, uint amount0In, uint amount1In, uint amount0Out, uint amount1Out, address to)internal {
        emit Swap(accout, amount0In, amount1In, amount0Out, amount1Out, to);
    }

    // force balances to match reserves
    function skim(address to) external lock {
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        _safeTransfer(_token0, to, IERC20(_token0).balanceOf(address(this)).sub(reserve0));
        _safeTransfer(_token1, to, IERC20(_token1).balanceOf(address(this)).sub(reserve1));
    }

    // force reserves to match balances
    function sync() external lock {
        _update(IERC20(token0).balanceOf(address(this)), IERC20(token1).balanceOf(address(this)), reserve0, reserve1);
    }
}

contract SpectiveFactory is ISpectiveFactory {
    bytes private constant creationcode = type(SpectivePair).creationCode;
    bytes private constant encoded = abi.encodePacked(type(SpectivePair).creationCode);
    bytes32 public constant INIT_CODE_PAIR_HASH = keccak256(abi.encodePacked(type(SpectivePair).creationCode));

    AggregatorV3Interface internal oracle;
    address public router;
    address public owner;

    address public WETH;

    mapping(address => mapping(address => address)) public getPair;
    address[] public allPairs;

    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    constructor(AggregatorV3Interface _oracle, address _WETH) public {
        oracle = _oracle;
        WETH = _WETH;
        owner = msg.sender;
    }

    function allPairsLength() external view returns (uint) {
        return allPairs.length;
    }

    function createPair(address tokenA, address tokenB) external returns (address pair) {
        require(tokenA != tokenB, 'Spective: IDENTICAL_ADDRESSES');
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'Spective: ZERO_ADDRESS');
        require(getPair[token0][token1] == address(0), 'Spective: PAIR_EXISTS'); // single check is sufficient
        bytes memory bytecode = type(SpectivePair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        assembly {
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        ISpectivePair(pair).initialize(token0, token1);
        getPair[token0][token1] = pair;
        getPair[token1][token0] = pair; // populate mapping in the reverse direction
        allPairs.push(pair);
        emit PairCreated(token0, token1, pair, allPairs.length);
    }

    function setRouter(address _router) public {
        require(msg.sender == owner, "only owner can call this function");
        router = _router;
    }

    function _getPriceFromOracle() internal view returns (uint) {
        (, uint price, , , ) = oracle.latestRoundData();
        return price;
    }

    function getPrice(address token) public view returns (uint){
        uint bnbPrice = _getPriceFromOracle();
        if(token == WETH) {
            return bnbPrice;
        }
        else {
            address pair = getPair[token][WETH];
            if(pair == address(0))
                return 0;
            uint reserveBNB;
            uint reserveToken;
            if(ISpectivePair(pair).token0() == WETH) {
                (reserveBNB, reserveToken,) = ISpectivePair(pair).getReserves();
            }
            else {
                (reserveToken, reserveBNB,) = ISpectivePair(pair).getReserves();
            }
            if(reserveToken == 0)
                return 0;
            return reserveBNB * bnbPrice / reserveToken ;
        }
    }
}
