{"AccessControl.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./Ownable.sol";

abstract contract AccessControl is Ownable{
  
    mapping(address => bool) private _operators;

    event SetOperator(address indexed add, bool value);

    constructor () {
        _setOperator(_msgSender(), true);
    }

    function setOperator(address _operator, bool _v) external onlyOwner {
        _setOperator(_operator, _v);
    }

    function _setOperator(address _operator, bool _v) internal {
        _operators[_operator] = _v;
        emit SetOperator(_operator, _v);
    }

    function isOperator(address _address) public view returns(bool){
        return  _operators[_address];
    }

    modifier onlyOperator() {
        require(_operators[msg.sender]);
        _;
    }
}"},"Context.sol":{"content":"// SPDX-License-Identifier: MIT
