pragma solidity ^0.8.4;

import "./IERC20.sol";
import "./INFT.sol";
import "./SafeMath.sol";
import "./Context.sol";
import "./AccessControl.sol";
import "./IPancakeRouter02.sol";
import "./IUniswapV2Factory.sol";
import "./IPancakePair.sol";

    /**
     * @dev NFT for tool interfaces related to dapp design
     *
     * Separation of NFT tool contract from NFT contract business
     * Provide NFT interface for NFT calls
     */
contract OPATool is Context, AccessControl {

    using SafeMath for uint256;
    address private _OPAToken;
    address private _USDTAddress;
    address private _NFTAddress;
	address private _collAccount;
    address private _uniswapV2Pair;
    address private DEAD = address(0x000000000000000000000000000000000000dEaD);

    bool private inMintAndSwap;

    IPancakeRouter02 private _uniswapV2Router;

    uint256 private constant MAX_UINT256 = type(uint256).max;

    uint256 private _nftInitPriceForUsdt;
    uint256 private _nftPriceIncreasePercent = 300; 
    uint256 private _nftBurnPercentOne = 5000;
	uint256 private _nftBurnPercentTwo = 7000;
	uint256 private _nftBurnPercentTri = 9000;
	uint256 private _usdtSwapRate = 5000;
	uint256 private _usdtReferralRate = 2500;
    uint256 private _baseRate = 10000;
	
    mapping (uint256 => uint256) private _tokenTradeCount;
    mapping (uint256 => uint256) private _tokenAuctionStat;

    event nftMintForUsdtLog(uint256 tokenId);
	event swapAllUsdtLog(uint256 usdtAmount);
	event swapAndCollectLog(uint256 usdtAmount);
	event transferUsdtLog(address indexed gather, address indexed referral, uint256 amount);

    modifier lockMintAndSwap {
        inMintAndSwap = true;
        _;
        inMintAndSwap = false;
    }

    constructor (
        address RouterAddress, 
        address USDTAddress,
        address OPAToken,
		address NFTAddress,
		address collAccount
    ) {
        _USDTAddress = USDTAddress;
        _OPAToken = OPAToken;
        _NFTAddress = NFTAddress;
		_collAccount = collAccount;
		
        IPancakeRouter02 uniswapV2Router = IPancakeRouter02(RouterAddress);
        _uniswapV2Router = uniswapV2Router;
		IERC20(USDTAddress).approve(address(uniswapV2Router), MAX_UINT256);
		_uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).getPair(OPAToken, USDTAddress);
		
        _nftInitPriceForUsdt = 200 * 10 ** IERC20(USDTAddress).decimals();
    }

    receive() external payable {}

	function swapAllUsdt(uint256 usdtAmount) public lockMintAndSwap returns (bool) {
        require(usdtAmount > 0, "ERC20: subscribe usdt amount equal zero");
        IERC20 USDT = IERC20(_USDTAddress);
        require(USDT.transferFrom(msg.sender, address(this), usdtAmount), "ERC20: transferFrom to contract failure");
        uint256 usdtBalance = USDT.balanceOf(address(this));
        if(usdtBalance > 0) {
            address[] memory path = new address[](2);
            path[0] = _USDTAddress;
            path[1] = _OPAToken;
            _uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                usdtBalance,
                0,
                path,
                DEAD,
                block.timestamp
            );
        }
		emit swapAllUsdtLog(usdtAmount);
        return true;
    }
	
	function swapAndCollect(uint256 usdtAmount) public lockMintAndSwap returns (bool) {
        require(usdtAmount > 0, "ERC20: subscribe usdt amount equal zero");
        IERC20 USDT = IERC20(_USDTAddress);
        require(USDT.transferFrom(msg.sender, address(this), usdtAmount), "ERC20: transferFrom to contract failure");
        uint256 usdtBalance = USDT.balanceOf(address(this));
        if(usdtBalance > 0) {
            uint256 usdtSwapAmount =  usdtBalance.mul(_usdtSwapRate).div(_baseRate);
            if(usdtSwapAmount > 0) {
                address[] memory path = new address[](2);
                path[0] = _USDTAddress;
                path[1] = _OPAToken;
                _uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                    usdtSwapAmount,
                    0,
                    path,
                    DEAD,
                    block.timestamp
                );
            }
            USDT.transfer(_collAccount, usdtBalance.sub(usdtSwapAmount));
        }
		emit swapAndCollectLog(usdtAmount);
        return true;
    }
	
	function transferUsdt(address gather, address referral, uint256 amount) external returns (bool) {
		require(amount > 0, "ERC20: subscribe usdt amount equal zero");
		uint256 referralAmount = 0;
		IERC20 USDT = IERC20(_USDTAddress);
		if(referral != address(0)) {
			referralAmount = amount.mul(_usdtReferralRate).div(_baseRate);
			if (referralAmount > 0) {
				require(USDT.transferFrom(msg.sender, referral, referralAmount), "ERC20: transferFrom to contract failure");
			}
		} 
        require(USDT.transferFrom(msg.sender, gather, amount.sub(referralAmount)), "ERC20: transferFrom to contract failure");
		emit transferUsdtLog(gather, referral, amount);
		return true;
	}

    function nftMintForUsdt(uint256 tokenId, uint256 usdtAmount) public lockMintAndSwap returns (bool) {
        require(usdtAmount >= _nftInitPriceForUsdt, "ERC20: subscribe usdt amount less than nftInitPriceForUsdt");
        IERC20 USDT = IERC20(_USDTAddress);
        require(USDT.transferFrom(msg.sender, address(this), usdtAmount), "ERC20: transferFrom to contract failure");
        uint256 usdtBalance = USDT.balanceOf(address(this));
        if(usdtBalance > 0) {
            address[] memory path = new address[](2);
            path[0] = _USDTAddress;
            path[1] = _OPAToken;
            _uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                usdtBalance,
                0,
                path,
                DEAD,
                block.timestamp
            );
        }
        INFT nft = INFT(_NFTAddress);
        nft.mint(_collAccount, tokenId);
        emit nftMintForUsdtLog(tokenId);
        return true;
    }

    function nftBurnForMintToken(uint256 tokenId, address account) public onlyOperator returns (bool) {
        INFT nft = INFT(_NFTAddress);
		require(nft.isApprovedOrOwner(_msgSender(), tokenId) && isOperator(_msgSender()), "OPATool: Not operation permission");
        uint256 swapAmount = calculateSwapAmount(tokenId);
		IERC20(_OPAToken).transfer(account, swapAmount);
        nft.burn(tokenId);
        return true;
    }

	function calculateSwapAmount(uint256 tokenId) public view returns (uint256) {
		uint256 nftAuctionStat = _tokenAuctionStat[tokenId];
        uint256 nftCurrentPrice = calculateNFTPrice(tokenId);
        uint256 tokenCurrentPrice = getTokenPrice();
        uint256 swapAmount = nftCurrentPrice.mul(10 ** IERC20(_OPAToken).decimals()).div(tokenCurrentPrice);
        if(nftAuctionStat != 2) {
			uint256 nftInitPrice = _nftInitPriceForUsdt;
			if (nftCurrentPrice > nftInitPrice * 100) {
				swapAmount = swapAmount.mul(_nftBurnPercentTri).div(_baseRate);
			} else if (nftCurrentPrice > nftInitPrice * 10) {
				swapAmount = swapAmount.mul(_nftBurnPercentTwo).div(_baseRate);
			} else {
				swapAmount = swapAmount.mul(_nftBurnPercentOne).div(_baseRate);
			}
        }
        return swapAmount;
    }
	
    function calculateNFTPrice(uint256 tokenId) public view returns (uint256) {
        uint256 tradeCount = _tokenTradeCount[tokenId];
        uint256 currentPrice = _nftInitPriceForUsdt;
        for(uint256 i = 0; i < tradeCount; i++) {
            uint256 increasePrice = currentPrice.mul(_nftPriceIncreasePercent).div(_baseRate);
            currentPrice += increasePrice;
        }
        return currentPrice;
    }

	function getTokenPrice() public view returns (uint256 price) {
        IPancakePair swapPair = IPancakePair(_uniswapV2Pair);
        (uint256 reserve0,uint256 reserve1,) = swapPair.getReserves();
        address token = _OPAToken;
        if (reserve0 > 0) {
            uint256 usdtAmount;
            uint256 tokenAmount;
            if (token < _USDTAddress) {
                tokenAmount = reserve0;
                usdtAmount = reserve1;
            } else {
                tokenAmount = reserve1;
                usdtAmount = reserve0;
            }
            price = 10 ** IERC20(token).decimals() * usdtAmount / tokenAmount;
        }
    }
	
    function setTokenTradeCount(uint256 tokenId) external onlyOperator returns (bool) {
        require(INFT(_NFTAddress).exists(tokenId), "OPATool: tradeCount set for nonexistent token");
        _tokenTradeCount[tokenId] += 1;
		uint256 nftAuctionStat = _tokenAuctionStat[tokenId];
		if (nftAuctionStat == 2) {
			_tokenAuctionStat[tokenId] = 0;
		}
        return true;
    }

    function getTokenTradeCount(uint256 tokenId) public view returns (uint256) {
        require(INFT(_NFTAddress).exists(tokenId), "OPATool: tradeCount query for nonexistent token");
        return _tokenTradeCount[tokenId];
    }

    function setTokenAuctionStat(uint256 tokenId, uint256 stat) external onlyOperator returns (bool) {
        require(INFT(_NFTAddress).exists(tokenId), "OPATool: auctionStat set for nonexistent token");
        require(stat == 0 || stat == 1 || stat == 2, "OPATool: stat value must be 0, 1, 2");
        _tokenAuctionStat[tokenId] = stat;
        return true;
    }

    function getTokenAuctionStat(uint256 tokenId) public view returns (uint256) {
        require(INFT(_NFTAddress).exists(tokenId), "OPATool: auctionStat query for nonexistent token");
        return _tokenAuctionStat[tokenId];
    }
	
	function setNftInitPriceForUsdt(uint256 amount) external onlyOwner {
        _nftInitPriceForUsdt = amount;
    }

    function setNftPriceIncreasePercent(uint256 rate) external onlyOwner {
        _nftPriceIncreasePercent = rate;
    }

    function setNftBurnPercentOne(uint256 rate) external onlyOwner {
        _nftBurnPercentOne = rate;
    }
	
	function setNftBurnPercentTwo(uint256 rate) external onlyOwner {
        _nftBurnPercentTwo = rate;
    }
	
	function setNftBurnPercentTri(uint256 rate) external onlyOwner {
        _nftBurnPercentTri = rate;
    }
	
	function setUsdtSwapRate(uint256 rate) external onlyOwner {
		_usdtSwapRate = rate;
	}
	
	function setUsdtReferralRate(uint256 rate) external onlyOwner {
		_usdtReferralRate = rate;
	}

	function setCollAccount(address addr) external onlyOwner {
        _collAccount = addr;
    }

    function claimBalance(address to) external onlyOwner {
        payable(to).transfer(address(this).balance);
    }

    function claimToken(address token, uint256 amount, address to) external onlyOwner {
        IERC20(token).transfer(to, amount);
    }

}"},"Ownable.sol":{"content":"// SPDX-License-Identifier: MIT
