pragma solidity 0.6.12;

contract OperonStaking is Ownable,ReentrancyGuard {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;
    // Info of each user.
    // Info of each user.
    struct UserInfo {
        uint256 amount;         // How many LP tokens the user has provided.
        uint256 lastRewardBlock; 
        uint256 unlockUntil;
        uint256 unstaked;
        uint256 unstakeUntil;
    }

    // Info of each pool.
    struct PoolInfo {
        IBEP20 lpToken;            // Address of LP token or token contract.
        uint256 lastRewardBlock; 
        bool isTokenonly;
        bool emergencyWithdrawnable;
    }


    // The Operon TOKEN!
    Operon public Oro;

    uint256 public perDaysecond;

    uint256 public timeInterval;

    uint256 public OperonAPYLP;

    uint256 public OperonAPY;
  
    PoolInfo[] public poolInfo;
   
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
   
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event Claim(address indexed user, uint256 indexed pid, uint256 amount);
    event UpdateEmission(uint256 amount,uint256 amountlp);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    constructor(
        Operon _Oro,
        uint256 _Operonapy,
        uint256 _Operonapylp,
        uint256 _perDaysecond,
        uint256 _timeInterval
    ) public {
        Oro = _Oro;
        OperonAPY = _Operonapy;
        OperonAPYLP = _Operonapylp;
        perDaysecond = _perDaysecond;
        timeInterval = _timeInterval;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function canUnstake(uint256 _pid,address _addr) public view returns (bool) {
        // PoolInfo storage pool = poolInfo[_pid];
          UserInfo storage user = userInfo[_pid][_addr];
        if (user.amount > 0) {
            if(user.unlockUntil > block.timestamp){
                return false;
            }else{
                 return true;
            }
          }
    }

    function getBlocktimeStamp() external view returns (uint256) {
        return block.timestamp;
    }

    function add(
        IBEP20 _lpToken,
        bool _isToken
    ) public onlyOwner {
        poolInfo.push(PoolInfo({
            lpToken: _lpToken,
            isTokenonly : _isToken,
            emergencyWithdrawnable : false,
            lastRewardBlock: block.timestamp
        }));
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to)
        public
        view
        returns (uint256)
    {
        return _to.sub(_from);
    }


    function pendingOperon(uint256 _pid, address _user)
        public
        view
        returns (uint256)
    {
        uint256 pending = 0;
        PoolInfo storage pool = poolInfo[_pid];
        uint256 _apy = pool.isTokenonly ? OperonAPY : OperonAPYLP;
        UserInfo storage user = userInfo[_pid][_user];
        if (block.timestamp > pool.lastRewardBlock && user.amount > 0) {
            uint256 multiplier = getMultiplier(
                user.lastRewardBlock,
                block.timestamp
            );
            uint256 perYear = perDaysecond.mul(365);
            uint256 OroPerSecond = _apy.div(perYear);
            uint256 OroUserReward = user.amount.mul(OroPerSecond).div(100);
            pending = OroUserReward.mul(multiplier).div(1000000000000);
        }else{
            pending = 0;
        }
        return pending;
    }



    // Deposit LP tokens to MasterChef for Oro allocation.
    function deposit(uint256 _pid, uint256 _amount) public nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        _harvest(_pid);
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            user.amount = user.amount.add(_amount);
            user.lastRewardBlock = block.timestamp;
        }
        emit Deposit(msg.sender, _pid, _amount);
    }

    function Harvest(uint256 _pid) public nonReentrant {
        _harvest(_pid);
    }

    function _harvest(uint256 _pid) internal {
        uint256 OroBal = Oro.balanceOf(address(this));
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        uint256 _apy = pool.isTokenonly ? OperonAPY : OperonAPYLP;
          if (user.amount > 0) {
              uint256 multiplier = getMultiplier(
                user.lastRewardBlock,
                block.timestamp
            );
            uint256 perYear = perDaysecond.mul(365);
            uint256 OroPerSecond = _apy.div(perYear);
            uint256 OroUserReward = user.amount.mul(OroPerSecond).div(100);
            uint256 pending = OroUserReward.mul(multiplier).div(1000000000000);
            require( OroBal > pending ,"Contract doest contain enough balance");
            if(pending > 0) {
                user.lastRewardBlock = block.timestamp;
                safeOperonTransfer(msg.sender, pending);
            }else{
                revert("You are not allow to harvest");
            }
        }
    }

    function withdraw(uint256 _pid, uint256 _amount) public nonReentrant {
        uint256 OroBal = Oro.balanceOf(address(this));
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        require( OroBal > _amount,"Contract doest contain enough balance");
        require(pendingOperon(_pid,msg.sender) > 0, "withdraw: not good");
        if (_amount > 0) {
             _harvest(_pid);
            if(pool.isTokenonly){
                user.amount = user.amount.sub(_amount);
                user.unstaked += _amount;
                user.unstakeUntil = block.timestamp.add(timeInterval);
            }else{
                user.amount = user.amount.sub(_amount);
                pool.lpToken.safeTransfer(address(msg.sender), _amount);
            } 
        }
        emit Withdraw(msg.sender, _pid, _amount);
    }

     function claim(uint256 _pid) public nonReentrant {
        uint256 OroBal = Oro.balanceOf(address(this));
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.unstaked > 0, "withdraw: not good");
        require( OroBal > user.unstaked,"Contract doest contain enough balance");
        require(pool.isTokenonly,"Only allow Oro stake");
        if (user.unstaked > 0 && user.unstakeUntil < block.timestamp) {
                pool.lpToken.safeTransfer(address(msg.sender), user.unstaked);
                user.unstaked = 0;
                user.unstakeUntil = 0;
                
        }
        emit Claim(msg.sender, _pid, user.unstaked);
    }

    // Withdraw without caring about locking and rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public nonReentrant {
        PoolInfo storage pool = poolInfo[_pid];
        require(
            pool.emergencyWithdrawnable,
            "!emergencyWithdrawnable not allowed"
        );
        UserInfo storage user = userInfo[_pid][msg.sender];
        uint256 amount = user.amount;
        user.amount = 0;
        user.lastRewardBlock = block.timestamp;
        pool.lpToken.safeTransfer(address(msg.sender), amount);
        emit EmergencyWithdraw(msg.sender, _pid, amount);
    }

     function setEmergencyWithdrawnable(uint256 _pid, bool _allowed)
        public
        onlyOwner
    {
        poolInfo[_pid].emergencyWithdrawnable = _allowed;
    }

    // Safe Oro transfer function, just in case if rounding error causes pool to not have enough Oros.
    function safeOperonTransfer(address _to, uint256 _amount) internal {
        uint256 OroBal = Oro.balanceOf(address(this));
        bool transferSuccess = false;
        if (_amount > OroBal) {
            transferSuccess = Oro.transfer(_to, OroBal);
        } else {
            transferSuccess = Oro.transfer(_to, _amount);
        }
        require(transferSuccess, "safeOperonTransfer: Transfer failed");
    }

     function safeWithdrawTransfer(address _to, uint256 _amount)
        public
        onlyOwner
    {
         uint256 OroBal = Oro.balanceOf(address(this));
        bool transferSuccess = false;
        if (_amount > OroBal) {
            transferSuccess = Oro.transfer(_to, OroBal);
        } else {
            transferSuccess = Oro.transfer(_to, _amount);
        }
        require(transferSuccess, "safeOperonTransfer: Transfer failed");
    }

    //Pancake has to add hidden dummy pools inorder to alter the emission, here we make it simple and transparent to all.
    function updateEmissionAPY(uint256 _Operonapy,uint256 _Operonapylp) public onlyOwner {
        OperonAPY = _Operonapy;
        OperonAPYLP = _Operonapylp;
        emit UpdateEmission(_Operonapy,_Operonapylp);
    }
      function updateTimeInterval(uint256 _timeInterval) public onlyOwner {
        timeInterval = _timeInterval;
    }

}
