contract BEP20KYC is Context, IBEP20, Ownable {
    using SafeMath for uint256;
    mapping(address => AccountLock) lockAmount;
    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => uint256) private distBalances; //list of distributed balance of each address to calculate restricted amount
    uint256 private distributed = 0;
    mapping(address => bool) public _lockedAddresses;
    uint256 private _totalSupply;
    uint8 public _decimals;
    string public _symbol;
    string public _name;

    constructor() {
        _name = "KUAI YAN Coin";
        _symbol = "KYC";
        _decimals = 18;
        _totalSupply = 1000000000 * 1E18;
        _balances[address(this)] = _totalSupply;
        emit Transfer(address(0), address(this), _totalSupply);
    }

    /**
     * @dev Returns the bep token owner.
     */
    function getOwner() external view returns (address) {
        return owner();
    }

    /**
     * @dev Returns the token decimals.
     */
    function decimals() external view returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Returns the token symbol.
     */
    function symbol() external view returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the token name.
     */
    function name() external view returns (string memory) {
        return _name;
    }

    /**
     * @dev See {BEP20-totalSupply}.
     */
    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {BEP20-balanceOf}.
     */
    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev See {BEP20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount)
        external
        returns (bool)
    {
        require(
            block.timestamp > lockAmount[msg.sender].start,
            "The token is still locked"
        );
        if (
            _balances[msg.sender] >= amount &&
            _balances[recipient] + amount > _balances[recipient]
        ) {
            uint256 _freeAmount = getFreeAmount(msg.sender);
            require(_freeAmount >= amount);
           

            _transfer(_msgSender(), recipient, amount);
            return true;
        } else {
            return false;
        }
    }

    function distribute(
        uint256 _amount,
        address account,
        uint256 _start,
        uint256 _end
    ) public onlyOwner {
        require(_balances[address(this)] >= _amount);
        require(!_lockedAddresses[account], "Address already distribute");
        require(
            _start <= _end,
            "The start time cannot be greater than the end time"
        );
        lockAmount[account].amount = _amount;
        lockAmount[account].end = _end;
        lockAmount[account].start = _start;
        require(distributed + _amount <= _totalSupply);

        distributed += _amount;
        distBalances[account] += _amount;
         _transfer(address(this), account, _amount);
        _lockedAddresses[account] = true;
    }

    
    function RestrictedAmount(address account)
        internal
        view
        returns (uint256 noReleaseAmount)
    {
        AccountLock memory lock = lockAmount[account];
        if (block.timestamp <= lockAmount[account].start) {
            return lockAmount[account].amount;
        } else if (block.timestamp >= lockAmount[account].end) {
            return 0;
        } else {
            uint256 timeRemaining =lock.end- block.timestamp;
            uint256 totalReleaseTime = lock.end - lock.start;
            noReleaseAmount = (timeRemaining * lock.amount) / totalReleaseTime;
            return noReleaseAmount;
        }
    }

    function getRestrictedAmount(address account)
        public
        view
        returns (uint256 amount)
    {
        amount = RestrictedAmount(account);
        return amount;
    }

    function getFreeAmount(address account)
        public
        view
        returns (uint256 amount)
    {
        amount =  _balances[account] - RestrictedAmount(account);
        return amount;
    }

    /**
     * @dev See {BEP20-allowance}.
     */
    function allowance(address owner, address spender)
        external
        view
        returns (uint256)
    {
        return _allowances[owner][spender];
