    "contracts/access/DramAccessControl.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import {
    Initializable
} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { IDramAccessControl } from "./IDramAccessControl.sol";
import {
    ContextUpgradeable
} from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import {
    ERC165Upgradeable
} from "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";

import {
    StringsUpgradeable
} from "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";

/**
 * @notice Role based access control contract to manage Dram's protected functions
 */
abstract contract DramAccessControl is
    Initializable,
    IDramAccessControl,
    ContextUpgradeable,
    ERC165Upgradeable
{
    mapping(bytes32 => mapping(address => bool)) private _roles;

    address private _currentAdmin;
    address private _pendingAdmin;

    /**
     * @notice Admin is the most powerful role which is responsible for managing other roles.
     * Admin also have access to the highly protected functions.
     */
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    /**
     * @notice Role manager is also responsible for managing roles (except admin role).
     * Role manager can set the minting cap for the supply manager role.
     */
    bytes32 public constant ROLE_MANAGER_ROLE = keccak256("ROLE_MANAGER_ROLE");
    /**
     * @notice Supply manager is responsible for minting and burning Dram tokens.
     */
    bytes32 public constant SUPPLY_MANAGER_ROLE =
        keccak256("SUPPLY_MANAGER_ROLE");
    /**
     * @notice Regulatory manager is responsible for freezing and pausing functionalities.
     */
    bytes32 public constant REGULATORY_MANAGER_ROLE =
        keccak256("REGULATORY_MANAGER_ROLE");

    /**
     * @dev A modifier to check if the current msg.seder has the required role.
     * Role must be the result of keccak256.
     * @param role The role that needs to be checked for the caller
     */
    modifier onlyRole(bytes32 role) {
        _checkRole(role);
        _;
    }

    /**
     * @dev Like onlyRole but bypasses the check if the msg.sender is the admin.
     */
    modifier onlyRoleOrAdmin(bytes32 role) {
        _checkRoleOrAdmin(role);
        _;
    }

    // solhint-disable-next-line func-name-mixedcase
    function __DramAccessControl_init(
        address admin,
        address roleManager,
        address supplyManager,
        address regulatoryManager
    ) internal onlyInitializing {
        __DramAccessControl_init_unchained(
            admin,
            roleManager,
            supplyManager,
            regulatoryManager
        );
    }

    // solhint-disable-next-line func-name-mixedcase
    function __DramAccessControl_init_unchained(
        address admin,
        address roleManager,
        address supplyManager,
        address regulatoryManager
    ) internal onlyInitializing {
        _grantRole(ADMIN_ROLE, admin);
        _grantRole(ROLE_MANAGER_ROLE, roleManager);
        _grantRole(SUPPLY_MANAGER_ROLE, supplyManager);
        _grantRole(REGULATORY_MANAGER_ROLE, regulatoryManager);
    }

    /**
     * @inheritdoc ERC165Upgradeable
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override returns (bool) {
        return
            interfaceId == type(IDramAccessControl).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function hasRole(
        bytes32 role,
        address account
    ) public view virtual override returns (bool) {
        return _roles[role][account];
    }

    /**
     * @dev Calls the _checkRole with msg.sender as account.
     * @param role The role that needs to be checked
     */
    function _checkRole(bytes32 role) internal view virtual {
        _checkRole(role, _msgSender());
    }

    /**
     * @dev Like _checkRole but bypasses the check if the caller is admin.
     * @param role The role that needs to be checked
     */
    function _checkRoleOrAdmin(bytes32 role) internal view virtual {
        if (!hasRole(ADMIN_ROLE, _msgSender())) {
            _checkRole(role, _msgSender());
        }
    }

    /**
     * @dev Calls hasRole with role and account and throws error if the result is false.
     * @param role The role that needs to be checked
     * @param account Address that needs to have the required role
     */
    function _checkRole(bytes32 role, address account) internal view virtual {
        if (!hasRole(role, account)) {
            revert MissingRoleError(role, account);
        }
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function grantRole(
        bytes32 role,
        address account
    ) external virtual override onlyRoleOrAdmin(ROLE_MANAGER_ROLE) {
        if (role == ADMIN_ROLE) revert DirectAdminUpdateError();
        _grantRole(role, account);
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function revokeRole(
        bytes32 role,
        address account
    ) external virtual override onlyRoleOrAdmin(ROLE_MANAGER_ROLE) {
        if (role == ADMIN_ROLE) revert DirectAdminUpdateError();
        _revokeRole(role, account);
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function renounceRole(
        bytes32 role,
        address account
    ) external virtual override {
        if (role == ADMIN_ROLE) revert DirectAdminUpdateError();
        if (account != _msgSender()) revert NotSelfError();
        _revokeRole(role, _msgSender());
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function transferAdminRole(
        address newAdmin
    ) external virtual onlyRole(ADMIN_ROLE) {
        if (newAdmin == address(0) || newAdmin == _msgSender())
            revert InvalidAdminTransferError();
        _currentAdmin = _msgSender();
        _pendingAdmin = newAdmin;
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function acceptAdminRoleTransfer() external virtual {
        if (_pendingAdmin != _msgSender()) revert NotPendingAdminError();
        _revokeRole(ADMIN_ROLE, _currentAdmin);
        _grantRole(ADMIN_ROLE, _pendingAdmin);
        _deleteAdminTransfer();
        emit AdminRoleTransferred(_currentAdmin, _pendingAdmin);
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function getAdminRoleTransferInfo()
        external
        view
        virtual
        returns (address currentAdmin, address pendingAdmin)
    {
        currentAdmin = _currentAdmin;
        pendingAdmin = _pendingAdmin;
    }

    /**
     * @inheritdoc IDramAccessControl
     */
    function cancelAdminTransfer() external onlyRole(ADMIN_ROLE) {
        _deleteAdminTransfer();
    }

    /**
     * @dev Implementation of the role granting process as an internal function so that
     * other contract functions can call the logic directly without worrying about modifiers.
     * @param role The role to be granted
     * @param account Address that will have the role
     */
    function _grantRole(bytes32 role, address account) internal virtual {
        if (!hasRole(role, account)) {
            _roles[role][account] = true;
            emit RoleGranted(role, account, _msgSender());
        }
    }

    /**
     * @dev Implementation of the role revoking process as an internal function so that
     * other contract functions can call the logic directly without worrying about modifiers.
     * @param role The role to be revoked
     * @param account Address that won't have the role anymore
     */
    function _revokeRole(bytes32 role, address account) internal virtual {
        if (hasRole(role, account)) {
            _roles[role][account] = false;
            emit RoleRevoked(role, account, _msgSender());
        }
    }

    /**
     * @dev finishes or cancels the admin transferring process by deleting the states.
     */
    function _deleteAdminTransfer() internal virtual {
        delete _currentAdmin;
        delete _pendingAdmin;
    }

    uint256[47] private __gap;
}
"
    },
    "contracts/access/DramFreezable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import {
    Initializable
} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { IDramFreezable } from "./IDramFreezable.sol";
import {
    ContextUpgradeable
} from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";

/**
 * @notice An abstract contract provide freezing functionalities and modifiers
 */
abstract contract DramFreezable is
    Initializable,
    IDramFreezable,
    ContextUpgradeable
{
    mapping(address => bool) private _isFreezed;

    /**
     * @dev A modifier that checks if the account is not freezed, else reverts.
     */
    modifier whenNotFreezed(address account) {
        _requireNotFreezed(account);
        _;
    }

    /**
     * @dev A modifier that checks if the account is freezed, else reverts.
     */
    modifier whenFreezed(address account) {
        _requireFreezed(account);
        _;
    }

    // solhint-disable-next-line func-name-mixedcase
    function __DramFreezable_init() internal onlyInitializing {
        __DramFreezable_init_unchained();
    }

    // solhint-disable-next-line func-name-mixedcase, no-empty-blocks
    function __DramFreezable_init_unchained() internal onlyInitializing {}

    /**
     * @inheritdoc IDramFreezable
     */
    function isFreezed(
        address account
    ) public view virtual override returns (bool) {
        return _isFreezed[account];
    }

    /**
     * @dev Freezes an account.
     * @param account Address to be freezed.
     */
    function _freeze(address account) internal virtual whenNotFreezed(account) {
        _isFreezed[account] = true;
        emit Freezed(account, _msgSender());
    }

    /**
     * @dev Un-freezes an account.
     * @param account Address to be un-freezed.
     */
    function _unfreeze(address account) internal virtual whenFreezed(account) {
        _isFreezed[account] = false;
        emit Unfreezed(account, _msgSender());
    }

    /**
     * Checks if an account is freezed and reverts.
     * @param account Account to be checked
     */
    function _requireNotFreezed(address account) internal view {
        if (isFreezed(account)) {
            revert FreezedError();
        }
    }

    /**
     * Checks if an account is not freezed and reverts.
     * @param account Account to be checked
     */
    function _requireFreezed(address account) internal view {
        if (!isFreezed(account)) {
            revert NotFreezedError();
        }
    }

    uint256[49] private __gap;
}
"
    },
    "contracts/access/IDramAccessControl.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

interface IDramAccessControl {
    /**
     * @dev The `account` is missing a `role`.
     */
    error MissingRoleError(bytes32 role, address account);

    /**
     * @dev Happens when a caller wants to renounce a role from another address.
     */
    error NotSelfError();

    /**
     * @dev Happens when updating the ADMIN_ROLE directly.
     */
    error DirectAdminUpdateError();

    /**
     * @dev Happens when the acceptAdminRoleTransfer gets called when there is no
     * pending admin.
     */
    error NotPendingAdminError();

    /**
     * @dev Happens if new admin address is either zero or the msg.sender
     */
    error InvalidAdminTransferError();

    /**
     * @notice Gets emitted when an account gets a role.
     * @param role Granted role
     * @param account Address with the role
     * @param sender Operator who granted the role
     */
    event RoleGranted(
        bytes32 indexed role,
        address indexed account,
        address indexed sender
    );

    /**
     * @notice Gets emitted when a role is revoke from an account.
     * @param role Revoked role
     * @param account Address which had the role
     * @param sender Operator who revoked the role
     */
    event RoleRevoked(
        bytes32 indexed role,
        address indexed account,
        address indexed sender
    );

    /**
     * @notice Happens when the admin role transfer process gets finalized.
     * @param previousAdmin The account that had the ADMIN_ROLE
     * @param newAdmin The account that has the ADMIN_ROLE
     */
    event AdminRoleTransferred(
        address indexed previousAdmin,
        address indexed newAdmin
    );

    /**
     * @notice Returns a boolean indicating if the account has the requested role.
     * @param role Role to check
     * @param account Address that needs to be checked wether it has the role or not
     */
    function hasRole(
        bytes32 role,
        address account
    ) external view returns (bool);

    /**
     * @notice Grants one of the defined roles to an account.
     * NOTE: Can't use for admin role management!
     * @dev Protected by onlyRoleOrAdmin modifier.
     * @param role The role to be granted
     * @param account Address that will have the role
     */
    function grantRole(bytes32 role, address account) external;

    /**
     * @notice Revokes one of the defined roles from an account.
     * NOTE: Can't use for admin role management!
     * @dev Protected by onlyRoleOrAdmin modifier.
     * @param role The role to be revoked
     * @param account Address that won't have the role anymore
     */
    function revokeRole(bytes32 role, address account) external;

    /**
     * @notice Renounces one of the defined roles from an account.
     * An account with a role can call this to renounce the role from its address.
     * NOTE: Can't use for admin role management!
     * @dev the account should be the msg.sender.
     * @param role The role to be renounced
     * @param account The caller address that will renounce the role from itself
     */
    function renounceRole(bytes32 role, address account) external;

    /**
     * @notice Transfers admin role to another account.
     * NOTE: Only the one with admin role is able to call this function.
     * This function starts transferring admin to a new address and will be finalized
     * when acceptAdminRoleTransfer is called by the new admin account.
     * @dev Protected by onlyRole so that only admin can call this function.
     * @param newAdmin The address of the new admin
     */
    function transferAdminRole(address newAdmin) external;

    /**
     * @notice Accepts admin role to from account.
     * Only works when there is a pending admin.
     * This function ends transferring admin to a new address process.
     */
    function acceptAdminRoleTransfer() external;

    /**
     * @notice Returns current admin and the pending admin addresses.
     */
    function getAdminRoleTransferInfo()
        external
        view
        returns (address, address);

    /**
     * @notice If admin transferring process is either started by mistake or needed to be
     * cancelled, the current admin should call this function.
     * @dev Protected by onlyRole so that only admin can call this function.
     */
    function cancelAdminTransfer() external;
}
"
    },
    "contracts/access/IDramFreezable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

interface IDramFreezable {
    /**
     * @dev Happens when an account is in freezed state but the function requires a non-freezed account
     */
    error FreezedError();

    /**
     * @dev Happens when an account is in non-freezed state but the function requires a freezed account
     */
    error NotFreezedError();

    /**
     * @notice Gets emitted when `operator` freezes an `account`.
     * @param account freezed account
     * @param operator Address that freezed the account
     */
    event Freezed(address indexed account, address indexed operator);

    /**
     * @notice Gets emitted when `operator` un-freezes an `account`.
     * @param account Un-freezed account
     * @param operator Address that un-freezed the account
     */
    event Unfreezed(address indexed account, address indexed operator);

    /**
     * @notice Check if an account is in the freezed state.
     * @param account The account to be checked
     */
    function isFreezed(address account) external view returns (bool);
}
"
    },
    "contracts/Dram.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import {
    Initializable
} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {
    ERC20Upgradeable
} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {
    PausableUpgradeable
} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {
    ERC20PermitUpgradeable
} from "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PermitUpgradeable.sol";
import { DramAccessControl } from "./access/DramAccessControl.sol";
import { DramFreezable } from "./access/DramFreezable.sol";
import { DramMintable } from "./token/ERC20/extensions/DramMintable.sol";

// Status: Under development, Not for public use
contract Dram is
    Initializable,
    DramAccessControl,
    DramFreezable,
    ERC20Upgradeable,
    DramMintable,
    PausableUpgradeable,
    ERC20PermitUpgradeable
{
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address admin,
        address roleManager,
        address supplyManager,
        address regulatoryManager
    ) public initializer {
        __ERC20_init("Dram", "DRM");
        __Pausable_init();
        __ERC20Permit_init("Dram");
        __DramAccessControl_init(
            admin,
            roleManager,
            supplyManager,
            regulatoryManager
        );
        __DramMintable_init();
        __DramFreezable_init();
    }

    /**
     * @notice Pauses the smart contract.
     */
    function pause() external onlyRoleOrAdmin(REGULATORY_MANAGER_ROLE) {
        _pause();
    }

    /**
     * @notice Resumes the smart contract.
     */
    function unpause() external onlyRoleOrAdmin(REGULATORY_MANAGER_ROLE) {
        _unpause();
    }

    /**
     * @notice Freezes an account. A freezed account can't send any transaction related to
     * transferring tokens.
     * @dev Protected by onlyRoleOrAdmin, only admin and regulatory manager can call the function.
     * @param account Account to be freezed
     */
    function freeze(
        address account
    ) external onlyRoleOrAdmin(REGULATORY_MANAGER_ROLE) {
        _freeze(account);
    }

    /**
     * @notice Unfreezes an account.
     * @dev Protected by onlyRoleOrAdmin, only admin and regulatory manager can call the function.
     * @param account Account to be unfreezed
     */
    function unfreeze(
        address account
    ) external onlyRoleOrAdmin(REGULATORY_MANAGER_ROLE) {
        _unfreeze(account);
    }

    /**
     * @notice Increases the minting cap of an address by amount.
     * @dev Protected by onlyRoleOrAmin, so only admin and role manager can call the function.
     * @param operator Address that gets its minting cap gets increased
     * @param amount Value to add to the current minting cap
     */
    function increaseMintCap(
        address operator,
        uint256 amount
    ) external onlyRoleOrAdmin(ROLE_MANAGER_ROLE) {
        _increaseMintCap(operator, amount);
    }

    /**
     * @notice Decreases the minting cap of an address by amount.
     * @dev Protected by onlyRoleOrAmin, so only admin and role manager can call the function.
     * @param operator Address that gets its minting cap gets decreased
     * @param amount Value to subtract from the current minting cap
     */
    function decreaseMintCap(
        address operator,
        uint256 amount
    ) external onlyRoleOrAdmin(ROLE_MANAGER_ROLE) {
        _decreaseMintCap(operator, amount);
    }

    /**
     * @notice Sets the minting cap of an address and emits the associated event.
     * @dev Protected by onlyRoleOrAmin, so only admin and role manager can call the function.
     * @param operator Address that its minting cap will be changed
     * @param amount New minting cap
     */
    function setMintCap(
        address operator,
        uint256 amount
    ) external onlyRoleOrAdmin(ROLE_MANAGER_ROLE) {
        _setMintCap(operator, amount);
    }

    /**
     * @notice Mints tokens for an account.
     * @dev Protected by onlyRole, so only role manager can call the function.
     * @param to Account to send tokens to
     * @param amount Amount of the tokens to be minted
     */
    function mint(
        address to,
        uint256 amount
    ) external onlyRole(SUPPLY_MANAGER_ROLE) {
        _mint(to, amount);
    }

    /**
     * @notice Burns the caller tokens.
     * @dev Protected by onlyRole, so only role manager can call the function.
     * @param amount Amount of the tokens to be minted
     */
    function burn(uint256 amount) external onlyRole(SUPPLY_MANAGER_ROLE) {
        _burn(_msgSender(), amount);
    }

    /**
     * @notice Burns the tokens from an account.
     * @dev Protected by onlyRole, so only role manager can call the function.
     * @param account Address to burn tokens from
     * @param amount Amount of the tokens to be burned
     */
    function burnFrom(
        address account,
        uint256 amount
    ) external onlyRole(SUPPLY_MANAGER_ROLE) {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }

    function _mint(
        address account,
        uint256 amount
    ) internal virtual override(ERC20Upgradeable, DramMintable) {
        super._mint(account, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused whenNotFreezed(from) whenNotFreezed(to) {
        super._beforeTokenTransfer(from, to, amount);
    }
}
"
    },
    "contracts/token/ERC20/extensions/DramMintable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import {
    ERC20Upgradeable
} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {
    Initializable
} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { IDramMintable } from "./IDramMintable.sol";

/**
 * @notice An abstract ERC20 extension with custom logic for minting tokens.
 */
abstract contract DramMintable is
    Initializable,
    IDramMintable,
    ERC20Upgradeable
{
    mapping(address => uint256) private _mintCaps;

    // solhint-disable-next-line func-name-mixedcase
    function __DramMintable_init() internal onlyInitializing {
        __DramMintable_init_unchained();
    }

    // solhint-disable-next-line func-name-mixedcase, no-empty-blocks
    function __DramMintable_init_unchained() internal onlyInitializing {}

    /**
     * @inheritdoc IDramMintable
     */
    function mintCap(address operator) public view returns (uint256) {
        return _mintCaps[operator];
    }

    /**
     * @dev mints token for an account and calculates the new minting cap of the caller.
     * @param account Account to send tokens to
     * @param amount Amount of the tokens to be minted
     */
    function _mint(address account, uint256 amount) internal virtual override {
        _spendMintCap(_msgSender(), amount);
        super._mint(account, amount);
    }

    /**
     * @dev Handles the arithmetic operations of calculating the minting cap.
     * NOTE: If an account has the maximum available minting cap, then this function
     * has no effect on it.
     * @param operator Address that used its minting cap by calling minting tokens
     * @param amount Amount of the minted tokens
     */
    function _spendMintCap(address operator, uint256 amount) internal virtual {
        uint256 currentMintCap = mintCap(operator);
        if (currentMintCap != type(uint256).max) {
            if (currentMintCap < amount) revert InsufficientMintCapError();
            unchecked {
                _setMintCap(operator, currentMintCap - amount);
            }
        }
    }

    /**
     * @dev Increases the minting cap of an address.
     * @param operator Address that gets its minting cap gets increased
     * @param addedValue Value to add to the current minting cap
     */
    function _increaseMintCap(
        address operator,
        uint256 addedValue
    ) internal virtual returns (bool) {
        _setMintCap(operator, mintCap(operator) + addedValue);
        return true;
    }

    /**
     * @dev Decreases the minting cap of an address.
     * @param operator Address that gets its minting cap gets decreased
     * @param subtractedValue Value to subtract from the current minting cap
     */
    function _decreaseMintCap(
        address operator,
        uint256 subtractedValue
    ) internal virtual returns (bool) {
        uint256 currentMintCap = mintCap(operator);
        if (currentMintCap < subtractedValue) revert InsufficientMintCapError();
        unchecked {
            _setMintCap(operator, currentMintCap - subtractedValue);
        }
        return true;
    }

    /**
     * @dev Sets the minting cap of an address and emits the associated event.
     * @param account Address that its minting cap will be changed
     * @param amount New minting cap
     */
    function _setMintCap(
        address account,
        uint256 amount
    ) internal virtual returns (bool) {
        _mintCaps[account] = amount;
        emit MintCapChanged(account, _msgSender(), amount);
        return true;
