contract MetatronBuy is Pausable, ReentrancyGuard, Ownable2Step {
    using Address for address payable;
    using Address for address;
    using ECDSA for bytes32;
    using SafeBEP20 for IBEP20;

    IBEP20 public USDTtoken;

    address public signer;

    address public operator;

    using EnumerableSet for EnumerableSet.AddressSet;

    EnumerableSet.AddressSet tokenList;

    //  address[] private _tokenList;

    // mapping(address => bool) private _approvedToken;

    struct Sig {
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    mapping(bytes32 => bool) private _signStatus;

    event AddToken(
        address indexed token,
        uint256 indexed numToken,
        uint256 time
    );
    event Deposit(
        address indexed _account,
        uint256 indexed amount,
        uint256 indexed time
    );

    event Withdraw(
        address indexed _account,
        uint256 indexed amount,
        uint256 indexed time
    );

    event FailSafe(
        address indexed token,
        address indexed user,
        uint256 indexed no_tokens,
        uint256 time
    );

    event UpdateSigner(
        address indexed previousSigner,
        address indexed newSigner,
        uint256 time
    );

    event UpdateToken(
        address indexed previousToken,
        address indexed newToken,
        uint256 time
    );

    event Claim(
        address indexed token,
        address indexed user,
        uint256 indexed no_tokens,
        uint256 time
    );

    event UpdateOperator(
        address indexed previousOperator,
        address indexed newOperator,
        uint256 time
    );

    event ApprovedToken(address indexed token, uint256 time);

    constructor(
        address _usdtToken,
        address _signerAddress,
        address _operatorAddress
    ) {
        _setToken(_usdtToken);
        _setSigner(_signerAddress);
        _setOperator(_operatorAddress);
    }

    modifier zeroAddress(address _account) {
        require(_account != address(0), "Address: Zero Address");
        _;
    }

    modifier NotContract(address _account) {
        require(!_account.isContract(), "Address not be a Contract");
        _;
    }

    modifier IsContract(address _account) {
        require(_account.isContract(), "Not a Contract");
        _;
    }

    modifier invalidAmount(uint256 _amount) {
        require(_amount > 0, "Amount: Zero Amount");
        _;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function setSigner(address newSigner) external onlyOwner {
        _setSigner(newSigner);
    }

    function setOperator(address _newOperator) external onlyOwner {
        _setOperator(_newOperator);
    }

    function _setSigner(address newSigner)
        private
        zeroAddress(newSigner)
        NotContract(newSigner)
    {
        address previousSigner = signer;
        signer = newSigner;
        emit UpdateSigner(previousSigner, newSigner, block.timestamp);
    }

    function _setOperator(address newOperator)
        private
        zeroAddress(newOperator)
        NotContract(newOperator)
    {
        address previousOperator = operator;
        operator = newOperator;
        emit UpdateOperator(previousOperator, newOperator, block.timestamp);
    }

    function _approvetoken(address token)
        internal
        zeroAddress(token)
        IsContract(token)
    {
        require(checkTokenStatus(token) == false, "Token is Already Approved");
        tokenList.add(token);
        emit ApprovedToken(token, block.timestamp);
    }

    function approveToken(address[] memory _token) external onlyOwner {
        for (uint256 i = 0; i < _token.length; i++) {
            _approvetoken(_token[i]);
        }
    }

    function addToken(address _token, uint256 _numTokens)
        external
        onlyOwner
        zeroAddress(_token)
        invalidAmount(_numTokens)
        IsContract(_token)
    {
        require(checkTokenStatus(_token) == true, "Token isNotApproved");
        IBEP20(_token).safeTransferFrom(
            _msgSender(),
            address(this),
            _numTokens
        );
        emit AddToken(_token, _numTokens, block.timestamp);
    }

    function checkTokenStatus(address _token) public view returns (bool) {
        return tokenList.contains(_token);
    }

    function removeToken(address _token)
        external
        onlyOwner
        zeroAddress(_token)
    {
        require(checkTokenStatus(_token) == true, "Invalid_TokenAddress");
        tokenList.remove(_token);
    }

    function tokenByIndex(uint256 _index) public view returns (address) {
        return tokenList.at(_index);
    }

    function tokenIndex() public view returns (uint256) {
        return tokenList.length();
    }

    function setUsdtToken(address _usdttoken) external onlyOwner {
        _setToken(_usdttoken);
    }

    function _setToken(address _token)
        private
        zeroAddress(_token)
        IsContract(_token)
    {
        address previousToken = address(USDTtoken);
        USDTtoken = IBEP20(_token);
        emit UpdateToken(previousToken, _token, block.timestamp);
    }

    function deposit(uint256 _amount)
        external
        whenNotPaused
        invalidAmount(_amount)
    {
        IBEP20(USDTtoken).safeTransferFrom(
            _msgSender(),
            address(this),
            _amount
        );
        emit Deposit(_msgSender(), _amount, block.timestamp);
    }

    function withdraw(
        address _token,
        address _user,
        uint256 _amount,
        uint256 _expiry,
        Sig memory sig
    )
        external
        nonReentrant
        whenNotPaused
        zeroAddress(_user)
        invalidAmount(_amount)
    {
        require(block.timestamp <= _expiry, "Time Invalid");
        require(_amount <= getBalance(_token), "Insufficient Balance");
        require(
            signer == verifySignature(_token, _user, _amount, _expiry, sig),
            "Invalid Signature"
        );

        IBEP20(_token).safeTransfer(_user, _amount);
        emit Withdraw(_user, _amount, block.timestamp);
    }

    function prepareHash(
        address _token,
        address _user,
        uint256 _amount,
        uint256 _expiry
    ) public view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(_token, _user, _amount, _expiry, address(this))
            );
    }

    function getBalance(address _token) public view returns (uint256) {
        return IBEP20(_token).balanceOf(address(this));
    }

    function verifySignature(
        address _token,
        address _user,
        uint256 _amount,
        uint256 _expiry,
        Sig memory sig
    ) private returns (address signatureAddress) {
        bytes32 hash = prepareHash(_token, _user, _amount, _expiry);
        bytes32 messageHash = hash.toEthSignedMessageHash();
        require(!_signStatus[messageHash], "Invalid Signature");
        _signStatus[messageHash] = true;
        signatureAddress = messageHash.recover(sig.v, sig.r, sig.s);
    }

    function failSafe(
        address _token,
        address _user,
        uint256 _amount
    )
        external
        onlyOwner
        zeroAddress(_user)
        invalidAmount(_amount)
        NotContract(_user)
        IsContract(_token)
    {
        if (_token == address(0)) {
            payable(_user).sendValue(_amount);
        } else {
            require(_amount <= getBalance(_token), "Insufficient Balance");
            IBEP20(_token).safeTransfer(_user, _amount);
        }
        emit FailSafe(_token, _user, _amount, block.timestamp);
    }

    function claim(
        address _token,
        address _user,
        uint256 _amount
    )
        external
        NotContract(_user)
        zeroAddress(_user)
        invalidAmount(_amount)
        IsContract(_token)
    {
        require(_msgSender() == operator, "Address: Invalid Operator Address");

        require(_amount <= getBalance(_token), "Insufficient Balance");

        IBEP20(_token).safeTransfer(_user, _amount);

        emit Claim(_token, _user, _amount, block.timestamp);
    }
}
