    "contracts/nft/marketplace/Marketplace.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;


import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/structs/BitMaps.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/security/Pausable.sol";


contract Marketplace is Ownable, ERC721Holder, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.AddressSet;
    using BitMaps for BitMaps.BitMap;


    enum BID_STATUS {WAITING, REJECTED, CANCEL, ACCEPTED}

    // structs
    struct Offer {
        uint256 tokenId;
        uint256 price;
        address nftAddress;
        address currency;
        address seller;
        bool onSelling;
    }

    struct Bid {
        uint256 offerId;
        uint256 price;
        address bidder;
        BID_STATUS status;
    }


    uint256 constant public ONE_HUNDRED_PERCENT = 1e6; // 100%

    uint256 constant public MIN_PRICE = 1000000000;

    address constant public BNB = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    // offer
    uint256 private OFFER_ID = 0;
    mapping(uint256 => Offer) private offers;
    mapping(uint256 => EnumerableSet.UintSet) private bidOfOffer; // offer id => list bid ids
    mapping(address => mapping(uint256 => uint256)) private hasOffer; // erc721 => tokenId => offerId

    EnumerableSet.UintSet private activeOffers; //

    // bid
    uint256 private BID_ID = 0;
    mapping(uint256 => Bid) private bids;


    //whitelist
    // erc20 address => status
    mapping(address => bool) public erc20Whitelist;

    // erc721 address => status
    mapping(address => bool) public erc721Whitelist;

    // black list users
    mapping(address => bool) public blackListUser;

    // blacklist tokenId
    mapping(address => mapping(uint256 => bool)) public blackListTokenId;

    //system
    uint256 public systemFeePercent;
    address public adminWallet;



    /* ===================== Modifier ===================== */
    modifier inWhitelist(address erc721, address erc20) {
        require(erc721Whitelist[erc721] && erc20Whitelist[erc20], "NftMarket: erc721 and erc20 must be in whitelist");
        _;
    }


    /* ===================== Events ===================== */
    event SystemFeePercentUpdated(uint256 percent);
    event AdminWalletUpdated(address wallet);
    event Erc20WhitelistUpdated(address[] erc20s, bool status);
    event Erc721WhitelistUpdated(address[] erc721s, bool status);

    event AskCreated(address erc721, address erc20, address seller, uint256 price, uint256 tokenId, uint256 askId);
    event AskUpdated(uint256 askId, uint256 oldAmount, uint256 newAmount);
    event AskCanceled(address erc721, address erc20, address seller, uint256 price, uint256 tokenId, uint256 askId);

    event BidRejected(address erc721, address erc20, address bidder, address seller, uint256 price, uint256 tokenId, uint256 bidId, uint256 askId);
    event BidCreated(address erc721, address erc20, address bidder, uint256 price, uint256 tokenId, uint256 bidId, uint256 askId);
    event BidAccepted(address erc721, address erc20, address bidder, address seller, uint256 price, uint256 tokenId, uint256 bidId, uint256 askId);
    event BidCanceled(address erc721, address erc20, address bidder, uint256 price, uint256 tokenId, uint256 bidId, uint256 askId);

    event Payout(address erc721, address erc20, uint256 tokenId, uint256 systemFeePayment, uint256 sellerPayment);
    event TokenSold(address erc721, address erc20, address buyer, address seller, uint256 price, uint256 tokenId, uint256 askId);

    event BlackListUser(address[] _users, bool status);
    event BlackListTokenId(address _nft, uint256[] _ids,bool _status);
    /* ===================== Constructor ===================== */
    constructor(address[] memory erc721s, address[] memory erc20s, address _adminWallet) {
        adminWallet = _adminWallet;

        systemFeePercent = 50000;
        // 5%

        for (uint i = 0; i < erc721s.length; i++) {
            erc721Whitelist[erc721s[i]] = true;
        }

        for (uint i = 0; i < erc20s.length; i++) {
            erc20Whitelist[erc20s[i]] = true;
        }
    }


    /* ===================== Internal functions ===================== */
    // seller
    function _createOffer(address _nft, uint256 _tokenId, address _currency, uint256 _price) internal {
        require(_checkTokenId(_nft, _tokenId), "Marketplace: token id has a offer ");
        _transferNFT(_nft, _tokenId, msg.sender, address(this));

        OFFER_ID ++;
        Offer memory _offer = Offer({
        tokenId : _tokenId,
        price : _price,
        nftAddress : _nft,
        currency : _currency,
        seller : msg.sender,
        onSelling : true
        });
        offers[OFFER_ID] = _offer;
        activeOffers.add(OFFER_ID);
        hasOffer[_nft][_tokenId] = OFFER_ID;
        emit AskCreated(_nft, _currency, msg.sender, _price, _tokenId, OFFER_ID);
    }

    function _updateOffer(uint256 _offerId, uint256 _price) internal {
        Offer storage _offer = offers[_offerId];
        require(_offer.onSelling, "Marketplace: offer not in sell");
        require(_offer.seller == msg.sender, "Marketplace: only owner");
        uint256 _old = _offer.price;
        _offer.price = _price;
        emit AskUpdated(_offerId, _old, _price);
    }

    function _cancelOffer(uint256 _offerId) internal {
        Offer storage _offer = offers[_offerId];
        require(_offer.onSelling, "Marketplace: offer not in sell");
        require(_offer.seller == msg.sender, "Marketplace: only owner");
        _offer.onSelling = false;
        delete hasOffer[_offer.nftAddress][_offer.tokenId];
        activeOffers.remove(_offerId);

        _transferNFT(_offer.nftAddress, _offer.tokenId, address(this), _offer.seller);
        emit AskCanceled(_offer.nftAddress, _offer.currency, _offer.seller, _offer.price, _offer.tokenId, _offerId);
    }

    function _acceptBid(uint256 _offerId, uint256 _bidId) internal {
        //offer
        Offer storage _offer = offers[_offerId];
        require(_offer.onSelling, "Marketplace: offer not in sell");
        require(_offer.seller == msg.sender, "Marketplace: only owner");
        require(bidOfOffer[_offerId].contains(_bidId), "Marketplace: not find bid in offer");

        //bid
        Bid storage _bid = bids[_bidId];
        require(_bid.status == BID_STATUS.WAITING, "Marketplace: bid not open");

        // update state
        delete hasOffer[_offer.nftAddress][_offer.tokenId];
        _offer.onSelling = false;
        _bid.status = BID_STATUS.ACCEPTED;
        activeOffers.remove(_offerId);

        //payment
        _payment(_offer.currency, _bid.price, _bid.bidder, _offer.seller);
        _transferNFT(_offer.nftAddress, _offer.tokenId, address(this), _bid.bidder);
        emit BidAccepted(_offer.nftAddress, _offer.currency, _bid.bidder, _offer.seller, _bid.price, _offer.tokenId, _bidId, _offerId);
    }

    function _rejectBid(uint256 _offerId, uint256 _bidId) internal {
        //offer
        Offer storage _offer = offers[_offerId];
        require(_offer.onSelling, "Marketplace: offer not in sell");
        require(_offer.seller == msg.sender, "Marketplace: only owner");
        require(bidOfOffer[_bidId].contains(_bidId), "Marketplace: not find bid in offer");

        //bid
        Bid storage _bid = bids[_bidId];
        require(_bid.status == BID_STATUS.WAITING, "Marketplace: bid not open");

        _bid.status = BID_STATUS.REJECTED;
        bidOfOffer[_bidId].remove(_bidId);

        emit BidRejected(_offer.nftAddress, _offer.currency, _bid.bidder, _offer.seller, _bid.price, _offer.tokenId, _bidId, _offerId);
    }

    //buyer
    function _createBid(uint256 _offerId, uint256 _price) internal returns (uint256){
        Offer memory _offer = offers[_offerId];
        require(_offer.onSelling, "Marketplace: offer not in sell");
        require(msg.sender != _offer.seller, "Marketplace: bid yourself");
        _checkPrice(_price);
        BID_ID ++;
        Bid memory _bid = Bid({
        bidder : msg.sender,
        price : _price,
        offerId : _offerId,
        status : BID_STATUS.WAITING
        });
        bids[BID_ID] = _bid;
        bidOfOffer[_offerId].add(BID_ID);
        emit BidCreated(_offer.nftAddress, _offer.currency, msg.sender, _price, _offer.tokenId, BID_ID, _offerId);

        return BID_ID;
    }

    function _cancelBid(uint256 _bidId) internal {
        Bid storage _bid = bids[_bidId];
        require(_bid.status == BID_STATUS.WAITING, "Marketplace: bid not open");
        require(_bid.bidder == msg.sender, "Marketplace: not owner");

        Offer memory _offer = offers[_bid.offerId];
        require(_offer.onSelling, "Marketplace: offer not in sell");

        bidOfOffer[_bid.offerId].remove(_bidId);
        _bid.status = BID_STATUS.CANCEL;

        emit BidCanceled(_offer.nftAddress, _offer.currency, msg.sender, _bid.price, _offer.tokenId, _bidId, _bid.offerId);
    }

    function _buy(uint256 _offerId, uint256 _price) internal {
        Offer storage _offer = offers[_offerId];
        require(_offer.onSelling, "Marketplace: offer not in sell");
        require(_offer.price == _price, "Marketplace: price has changed");
        require(msg.sender != _offer.seller, "Marketplace: buy your self");


        //change state
        delete hasOffer[_offer.nftAddress][_offer.tokenId];
        _offer.onSelling = false;

        //payment
        _payment(_offer.currency, _offer.price, msg.sender, _offer.seller);
        _transferNFT(_offer.nftAddress, _offer.tokenId, address(this), msg.sender);
        activeOffers.remove(_offerId);
        //
        emit TokenSold(_offer.nftAddress, _offer.currency, msg.sender, _offer.seller, _price, _offer.tokenId, _offerId);
    }

    //payment
    function _takeFee(uint256 _amount) internal view returns (uint256 _actualAmount, uint256 _fee){
        _fee = _amount * systemFeePercent / ONE_HUNDRED_PERCENT;
        _actualAmount = _amount - _fee;
    }

    function _payment(address _erc20, uint256 _amount, address _from, address _to) internal {
        (uint256 _amountSent, uint256 _fee) = _takeFee(_amount);
        if (_fee > 0) {
            IERC20(_erc20).safeTransferFrom(_from, adminWallet, _fee);
        }
        IERC20(_erc20).safeTransferFrom(_from, _to, _amountSent);
    }

    function _transferNFT(address _nft, uint256 _tokenId, address _owner, address _receiver) internal {
        IERC721(_nft).safeTransferFrom(_owner, _receiver, _tokenId);
    }

    // optional
    function _checkPrice(uint256 _amount) internal pure {
        require(_amount >= MIN_PRICE, "Marketplace:  less than min price");
    }

    function _checkTokenId(address _nft, uint256 _tokenId) internal view returns (bool) {
        return hasOffer[_nft][_tokenId] == 0;
    }

    function _min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }

    /* ===================== External functions ===================== */
    function createOffer(address _nft, uint256 _tokenId, address _currency, uint256 _price) external inWhitelist(_nft, _currency) whenNotPaused {
        require(!blackListTokenId[_nft][_tokenId], "Marketplace: token id in blacklist");
        require(!blackListUser[msg.sender], "Marketplace: user in blacklist");
        _checkPrice(_price);
        _createOffer(_nft, _tokenId, _currency, _price);
    }

    function updateOffer(uint256 _offerId, uint256 _price) external {
        _checkPrice(_price);
        _updateOffer(_offerId, _price);
    }

    function cancelOffer(uint256 _offerId) external {
        _cancelOffer(_offerId);
    }

    function acceptBid(uint256 _offerId, uint256 _bidId) external nonReentrant whenNotPaused {
        _acceptBid(_offerId, _bidId);
    }

    function rejectBid(uint256 _offerId, uint256 _bidId) external whenNotPaused {
        _rejectBid(_offerId, _bidId);
    }

    function buy(uint256 _offerId, uint256 _price) external nonReentrant whenNotPaused{
        require(!blackListUser[msg.sender], "Marketplace: user in blacklist");
        _checkPrice(_price);
        _buy(_offerId, _price);
    }

    function createBid(uint256 _offerId, uint256 _price) external whenNotPaused {
        require(!blackListUser[msg.sender], "Marketplace: user in blacklist");
        _checkPrice(_price);
        _createBid(_offerId, _price);
    }

    function cancelBid(uint256 _bidId) external {
        _cancelBid(_bidId);
    }


    /* ===================== View functions ===================== */
    function getCurrentOfferForToken(address _nft, uint256 _tokenId) external view returns (uint256) {
        return hasOffer[_nft][_tokenId];
    }

    function getActiveOffers(uint256 _page, uint256 _limit) external view returns (Offer[] memory, uint256 _length) {
        uint256 _from = _page * _limit;
        _length = activeOffers.length();
        uint256 _to = _min((_page + 1) * _limit, activeOffers.length());
        Offer[] memory _result = new Offer[](_to - _from);
        for (uint256 i = 0; _from < _to; i++) {
            _result[i] = offers[activeOffers.at(_from)];
            ++_from;
        }
        return (_result, _length);
    }

    function getBidOfOffer(uint256 _offerId, uint256 _page, uint256 _limit) external view returns (Bid[] memory, uint256 _length) {
        uint256 _from = _page * _limit;
        _length = bidOfOffer[_offerId].length();
        uint256 _to = _min((_page + 1) * _limit, bidOfOffer[_offerId].length());
        Bid[] memory _result = new Bid[](_to - _from);
        for (uint256 i = 0; _from < _to; i++) {
            _result[i] = bids[bidOfOffer[_offerId].at(_from)];
            ++_from;
        }
        return (_result, _length);
    }

    function getOffer(uint256 _offerId) external view returns(Offer memory) {
        return offers[_offerId];
    }

    function getBid(uint256 _bidId) external view returns(Bid memory) {
        return bids[_bidId];
    }

